new GadgetDescription { Id = Guid.Parse("4c943d5b-6895-429b-9876-f5c1b1f1c41d"), GadgetId = Guid.Parse("38d42aac-167d-423a-bcae-ceace1c3e710"), Value = "Thiết kế loa khá gọn gàng, khối lượng khoảng 6.3 kg, trang bị quai xách cứng cáp giúp dễ dàng di chuyển mà không quá bất tiện, kiểu dáng đẹp mắt với hệ thống đèn LED đa màu làm tăng tính thẩm mỹ cho không gian của bạn.", Type = ParseGadgetDescriptionType("NormalText"), Index = 2 },
