new GadgetDescription { Id = Guid.Parse("4ccfab67-7d40-4679-8c21-e6570b5a3979"), GadgetId = Guid.Parse("3ef4a6f4-4806-430d-be00-65993e9abccd"), Value = "Loa Bluetooth JBL Go 4 nhỏ gọn mang đến âm thanh mạnh mẽ và rõ ràng với âm trầm sâu lắng, tất cả nằm gọn trong lòng bàn tay của bạn. Với nhiều tùy chọn phối màu đa dạng, JBL Go 4 cho phép bạn lựa chọn kiểu dáng phù hợp với tâm trạng và phong cách của mình.", Type = ParseGadgetDescriptionType("BoldText"), Index = 0 },
