new GadgetDescription { Id = Guid.Parse("0ece0dc4-9544-467e-b5da-49218c0ea72a"), GadgetId = Guid.Parse("3ef4a6f4-4806-430d-be00-65993e9abccd"), Value = "Được thiết kế mang tính di động, chiếc loa nhỏ gọn đi kèm với một vòng đeo tích hợp, giúp bạn dễ dàng di chuyển và tận hưởng âm nhạc ở bất cứ đâu. Lưới tản nhiệt của loa được làm từ nhựa và vải tái chế, góp phần nhỏ vào mục tiêu bảo vệ môi trường chung của thế giới.", Type = ParseGadgetDescriptionType("NormalText"), Index = 2 },
new GadgetDescription { Id = Guid.Parse("ddb628b6-8f96-432a-a02b-d817e84554d8"), GadgetId = Guid.Parse("3ef4a6f4-4806-430d-be00-65993e9abccd"), Value = "JBL Go 4 có các thiết kế phối màu độc quyền lấy cảm hứng từ xu hướng thời trang đường phố, giới thiệu các sắc thái và những sự kết hợp mới. Đây không chỉ là một chiếc loa mà còn là phụ kiện thời trang thể hiện phong cách cá nhân của bạn.", Type = ParseGadgetDescriptionType("NormalText"), Index = 3 },
