new GadgetDescription { Id = Guid.Parse("ef6298e0-acf0-405c-841d-fbae50d0eb1e"), GadgetId = Guid.Parse("33e08afb-3ce2-4fc6-8c02-e113c0bc9475"), Value = "Chế tác chỉn chu, thiết kế gọn gàng", Type = ParseGadgetDescriptionType("BoldText"), Index = 1 },
new GadgetDescription { Id = Guid.Parse("f7b74082-64bb-4261-bfca-690fa7954295"), GadgetId = Guid.Parse("33e08afb-3ce2-4fc6-8c02-e113c0bc9475"), Value = "Ấn tượng đầu tiên của mình khi mở hộp là một chiếc loa được thiết kế dạng hình trụ, trông khá gọn gàng và cân đối, dáng loa hơi phình nhẹ ở phần giữa. Phần vỏ ngoài sẽ hơi bám vân tay một chút, do vậy để loa luôn sạch sẽ thì bạn có thể thường xuyên lau nhẹ loa trước và sau khi sử dụng bằng khăn.", Type = ParseGadgetDescriptionType("NormalText"), Index = 2 },
new GadgetDescription { Id = Guid.Parse("9e7fbbfb-757b-4c72-80b5-e3bd1e15e7b4"), GadgetId = Guid.Parse("33e08afb-3ce2-4fc6-8c02-e113c0bc9475"), Value = "Phần đế với thiết kế 3 chân được bọc cao su giúp tăng ma sát, giữ loa luôn cố định trên mặt bàn, mình cũng yên tâm hơn khi sử dụng. Ngoài ra, logo JBL được in chìm giúp thương hiệu hoàn toàn nổi bật, dễ dàng nhận biết.", Type = ParseGadgetDescriptionType("NormalText"), Index = 3 },
