new GadgetDescription { Id = Guid.Parse("107b185b-d799-490b-bf7d-1046a3379357"), GadgetId = Guid.Parse("6b0c269d-ead7-4829-b2db-bc5580c638c2"), Value = "Emberton II sở hữu thiết kế cổ điển đặc trưng của thương hiệu Marshall, kết hợp giữa vẻ đẹp vintage và hiện đại. Bề mặt loa được trang trí logo Marshall nổi bật, tôn lên vẻ đẹp tinh tế và mạnh mẽ. Kích thước nhỏ gọn cùng khối lượng chỉ 0.7 kg, loa có thiết kế tiện lợi và dễ dàng mang theo bất kỳ nơi nào bạn muốn. Chất liệu kết hợp giữa kim loại và nhựa tái chế không chỉ mang lại vẻ mạnh mẽ mà còn đem lại sự bền bỉ vượt thời gian. Thiết kế của Marshall Emberton II không chỉ đáp ứng nhu cầu thẩm mỹ mà còn mang lại sự tiện lợi và linh hoạt cho người sử dụng.", Type = ParseGadgetDescriptionType("NormalText"), Index = 10 },
new GadgetDescription { Id = Guid.Parse("49cdb50c-1a68-4ac0-92c5-5a861519ae94"), GadgetId = Guid.Parse("6b0c269d-ead7-4829-b2db-bc5580c638c2"), Value = "https://storage.googleapis.com/fbdemo-f9d5f.appspot.com/Gadgets/85e80e04-3e93-449f-85d8-36becec40a57.jpg", Type = ParseGadgetDescriptionType("Image"), Index = 11 },
new GadgetDescription { Id = Guid.Parse("525e552b-fdbf-43b5-bb28-42382617b910"), GadgetId = Guid.Parse("6b0c269d-ead7-4829-b2db-bc5580c638c2"), Value = "Loa Marshall Emberton II là sự kết hợp hoàn hảo giữa công nghệ âm thanh tiên tiến và thiết kế đẳng cấp. Loa mang đến chất lượng âm thanh vượt trội, truyền tải âm nhạc một cách chân thực và sống động. Thiết kế của loa tạo nên sự sang trọng, tinh tế, phù hợp với nhiều không gian và phong cách sử dụng khác nhau. Sự nhỏ gọn, tiện lợi cùng với tính di động cao giúp bạn có thể mang theo loa bên mình mọi lúc, mọi nơi.", Type = ParseGadgetDescriptionType("NormalText"), Index = 12 },
new GadgetDescription { Id = Guid.Parse("d6f19845-fcb5-4da0-af3e-c8921a7ae4be"), GadgetId = Guid.Parse("728e59a4-a889-4582-8787-38caae8af4e5"), Value = "Loa Bluetooth Marshall Acton III một sản phẩm âm thanh từ thương hiệu nổi tiếng Marshall, mang đến trải nghiệm âm nhạc tuyệt vời với thiết kế cổ điển và âm thanh chất lượng cao. Được trang bị công nghệ tiên tiến, Acton III không chỉ tạo nên âm thanh mạnh mẽ và rõ nét mà còn thể hiện sự tinh tế trong từng chi tiết.", Type = ParseGadgetDescriptionType("BoldText"), Index = 0 },
new GadgetDescription { Id = Guid.Parse("ad505ac7-38a4-49ea-87f1-3ea472a4ce04"), GadgetId = Guid.Parse("728e59a4-a889-4582-8787-38caae8af4e5"), Value = "Vẻ ngoài thu hút mọi ánh nhìn", Type = ParseGadgetDescriptionType("BoldText"), Index = 1 },
new GadgetDescription { Id = Guid.Parse("92ea16fe-90a0-46b2-b05d-e0509d5a1668"), GadgetId = Guid.Parse("728e59a4-a889-4582-8787-38caae8af4e5"), Value = "Marshall Acton III nổi bật với thiết kế đặc trưng mang đậm phong cách cổ điển và sang trọng. Vỏ loa được bọc bằng chất liệu nhựa tái chế giúp bảo vệ loa tốt hơn và đem đến sự thanh lịch cho sản phẩm. Phần trên của loa là bảng điều khiển với các nút vặn, cho phép bạn dễ dàng điều chỉnh âm lượng theo ý thích. Mặt trước của loa được trang bị lớp lưới màu vàng với logo Marshall nổi bật, tạo nên điểm nhấn tinh tế và đẳng cấp.", Type = ParseGadgetDescriptionType("NormalText"), Index = 2 },
new GadgetDescription { Id = Guid.Parse("f0465735-fe49-4ffe-9612-e623f022632b"), GadgetId = Guid.Parse("728e59a4-a889-4582-8787-38caae8af4e5"), Value = "https://storage.googleapis.com/fbdemo-f9d5f.appspot.com/Gadgets/56134e22-6c0a-42e5-9e32-7f0096b5feb4.jpg", Type = ParseGadgetDescriptionType("Image"), Index = 3 },
new GadgetDescription { Id = Guid.Parse("88880853-7257-4166-8efd-15c108dc6f4f"), GadgetId = Guid.Parse("728e59a4-a889-4582-8787-38caae8af4e5"), Value = "Các nút điều khiển được đặt ở phía trên loa, cho phép bạn dễ dàng điều chỉnh âm lượng một cách chính xác. Kích thước của Marshall Acton III nhỏ gọn, phù hợp với nhiều không gian khác nhau, thiết kế này không chỉ giúp loa trở thành một món đồ trang trí đẹp mắt mà còn mang lại sự tiện lợi và linh hoạt trong quá trình sử dụng.", Type = ParseGadgetDescriptionType("NormalText"), Index = 4 },
new GadgetDescription { Id = Guid.Parse("1b9b3b19-5472-49b2-bdff-d73c8e5e8be2"), GadgetId = Guid.Parse("728e59a4-a889-4582-8787-38caae8af4e5"), Value = "Kết nối liền mạch với công nghệ Bluetooth", Type = ParseGadgetDescriptionType("BoldText"), Index = 5 },
new GadgetDescription { Id = Guid.Parse("97f85fa6-eb0a-49ad-affa-8bd3fc467451"), GadgetId = Guid.Parse("728e59a4-a889-4582-8787-38caae8af4e5"), Value = "Loa Marshall được trang bị kết nối không dây Bluetooth 5.2 tiên tiến, mang lại nhiều ưu điểm vượt trội cho bạn. Công nghệ Bluetooth 5.2 không chỉ đảm bảo kết nối ổn định và nhanh chóng, mà còn cải thiện chất lượng âm thanh truyền tải, giúp giảm thiểu độ trễ và nhiễu sóng.", Type = ParseGadgetDescriptionType("NormalText"), Index = 6 },
new GadgetDescription { Id = Guid.Parse("80fb0e73-8ad0-443f-b672-41b896963d11"), GadgetId = Guid.Parse("728e59a4-a889-4582-8787-38caae8af4e5"), Value = "https://storage.googleapis.com/fbdemo-f9d5f.appspot.com/Gadgets/d337899c-6960-49bd-a19a-0a6a00294191.jpg", Type = ParseGadgetDescriptionType("Image"), Index = 7 },
new GadgetDescription { Id = Guid.Parse("809c38ad-0d2c-471e-84a3-52eceecbbfb4"), GadgetId = Guid.Parse("728e59a4-a889-4582-8787-38caae8af4e5"), Value = "Loa Bluetooth Marshall Acton III được thiết kế để cắm nguồn trực tiếp, đảm bảo hiệu suất hoạt động ổn định và mạnh mẽ. Việc cắm nguồn trực tiếp không chỉ giúp loa duy trì công suất cao liên tục mà còn loại bỏ những lo ngại về thời gian sử dụng pin hay tình trạng sạc pin. Bạn có thể yên tâm thưởng thức âm nhạc trong thời gian dài mà không cần lo lắng về việc ngắt quãng do hết pin.", Type = ParseGadgetDescriptionType("NormalText"), Index = 8 },
