new GadgetDescription { Id = Guid.Parse("b1deeabd-0aea-47fe-a88f-d016bf59eb59"), GadgetId = Guid.Parse("728e59a4-a889-4582-8787-38caae8af4e5"), Value = "Công nghệ Hi-Res Audio trên loa giúp bạn cảm nhận được đầy đủ các chi tiết nhỏ nhất trong bản nhạc, từ những âm bass sâu lắng đến những nốt cao trong trẻo, mang lại cảm giác như đang nghe trực tiếp tại phòng thu.", Type = ParseGadgetDescriptionType("NormalText"), Index = 10 },
