new GadgetDescription { Id = Guid.Parse("36002f82-d7f8-4eaf-b8bc-664e780aa319"), GadgetId = Guid.Parse("728e59a4-a889-4582-8787-38caae8af4e5"), Value = "Loa Bluetooth với công suất đạt 60 W, đảm bảo mang lại âm thanh mạnh mẽ và sống động, phù hợp với nhiều thể loại nhạc khác nhau. Với công suất trên, Marshall Acton III có khả năng lấp đầy không gian phòng với âm thanh phong phú và rõ nét, làm cho trải nghiệm nghe nhạc trở nên tuyệt vời hơn bao giờ hết.", Type = ParseGadgetDescriptionType("NormalText"), Index = 12 },
new GadgetDescription { Id = Guid.Parse("1d7b04df-71af-4304-9f3a-30cd61f30c82"), GadgetId = Guid.Parse("728e59a4-a889-4582-8787-38caae8af4e5"), Value = "Loa Bluetooth Marshall Acton III gây ấn tượng với thiết kế cổ điển, sang trọng vàchinh phục người dùng bằng chất lượng âm thanh đỉnh cao. Marshall Acton III không chỉ là một thiết bị âm thanh, mà còn là biểu tượng của phong cách sống và niềm đam mê âm nhạc, xứng đáng có mặt trong bộ sưu tập của mọi tín đồ âm nhạc.", Type = ParseGadgetDescriptionType("NormalText"), Index = 13 },
new GadgetDescription { Id = Guid.Parse("6841e73a-58e7-4697-b653-87abb35a19c1"), GadgetId = Guid.Parse("83725f1b-79ed-4406-8ec3-4a201c0b6fa6"), Value = "Với sự phát triển của loa Bluetooth, bạn không chỉ có thể thưởng thức âm nhạc mọi lúc mọi nơi mà còn được trải nghiệm chất lượng âm thanh tuyệt vời. Trong số hàng ngàn sản phẩm trên thị trường, loa Bluetooth Marshall Kilburn II đã nổi bật với sự kết hợp hoàn hảo giữa phong cách cổ điển và công nghệ tiên tiến.", Type = ParseGadgetDescriptionType("BoldText"), Index = 0 },
new GadgetDescription { Id = Guid.Parse("f0fe025f-5729-48f6-a3c1-1f546c1ea1ac"), GadgetId = Guid.Parse("83725f1b-79ed-4406-8ec3-4a201c0b6fa6"), Value = "Thiết kế tinh tế", Type = ParseGadgetDescriptionType("BoldText"), Index = 1 },
new GadgetDescription { Id = Guid.Parse("914a35de-1768-48a5-a023-116ada40e91d"), GadgetId = Guid.Parse("83725f1b-79ed-4406-8ec3-4a201c0b6fa6"), Value = "Thiết kế của loa là sự kết hợp hài hòa giữa vẻ ngoài cổ điển và tính năng hiện đại. Vỏ ngoài được gia công tỉ mỉ, tạo nên sự sang trọng và độ bền cao. Không những vậy khối lượng chỉ 2.5 kg, bạn có thể dễ dàng cầm nó đi bất kỳ đâu, từ nhà đến công viên hoặc dự tiệc.", Type = ParseGadgetDescriptionType("NormalText"), Index = 2 },
new GadgetDescription { Id = Guid.Parse("54eda97a-85a3-49a1-9137-825152ec9838"), GadgetId = Guid.Parse("83725f1b-79ed-4406-8ec3-4a201c0b6fa6"), Value = "https://storage.googleapis.com/fbdemo-f9d5f.appspot.com/Gadgets/3a192dce-37fc-4339-ab7b-14bd451e9bbf.jpg", Type = ParseGadgetDescriptionType("Image"), Index = 3 },
new GadgetDescription { Id = Guid.Parse("987f635c-894c-4e00-a046-e84e45a5d95e"), GadgetId = Guid.Parse("83725f1b-79ed-4406-8ec3-4a201c0b6fa6"), Value = "Loa Marshall Kilburn II mang đậm phong cách thiết kế cổ điển và mạnh mẽ, làm gợi nhớ đến những dòng ampli guitar huyền thoại. Lưới tản nhiệt phía trước được thiết kế theo kiểu vintage với logo Marshall nổi bật, mang đến nét đặc trưng không thể nhầm lẫn. Các nút điều chỉnh âm lượng, bass và treble được bố trí tiện lợi trên bề mặt trên của loa, giúp bạn dễ dàng tùy chỉnh âm thanh theo sở thích cá nhân.", Type = ParseGadgetDescriptionType("NormalText"), Index = 4 },
new GadgetDescription { Id = Guid.Parse("35ce1ad2-8888-496e-a993-511fee65fa31"), GadgetId = Guid.Parse("83725f1b-79ed-4406-8ec3-4a201c0b6fa6"), Value = "Công suất loa lớn", Type = ParseGadgetDescriptionType("BoldText"), Index = 5 },
new GadgetDescription { Id = Guid.Parse("020c5d59-2412-422c-9b85-785ad6b94ad2"), GadgetId = Guid.Parse("83725f1b-79ed-4406-8ec3-4a201c0b6fa6"), Value = "Với tổng công suất 36 W, loa Marshall không chỉ mang lại âm thanh mạnh mẽ mà còn đảm bảo rõ ràng và chi tiết. Công suất này cho phép loa phát ra âm thanh lớn với độ ổn định cao, phù hợp cho cả những buổi tiệc nhỏ tại nhà hay các buổi picnic ngoài trời.", Type = ParseGadgetDescriptionType("NormalText"), Index = 6 },
new GadgetDescription { Id = Guid.Parse("b0602102-307e-4d64-b5c0-d40fb8f1de17"), GadgetId = Guid.Parse("83725f1b-79ed-4406-8ec3-4a201c0b6fa6"), Value = "https://storage.googleapis.com/fbdemo-f9d5f.appspot.com/Gadgets/22eb26ef-3914-487a-87e8-f5e62800581d.jpg", Type = ParseGadgetDescriptionType("Image"), Index = 7 },
new GadgetDescription { Id = Guid.Parse("2f40873c-1caa-4322-bffa-40441f08219e"), GadgetId = Guid.Parse("83725f1b-79ed-4406-8ec3-4a201c0b6fa6"), Value = "Hi-Res Audio trên loa đảm bảo rằng mỗi chi tiết âm nhạc được tái tạo một cách chân thực và sắc nét nhất, từ những dải âm trầm sâu đến những nốt cao trong trẻo. Điều này giúp bạn thưởng thức những bản nhạc yêu thích với chất lượng gần như gốc, không bị mất mát hoặc biến đổi.", Type = ParseGadgetDescriptionType("NormalText"), Index = 8 },
new GadgetDescription { Id = Guid.Parse("646cc4f2-7f5e-4be5-b8d6-fd65ccad13d9"), GadgetId = Guid.Parse("83725f1b-79ed-4406-8ec3-4a201c0b6fa6"), Value = "Sử dụng lâu dài với viên pin lớn", Type = ParseGadgetDescriptionType("BoldText"), Index = 9 },
new GadgetDescription { Id = Guid.Parse("728a997b-4d8d-4f86-bab9-82a17e91dddf"), GadgetId = Guid.Parse("83725f1b-79ed-4406-8ec3-4a201c0b6fa6"), Value = "Thời gian sử dụng khoảng 20 tiếng và thời gian sạc chỉ khoảng 2.5 tiếng, loa này sẽ giúp bạn thoải mái thưởng thức âm nhạc suốt cả ngày mà không cần lo lắng về việc hết pin.", Type = ParseGadgetDescriptionType("NormalText"), Index = 10 },
