new GadgetDescription { Id = Guid.Parse("d68cc908-419e-4ace-95f5-46874014da8d"), GadgetId = Guid.Parse("83725f1b-79ed-4406-8ec3-4a201c0b6fa6"), Value = "Bluetooth 5.0 trên loa Bluetooth Marshall Kilburn II không chỉ cải thiện tốc độ truyền dữ liệu mà còn mở rộng phạm vi kết nối, cho phép bạn thưởng thức âm nhạc mà không cần phải ở gần loa. Điều này có nghĩa là bạn có thể điều khiển loa từ xa mà không bị gián đoạn, mang lại sự linh hoạt và tự do trong việc trải nghiệm âm nhạc.", Type = ParseGadgetDescriptionType("NormalText"), Index = 12 },
new GadgetDescription { Id = Guid.Parse("44795d76-17ca-4e7b-b23c-2d5928be0f37"), GadgetId = Guid.Parse("83725f1b-79ed-4406-8ec3-4a201c0b6fa6"), Value = "Marshall Kilburn II không chỉ gây ấn tượng bởi thiết kế cổ điển đầy cuốn hút, mà còn chinh phục người dùng bởi chất lượng âm thanh vượt trội và khả năng kết nối linh hoạt. Với âm bass mạnh mẽ, dải âm trung rõ ràng và âm treble tinh tế, loa mang đến trải nghiệm âm nhạc sống động và chân thực. Loa không chỉ đáp ứng nhu cầu nghe nhạc cao cấp mà còn thể hiện đẳng cấp và phong cách riêng của người sở hữu. Marshall Kilburn II thực sự là một lựa chọn đáng giá cho những ai đam mê âm nhạc và yêu thích phong cách thiết kế tinh tế, cổ điển.", Type = ParseGadgetDescriptionType("NormalText"), Index = 13 },
new GadgetDescription { Id = Guid.Parse("682c7adf-69bd-4231-b15f-358515e293ef"), GadgetId = Guid.Parse("07b0be10-fa0f-4588-bc32-42acbdd38521"), Value = "Loa Bluetooth Marshall Middleton nhanh chóng thu hút sự chú ý của người tiêu dùng với sự kết hợp hoàn hảo giữa thiết kế đẳng cấp và chất âm đỉnh cao. Middleton không chỉ là một chiếc loa thông thường, mà còn là biểu tượng của sự sang trọng và đẳng cấp trong thế giới âm nhạc di động.", Type = ParseGadgetDescriptionType("BoldText"), Index = 0 },
