new GadgetDescription { Id = Guid.Parse("723d8acb-f99c-4c3b-a6bf-4e118c985aff"), GadgetId = Guid.Parse("07b0be10-fa0f-4588-bc32-42acbdd38521"), Value = "Mẫu loa Bluetooth là một sản phẩm với sức mạnh âm thanh mạnh mẽ có công suất lên đến 60 W. Khả năng tái tạo âm thanh tốt và rõ ràng của nó làm cho mỗi bản nhạc trở nên sống động hơn.", Type = ParseGadgetDescriptionType("NormalText"), Index = 2 },
