new GadgetDescription { Id = Guid.Parse("326f1b2b-a7ab-4e30-964b-f7a63d48cc5f"), GadgetId = Guid.Parse("07b0be10-fa0f-4588-bc32-42acbdd38521"), Value = "Không những thế với khả năng hỗ trợ Hi-Res Audio trên loa giúp bạn trải nghiệm âm nhạc ở mức độ chất lượng cao, tái tạo mọi chi tiết âm nhạc với độ chính xác và sắc nét đáng kinh ngạc. Dù là những nốt nhạc nhẹ nhàng của piano hoặc những giai điệu mạnh mẽ của guitar điện, bạn sẽ cảm nhận được sự sống động và sự rõ ràng của từng nốt nhạc.", Type = ParseGadgetDescriptionType("NormalText"), Index = 4 },
new GadgetDescription { Id = Guid.Parse("cf2210ba-8672-4de9-a2cf-13a8bdb98ee6"), GadgetId = Guid.Parse("07b0be10-fa0f-4588-bc32-42acbdd38521"), Value = "Kết nối ổn định, sử dụng dài lâu", Type = ParseGadgetDescriptionType("BoldText"), Index = 5 },
new GadgetDescription { Id = Guid.Parse("c6341ccf-b7d1-486e-9fe8-8a39b76f1a53"), GadgetId = Guid.Parse("07b0be10-fa0f-4588-bc32-42acbdd38521"), Value = "Loa Bluetooth Marshall với khả năng hoạt động liên tục lên đến khoảng 20 tiếng, bạn có thể thả mình vào thế giới âm nhạc mà không cần phải lo lắng về việc sạc pin liên tục. Và khi cần, quá trình sạc chỉ mất khoảng 4.5 tiếng, giúp bạn nhanh chóng trở lại cuộc vui âm nhạc.", Type = ParseGadgetDescriptionType("NormalText"), Index = 6 },
new GadgetDescription { Id = Guid.Parse("65f54076-6969-4d4c-8ed5-4668661a192b"), GadgetId = Guid.Parse("07b0be10-fa0f-4588-bc32-42acbdd38521"), Value = "https://storage.googleapis.com/fbdemo-f9d5f.appspot.com/Gadgets/54dfd677-25c5-4d7f-90fc-074d07948528.jpg", Type = ParseGadgetDescriptionType("Image"), Index = 7 },
new GadgetDescription { Id = Guid.Parse("e4898b82-01a1-4b4d-afa3-82d84d468042"), GadgetId = Guid.Parse("07b0be10-fa0f-4588-bc32-42acbdd38521"), Value = "Công nghệ kết nối không dây tiên tiến Bluetooth 5.1, loa Marshall này không chỉ mang lại sự tiện lợi mà còn đảm bảo kết nối ổn định và độ trễ thấp. Bạn có thể dễ dàng kết nối Middleton với các thiết bị di động một cách nhanh chóng và đơn giản chỉ bằng một vài thao tác. Khả năng chống nước IP67 trên loa giúp bạn có thể thoải mái mang theo Middleton khi đi bất cứ nơi nào mà không cần phải lo lắng về việc loa bị nước hoặc bụi xâm nhập. Với khả năng chống nước và chống bụi tuyệt vời này, Middleton trở thành người bạn đồng hành lý tưởng cho mọi cuộc phiêu lưu và trải nghiệm ngoài trời của bạn.", Type = ParseGadgetDescriptionType("NormalText"), Index = 8 },
new GadgetDescription { Id = Guid.Parse("cb2b57ed-5c79-487a-81aa-d1a58e0ecfb6"), GadgetId = Guid.Parse("07b0be10-fa0f-4588-bc32-42acbdd38521"), Value = "Thiết kế cuốn hút", Type = ParseGadgetDescriptionType("BoldText"), Index = 9 },
new GadgetDescription { Id = Guid.Parse("72b3f503-9626-4e8f-898d-6b5a15cbd56a"), GadgetId = Guid.Parse("07b0be10-fa0f-4588-bc32-42acbdd38521"), Value = "Loa Marshall Middleton gây ấn tượng mạnh mẽ với thiết kế cổ điển đặc trưng của Marshall, mang đậm chất rock 'n' roll. Vỏ loa được chế tác từ các chất liệu cao cấp, tạo cảm giác chắc chắn và bền bỉ, đồng thời tôn lên vẻ sang trọng và đẳng cấp.", Type = ParseGadgetDescriptionType("NormalText"), Index = 10 },
new GadgetDescription { Id = Guid.Parse("df0aeaf9-e088-46f9-be76-2be777a54204"), GadgetId = Guid.Parse("07b0be10-fa0f-4588-bc32-42acbdd38521"), Value = "https://storage.googleapis.com/fbdemo-f9d5f.appspot.com/Gadgets/e5dbcd73-97a4-4556-8681-2b63fe870026.jpg", Type = ParseGadgetDescriptionType("Image"), Index = 11 },
new GadgetDescription { Id = Guid.Parse("dd2c9399-9520-426f-806c-fd9e5db18de2"), GadgetId = Guid.Parse("07b0be10-fa0f-4588-bc32-42acbdd38521"), Value = "Mặt trước của loa được trang trí bởi dòng logo Marshall nổi bật, mang lại vẻ ngoài mạnh mẽ và tinh tế. Các nút điều khiển được thiết kế đơn giản nhưng tinh tế, dễ dàng sử dụng và điều chỉnh. Kích thước nhỏ gọn và thiết kế tiện lợi giúp Marshall Middleton dễ dàng di chuyển và phù hợp với nhiều không gian khác nhau, từ phòng khách, phòng làm việc đến các buổi dã ngoại hoặc tiệc tùng ngoài trời.", Type = ParseGadgetDescriptionType("NormalText"), Index = 12 },
new GadgetDescription { Id = Guid.Parse("68e6bcfd-1ab2-46c9-bb34-af1acc3b3374"), GadgetId = Guid.Parse("07b0be10-fa0f-4588-bc32-42acbdd38521"), Value = "Loa Bluetooth Marshall Middleton là sự kết hợp hoàn hảo giữa công nghệ âm thanh tiên tiến và thiết kế tinh tế giúp loa đáp ứng được nhu cầu nghe nhạc của những người yêu âm nhạc khó tính nhất. Với tính di động cao và khả năng kết nối Bluetooth tiện lợi, loa Marshall Middleton là người bạn đồng hành lý tưởng trong mọi hoạt động, từ nghe nhạc thư giãn đến các buổi tiệc sôi động.", Type = ParseGadgetDescriptionType("NormalText"), Index = 13 },
new GadgetDescription { Id = Guid.Parse("ee25e812-ed1e-4e6f-ab0d-aeab5fbcc068"), GadgetId = Guid.Parse("6a6bb788-5dfc-423f-828c-71d3d29fdc2a"), Value = "Marshall Stockwell II là một trong những mẫu loa Bluetooth đáng chú ý với sự kết hợp tinh tế giữa thiết kế cổ điển và công nghệ hiện đại. Với vẻ ngoài đậm chất retro, loa này mang đậm dấu ấn của thương hiệu âm nhạc huyền thoại Marshall.", Type = ParseGadgetDescriptionType("BoldText"), Index = 0 },
new GadgetDescription { Id = Guid.Parse("669a29e5-5a0a-49b9-be77-644a7be3337a"), GadgetId = Guid.Parse("6a6bb788-5dfc-423f-828c-71d3d29fdc2a"), Value = "Vẻ ngoài nhỏ gọn đậm chất cổ điển", Type = ParseGadgetDescriptionType("BoldText"), Index = 1 },
new GadgetDescription { Id = Guid.Parse("7dbb2a1a-8d2a-4d63-959b-5515f3b1c3d3"), GadgetId = Guid.Parse("6a6bb788-5dfc-423f-828c-71d3d29fdc2a"), Value = "Thiết kế của loa Marshall là sự kết hợp giữa phong cách cổ điển và hiện đại. Với kích thước nhỏ gọn và kiểu dáng vuông vức kết hợp cùng quai đeo giúp loa này dễ dàng mang theo bất kỳ nơi đâu bạn mong muốn.", Type = ParseGadgetDescriptionType("NormalText"), Index = 2 },
new GadgetDescription { Id = Guid.Parse("63c17dba-ef51-4c09-b71c-1f96399abcb1"), GadgetId = Guid.Parse("6a6bb788-5dfc-423f-828c-71d3d29fdc2a"), Value = "https://storage.googleapis.com/fbdemo-f9d5f.appspot.com/Gadgets/3eb8bbc1-b9ec-4f09-ac70-71aba987b027.jpg", Type = ParseGadgetDescriptionType("Image"), Index = 3 },
new GadgetDescription { Id = Guid.Parse("f20b3af0-c0db-465b-b4c6-d96add268d79"), GadgetId = Guid.Parse("6a6bb788-5dfc-423f-828c-71d3d29fdc2a"), Value = "Với kích thước nhỏ gọn, loa dễ dàng mang theo bên mình, phù hợp cho những chuyến du lịch hay các buổi dã ngoại. Vỏ ngoài của loa được bọc trong chất liệu cao cấp, mang lại cảm giác chắc chắn và bền bỉ, cùng với lớp hoàn thiện bên ngoài tạo vẻ sang trọng và đẳng cấp. Các nút điều khiển của Stockwell II được thiết kế đơn giản và tinh tế, nằm ở phía trên loa, giúp người dùng dễ dàng điều chỉnh.", Type = ParseGadgetDescriptionType("NormalText"), Index = 4 },
