new GadgetDescription { Id = Guid.Parse("b48adaa9-e649-482b-bba6-fd14655b7824"), GadgetId = Guid.Parse("6a6bb788-5dfc-423f-828c-71d3d29fdc2a"), Value = "Marshall Stockwell II mang lại hiệu suất âm thanh mạnh mẽ và rõ ràng mặc dù sở hữu kích thước nhỏ gọn với tổng công suất lên đến 20 W. Dù kích thước nhỏ, nhưng loa này vẫn đủ sức để tạo ra không gian âm nhạc sôi động và thú vị, phục vụ cho nhu cầu giải trí của bạn trong nhiều tình huống khác nhau.", Type = ParseGadgetDescriptionType("NormalText"), Index = 6 },
