new GadgetDescription { Id = Guid.Parse("2a3607a5-dc8c-4a08-af40-92fd657cdbf7"), GadgetId = Guid.Parse("878e92e5-5822-47e1-ad4e-ce8aa007d7a9"), Value = "Âm thanh vang dội lấp đầy cả không gian với 8 driver cho 2 loa vệ tinh", Type = ParseGadgetDescriptionType("BoldText"), Index = 6 },
