new GadgetDescription { Id = Guid.Parse("317a21a9-1c8b-4ee4-8255-ddb3c4d1ddf8"), GadgetId = Guid.Parse("3bd7d862-9aeb-4849-b95d-0cea10ddc8d4"), Value = "Go + Play 3 sở hữu thiết kế bán nguyệt quen thuộc của dòng Go + Play, nhưng được nâng cấp với mặt lưới tản nhiệt bắt mắt hơn và tay cầm bằng nhôm chắc chắn. Loa được bao bọc bởi lớp vải cao cấp, tạo cảm giác sang trọng và tinh tế. Kích thước nhỏ gọn cùng khối lượng chỉ 4.7 kg giúp bạn dễ dàng mang theo loa mọi lúc mọi nơi.", Type = ParseGadgetDescriptionType("NormalText"), Index = 2 },
