new GadgetDescription { Id = Guid.Parse("481ca0c5-14e1-4b89-a5dc-6995fa66775b"), GadgetId = Guid.Parse("a19148a8-2272-475e-8774-bf5f1ce61118"), Value = "Sở hữu 1 củ loa subwoofer, 2 củ loa tweeter cho âm siêu trầm rõ ràng, âm treble trong trẻo, dải âm rộng, thể hiện trọn vẹn mọi thể loại nhạc. Với mức công suất là 50 W, dư sức để bạn mở ra không gian âm nhạc riêng ngay tại góc phòng nhỏ của mình.", Type = ParseGadgetDescriptionType("NormalText"), Index = 4 },
