new GadgetDescription { Id = Guid.Parse("d52519e8-5240-46ad-a90f-7a7567d0a65c"), GadgetId = Guid.Parse("a19148a8-2272-475e-8774-bf5f1ce61118"), Value = "Công nghệ Bluetooth 5.2 hiện đại, đường truyền mượt mà, kết nối ổn định trong phạm vi 10 m.", Type = ParseGadgetDescriptionType("NormalText"), Index = 6 },
new GadgetDescription { Id = Guid.Parse("beeb7d14-5b7e-4b70-92ce-231fedbe26b7"), GadgetId = Guid.Parse("3d9bfed1-4b10-4695-a719-ce116448c3be"), Value = "Loa Bluetooth Sony ULT10 Field 1 là sự lựa chọn hoàn hảo cho những ai yêu thích âm nhạc và muốn mang theo giai điệu yêu thích đến bất cứ đâu. Với thiết kế nhỏ gọn, âm thanh mạnh mẽ cùng nhiều tính năng tiện ích, sản phẩm này không chỉ mang đến trải nghiệm âm nhạc tuyệt vời mà còn đảm bảo độ bền và khả năng chống chịu trong nhiều điều kiện khác nhau.", Type = ParseGadgetDescriptionType("BoldText"), Index = 0 },
new GadgetDescription { Id = Guid.Parse("29161f97-a3be-41dc-a348-4616a61c9a3b"), GadgetId = Guid.Parse("3d9bfed1-4b10-4695-a719-ce116448c3be"), Value = "Âm thanh chất lượng, âm trầm ấn tượng", Type = ParseGadgetDescriptionType("BoldText"), Index = 1 },
new GadgetDescription { Id = Guid.Parse("c7ffe43f-0fc3-4a42-b727-4ea66471f11e"), GadgetId = Guid.Parse("3d9bfed1-4b10-4695-a719-ce116448c3be"), Value = "Sony ULT 10 Field 1 sở hữu âm thanh vượt trội, đặc biệt là khả năng nâng cao chất lượng âm trầm. Thiết kế nhỏ nhắn không làm giảm đi chất lượng âm thanh, ngược lại, chiếc loa này mang đến âm trầm mạnh mẽ và sâu lắng, giúp bạn thưởng thức từng giai điệu một cách chân thực và rõ ràng.", Type = ParseGadgetDescriptionType("NormalText"), Index = 2 },
new GadgetDescription { Id = Guid.Parse("ac145263-33eb-46af-a967-155f86cc8362"), GadgetId = Guid.Parse("3d9bfed1-4b10-4695-a719-ce116448c3be"), Value = "Nhờ công nghệ tăng cường âm trầm, âm thanh ở tần số thấp được khuếch đại, mang đến trải nghiệm nghe sống động hơn. Loa Sony có chế độ ULT POWER SOUND giúp tăng cường âm lượng và nâng cao chất lượng âm trầm, khiến bạn như được đắm chìm trong thế giới âm nhạc.", Type = ParseGadgetDescriptionType("NormalText"), Index = 3 },
new GadgetDescription { Id = Guid.Parse("3548cfc1-5688-404b-84d1-c57f27ad182d"), GadgetId = Guid.Parse("3d9bfed1-4b10-4695-a719-ce116448c3be"), Value = "https://storage.googleapis.com/fbdemo-f9d5f.appspot.com/Gadgets/566692a1-27e4-42af-9b6f-bbcb3eac2be2.jpg", Type = ParseGadgetDescriptionType("Image"), Index = 4 },
new GadgetDescription { Id = Guid.Parse("bfbbe08b-2c22-40c6-a327-6d31b5668d12"), GadgetId = Guid.Parse("3d9bfed1-4b10-4695-a719-ce116448c3be"), Value = "Bố cục của bộ tản âm thụ động được tối ưu hóa để nâng cao chất lượng âm trầm, giúp lan tỏa âm thanh đều trong không gian. Dù kích thước nhỏ gọn nhưng Sony ULT 10 Field 1 vẫn có khả năng phát ra âm thanh vòm ấn tượng nhờ công nghệ DSP (Digital Signal Processing), giúp âm thanh được khuếch tán rộng rãi, phủ kín không gian xung quanh.", Type = ParseGadgetDescriptionType("NormalText"), Index = 5 },
new GadgetDescription { Id = Guid.Parse("051d3345-0393-4a15-894e-93a5fc8d446d"), GadgetId = Guid.Parse("3d9bfed1-4b10-4695-a719-ce116448c3be"), Value = "Đồng hành cùng bạn cả ngày dài", Type = ParseGadgetDescriptionType("BoldText"), Index = 6 },
new GadgetDescription { Id = Guid.Parse("617f35b5-1ef4-46c1-921c-37464666a2d4"), GadgetId = Guid.Parse("3d9bfed1-4b10-4695-a719-ce116448c3be"), Value = "Sony ULT 10 Field 1 không chỉ nổi bật với âm thanh mà còn là thiết bị cực kỳ tiện lợi khi mang theo bên mình. Với thời lượng pin kéo dài lên đến 12 giờ, bạn có thể tận hưởng âm nhạc suốt cả ngày mà không lo hết pin. Loa cũng hỗ trợ sạc nhanh qua cổng USB Type-C, mang lại sự tiện lợi và nhanh chóng cho người dùng.", Type = ParseGadgetDescriptionType("NormalText"), Index = 7 },
new GadgetDescription { Id = Guid.Parse("74345f6a-9fff-4eda-8489-920be48080d6"), GadgetId = Guid.Parse("3d9bfed1-4b10-4695-a719-ce116448c3be"), Value = "https://storage.googleapis.com/fbdemo-f9d5f.appspot.com/Gadgets/be66c7ab-c597-4d30-9440-6dd519998a5d.jpg", Type = ParseGadgetDescriptionType("Image"), Index = 8 },
new GadgetDescription { Id = Guid.Parse("cc3ac99b-6bac-428f-8680-9e277996c05e"), GadgetId = Guid.Parse("3d9bfed1-4b10-4695-a719-ce116448c3be"), Value = "Thiết kế nhỏ gọn, vừa vặn trong tay, cho phép bạn dễ dàng mang loa đi khắp nơi. Dây đeo đa hướng tháo rời là một lợi thế, giúp bạn treo loa hoặc mang theo một cách tiện lợi ở bất kỳ không gian nào ngoài trời. Từ công viên, hồ bơi, đến bãi biển, Sony ULT 10 Field 1 sẽ là người bạn đồng hành lý tưởng trong mọi chuyến đi.", Type = ParseGadgetDescriptionType("NormalText"), Index = 9 },
new GadgetDescription { Id = Guid.Parse("6a946a5c-2c31-4f29-ab3b-098e1d2b89c0"), GadgetId = Guid.Parse("3d9bfed1-4b10-4695-a719-ce116448c3be"), Value = "Với tiêu chuẩn chống nước và chống bụi IP67, loa Bluetooth Sony có thể chịu được nhiều điều kiện thời tiết và môi trường khắc nghiệt. Bạn có thể hoàn toàn yên tâm sử dụng loa trong môi trường ẩm ướt, như khi đi hồ bơi hay dã ngoại dưới mưa, mà không lo bị hỏng. Đặc biệt, loa còn có khả năng chống nước mặn, cho phép bạn tận hưởng âm nhạc ngay tại bãi biển mà không phải lo lắng.", Type = ParseGadgetDescriptionType("NormalText"), Index = 10 },
new GadgetDescription { Id = Guid.Parse("3ccbdf34-08c4-45db-82ac-ca1da057e17f"), GadgetId = Guid.Parse("3d9bfed1-4b10-4695-a719-ce116448c3be"), Value = "https://storage.googleapis.com/fbdemo-f9d5f.appspot.com/Gadgets/d2c6cdc9-17bb-482e-8150-53f499105c6c.jpg", Type = ParseGadgetDescriptionType("Image"), Index = 11 },
new GadgetDescription { Id = Guid.Parse("4d057126-dee5-4931-be16-6292d12245e9"), GadgetId = Guid.Parse("54cce895-ac4e-4b87-885b-2ae064aed97c"), Value = "Sony ULT Field 7 là sự lựa chọn hoàn hảo cho những ai yêu thích âm nhạc và muốn tận hưởng những bữa tiệc không giới hạn. Với thiết kế nhỏ gọn, tiện lợi, cùng nhiều tính năng hiện đại như âm trầm mạnh mẽ, hệ thống đèn LED rực rỡ và khả năng chống nước, chống bụi, sản phẩm này chắc chắn sẽ là người bạn đồng hành hoàn hảo cho mọi hoạt động ngoài trời của bạn.", Type = ParseGadgetDescriptionType("BoldText"), Index = 0 },
new GadgetDescription { Id = Guid.Parse("6a43cccb-3de2-4b2d-8c32-edac6de5d6ba"), GadgetId = Guid.Parse("54cce895-ac4e-4b87-885b-2ae064aed97c"), Value = "Nâng cao trải nghiệm âm nhạc cho bạn", Type = ParseGadgetDescriptionType("BoldText"), Index = 1 },
new GadgetDescription { Id = Guid.Parse("7ad89291-5f7f-4abf-8b7b-d869490ce4f1"), GadgetId = Guid.Parse("54cce895-ac4e-4b87-885b-2ae064aed97c"), Value = "Loa Sony không chỉ mang đến âm trầm sâu lắng, mà còn cải thiện rõ rệt chất lượng âm thanh ở tần số thấp, cho phép bạn cảm nhận từng nhịp điệu một cách chân thực và sống động hơn bao giờ hết. Với X-Balanced Speaker Unit, âm thanh phát ra từ loa trở nên mạnh mẽ, sống động và vang vọng hơn, mang lại cho bạn cảm giác như đang ở giữa một buổi hòa nhạc trực tiếp.", Type = ParseGadgetDescriptionType("NormalText"), Index = 2 },
new GadgetDescription { Id = Guid.Parse("4f703277-75fd-4186-beb3-eabe4769a17e"), GadgetId = Guid.Parse("54cce895-ac4e-4b87-885b-2ae064aed97c"), Value = "Sony ULT Field 7 không chỉ phát nhạc với chất lượng cao mà còn có hai chế độ âm thanh đặc biệt: ULT1 và ULT2, giúp bạn dễ dàng điều chỉnh âm thanh theo ý thích. Nếu bạn muốn âm bass sâu và âm thanh trầm hơn, bạn hãy chọn ULT1. Còn nếu bạn yêu thích âm thanh mạnh mẽ và sôi động, ULT2 sẽ là sự lựa chọn tuyệt vời.", Type = ParseGadgetDescriptionType("NormalText"), Index = 3 },
new GadgetDescription { Id = Guid.Parse("63751098-b69e-4ae2-8797-794aa42a0216"), GadgetId = Guid.Parse("54cce895-ac4e-4b87-885b-2ae064aed97c"), Value = "https://storage.googleapis.com/fbdemo-f9d5f.appspot.com/Gadgets/11ad2c4c-8ad6-4de7-b550-29c94a23a529.jpg", Type = ParseGadgetDescriptionType("Image"), Index = 4 },
new GadgetDescription { Id = Guid.Parse("4ed5fecd-06c2-4b52-9173-1052cec99e1b"), GadgetId = Guid.Parse("54cce895-ac4e-4b87-885b-2ae064aed97c"), Value = "Ánh đèn khuấy động bữa tiệc của bạn", Type = ParseGadgetDescriptionType("BoldText"), Index = 5 },
new GadgetDescription { Id = Guid.Parse("04692db2-66ff-42fb-96bb-ea5327ba74df"), GadgetId = Guid.Parse("54cce895-ac4e-4b87-885b-2ae064aed97c"), Value = "Một trong những tính năng nổi bật của Sony ULT Field 7 chính là hệ thống ánh sáng đầy màu sắc, đồng bộ hoàn hảo với giai điệu âm nhạc. Loa có đèn LED sẽ góp phần mang đến bầu không khí vui nhộn hơn, đặc biệt là hệ thống đèn nhấp nháy theo nhạc, giúp các bữa tiệc thêm phần hấp dẫn và thú vị.", Type = ParseGadgetDescriptionType("NormalText"), Index = 6 },
new GadgetDescription { Id = Guid.Parse("6e02c163-3cbf-4bfe-a407-e14b4d182243"), GadgetId = Guid.Parse("54cce895-ac4e-4b87-885b-2ae064aed97c"), Value = "https://storage.googleapis.com/fbdemo-f9d5f.appspot.com/Gadgets/55732824-21e8-48a5-ba60-5f5ac0fb8f21.jpg", Type = ParseGadgetDescriptionType("Image"), Index = 7 },
new GadgetDescription { Id = Guid.Parse("472ca827-9a2c-4424-9d72-a32c6955401f"), GadgetId = Guid.Parse("54cce895-ac4e-4b87-885b-2ae064aed97c"), Value = "Bạn hoàn toàn có thể điều khiển đèn và nhạc thông qua ứng dụng Sony | Music Center và Fiestable, từ việc chọn bài hát đến điều chỉnh ánh sáng và âm thanh. Với tính năng Điều khiển DJ trên ứng dụng, bạn sẽ trở thành người làm chủ bữa tiệc, dễ dàng điều chỉnh nhạc và tạo ra không khí sôi nổi ở bất cứ đâu.", Type = ParseGadgetDescriptionType("NormalText"), Index = 8 },
new GadgetDescription { Id = Guid.Parse("8211476c-da8d-4ff8-970b-8c51207f1e3f"), GadgetId = Guid.Parse("54cce895-ac4e-4b87-885b-2ae064aed97c"), Value = "Thoải mái mang theo, thời gian sử dụng lâu dài", Type = ParseGadgetDescriptionType("BoldText"), Index = 9 },
new GadgetDescription { Id = Guid.Parse("c644c2bf-8188-459f-bd17-200fec45babe"), GadgetId = Guid.Parse("54cce895-ac4e-4b87-885b-2ae064aed97c"), Value = "Được thiết kế với kiểu dáng nhỏ gọn, dễ cầm nắm và tay cầm thoải mái, loa bluetooth cho phép bạn dễ dàng mang theo loa đến bất kỳ địa điểm nào. Bạn có thể đặt loa theo hai tư thế: thẳng đứng hoặc nằm ngang, tùy thuộc vào không gian sử dụng, giúp bạn tận dụng hiệu quả không gian xung quanh, cho dù đó là một buổi tiệc ngoài trời, dã ngoại hay trong nhà.", Type = ParseGadgetDescriptionType("NormalText"), Index = 10 },
new GadgetDescription { Id = Guid.Parse("c4883684-e0c2-4a57-bd76-3f713f2c27df"), GadgetId = Guid.Parse("54cce895-ac4e-4b87-885b-2ae064aed97c"), Value = "https://storage.googleapis.com/fbdemo-f9d5f.appspot.com/Gadgets/d73da8a3-f5f0-4969-8c45-adb9e2d02e64.jpg", Type = ParseGadgetDescriptionType("Image"), Index = 11 },
new GadgetDescription { Id = Guid.Parse("d4e81377-e6d3-4fb0-8c93-bd393b0e30b8"), GadgetId = Guid.Parse("54cce895-ac4e-4b87-885b-2ae064aed97c"), Value = "Khả năng chống nước và chống bụi đạt chuẩn IP67 của loa giúp bạn yên tâm sử dụng ở bất cứ môi trường nào, từ bãi biển, hồ bơi đến công viên mà không lo bị hư hại. Thậm chí, sau khi sử dụng, bạn hoàn toàn có thể rửa sạch loa mà không cần lo ngại về việc nước có thể ảnh hưởng đến chất lượng của nó. Đặc biệt, Sony ULT Field 7 còn có khả năng chống nước mặn, cho phép bạn thưởng thức âm nhạc trên bãi biển mà không lo bị hư hỏng do muối biển.", Type = ParseGadgetDescriptionType("NormalText"), Index = 12 },
new GadgetDescription { Id = Guid.Parse("5c27e0ce-d230-4544-99df-aec3a38f72bc"), GadgetId = Guid.Parse("54cce895-ac4e-4b87-885b-2ae064aed97c"), Value = "Không chỉ bền bỉ, thời gian sử dụng pin lên đến 30 giờ của ULT Field 7 giúp bạn thoải mái phát nhạc suốt cả ngày dài. Và trong trường hợp pin yếu, bạn có thể tận dụng tính năng sạc nhanh, chỉ cần 10 phút sạc để sử dụng thêm 3 giờ.", Type = ParseGadgetDescriptionType("NormalText"), Index = 13 },
new GadgetDescription { Id = Guid.Parse("6548c034-6a6a-4898-a965-bf0038aadbaf"), GadgetId = Guid.Parse("22f9a972-7cd6-4ea8-b950-1d29a3106f32"), Value = "Loa Bluetooth Sony SRS-XB100 sở hữu thiết kế vô cùng nhỏ gọn, độ bền cao, chất âm ấn tượng cùng thời lượng pin lâu dài,... đáp ứng đa dạng nhu cầu sử dụng của người dùng mọi lúc mọi nơi.", Type = ParseGadgetDescriptionType("BoldText"), Index = 0 },
new GadgetDescription { Id = Guid.Parse("7f2fc7d7-ca19-482e-a685-6ba8c079e3f8"), GadgetId = Guid.Parse("22f9a972-7cd6-4ea8-b950-1d29a3106f32"), Value = "Thiết kế có độ bền cao từ vật liệu thân thiện với môi trường", Type = ParseGadgetDescriptionType("BoldText"), Index = 1 },
new GadgetDescription { Id = Guid.Parse("1aaff86a-00a4-4825-9597-4698f91bee6e"), GadgetId = Guid.Parse("22f9a972-7cd6-4ea8-b950-1d29a3106f32"), Value = "Sony SRS-XB 100 mang diện mạo hình trụ vô cùng nhỏ gọn với kích thước 7.6 x 7.6 x 9.5 cm. Được hoàn thiện từ nhựa tái chế thân thiện với môi trường giúp sản phẩm có độ bền cao, chịu lực tốt trước các va chạm. Sony cũng trang bị nhiều phiên bản màu năng động cho dòng loa này, đáp ứng được nhiều sở thích khác nhau của người dùng, trong bài viết này mình sử dụng phiên bản màu cam.", Type = ParseGadgetDescriptionType("NormalText"), Index = 2 },
new GadgetDescription { Id = Guid.Parse("f796e40e-297b-4b14-84dd-92f896e8ed82"), GadgetId = Guid.Parse("22f9a972-7cd6-4ea8-b950-1d29a3106f32"), Value = "https://storage.googleapis.com/fbdemo-f9d5f.appspot.com/Gadgets/4a5c7dc8-2629-45f9-915e-9e59ee6d1d2f.jpg", Type = ParseGadgetDescriptionType("Image"), Index = 3 },
new GadgetDescription { Id = Guid.Parse("ed09fc29-2697-43c5-8320-c04de9271df5"), GadgetId = Guid.Parse("22f9a972-7cd6-4ea8-b950-1d29a3106f32"), Value = "Khối lượng tổng thể chỉ 0.274 kg với cách phân bố đều linh kiện ở bên trong cho mình cảm giác rất đằm tay khi cầm nắm. Bên cạnh đó, mình còn có thể dễ dàng bỏ vào balo, túi xách mang đi bất kì đâu mà không phải lo về vấn đề cân nặng.", Type = ParseGadgetDescriptionType("NormalText"), Index = 4 },
new GadgetDescription { Id = Guid.Parse("51511abd-d992-460e-bc85-3a54bbf3ccaa"), GadgetId = Guid.Parse("22f9a972-7cd6-4ea8-b950-1d29a3106f32"), Value = "https://storage.googleapis.com/fbdemo-f9d5f.appspot.com/Gadgets/55ff92ce-2ce6-4472-9422-bc0f22fae4a0.jpg", Type = ParseGadgetDescriptionType("Image"), Index = 5 },
new GadgetDescription { Id = Guid.Parse("6be0fea1-a707-477e-abde-c5fe994fd924"), GadgetId = Guid.Parse("22f9a972-7cd6-4ea8-b950-1d29a3106f32"), Value = "So với đàn anh trước đó là XB13 thì mẫu loa này cho thiết kế hiện đại và tinh tế hơn khi logo SONY được khắc chìm trên thân loa, phần lưới cũng được thiết kế với màng loa rộng hơn.", Type = ParseGadgetDescriptionType("NormalText"), Index = 6 },
new GadgetDescription { Id = Guid.Parse("5fbf0ba2-19bc-4e88-968e-c3d900950129"), GadgetId = Guid.Parse("22f9a972-7cd6-4ea8-b950-1d29a3106f32"), Value = "https://storage.googleapis.com/fbdemo-f9d5f.appspot.com/Gadgets/07b8bd87-e054-4a05-897d-0d59432f4952.jpg", Type = ParseGadgetDescriptionType("Image"), Index = 7 },
new GadgetDescription { Id = Guid.Parse("0477c857-000b-4501-8c1b-38b493e7150c"), GadgetId = Guid.Parse("22f9a972-7cd6-4ea8-b950-1d29a3106f32"), Value = "Thiết kế đường vân dọc trên thân giúp loa không bị trơn trượt khi dính nước. Xung quanh thân dưới còn có những lỗ khoét ngang dài giúp sản phẩm thêm phần độc đáo. Phần đáy được hoàn thiện với lớp cao su dẹt tạo độ bám chắc chắn cho loa trên mọi mặt phẳng.", Type = ParseGadgetDescriptionType("NormalText"), Index = 8 },
new GadgetDescription { Id = Guid.Parse("bf3d3b1a-12e4-4bbc-9f08-845c7d7426f1"), GadgetId = Guid.Parse("22f9a972-7cd6-4ea8-b950-1d29a3106f32"), Value = "https://storage.googleapis.com/fbdemo-f9d5f.appspot.com/Gadgets/6b229882-3a33-43d2-9a7d-ccead585bcf5.jpg", Type = ParseGadgetDescriptionType("Image"), Index = 9 },
new GadgetDescription { Id = Guid.Parse("ebfd87b9-147f-4026-a7b8-061537e98a82"), GadgetId = Guid.Parse("22f9a972-7cd6-4ea8-b950-1d29a3106f32"), Value = "Dây đeo trên loa cũng được làm từ chất liệu nhựa tái chế có độ chắc chắn rất cao, giúp chúng ta yên tâm khi treo trên balo, trên xe hay bất cứ nơi nào. Phần móc gài với logo Sony được khắc chìm cũng cho thấy sự chỉn chu, tinh tế đến từng chi tiết nhỏ nhất của hãng.", Type = ParseGadgetDescriptionType("NormalText"), Index = 10 },
new GadgetDescription { Id = Guid.Parse("5b7747fe-7d39-4312-ac90-b10c6a05f9c7"), GadgetId = Guid.Parse("22f9a972-7cd6-4ea8-b950-1d29a3106f32"), Value = "https://storage.googleapis.com/fbdemo-f9d5f.appspot.com/Gadgets/65ffdc13-ae8f-4449-8b09-6774a340f731.jpg", Type = ParseGadgetDescriptionType("Image"), Index = 11 },
new GadgetDescription { Id = Guid.Parse("bac5ed1a-9764-4ee2-9e22-bee73826c266"), GadgetId = Guid.Parse("83a0e0ad-e849-423f-ae3d-af07576ba0c0"), Value = "Bộ loa Sony HT-S20R 400W phát âm thanh sống động với tổng công suất 400W, tái tạo âm thanh vòm Dolby Digital bao trùm không gian, điều khiển tiện lợi với remote đi kèm, phối ghép với các thiết bị di động dễ dàng qua kết nối không dây chuẩn Bluetooth 5.0.", Type = ParseGadgetDescriptionType("BoldText"), Index = 0 },
new GadgetDescription { Id = Guid.Parse("406766ee-f246-4b95-b915-db91484d1e1c"), GadgetId = Guid.Parse("83a0e0ad-e849-423f-ae3d-af07576ba0c0"), Value = "Thiết kế", Type = ParseGadgetDescriptionType("BoldText"), Index = 1 },
new GadgetDescription { Id = Guid.Parse("30611e0d-cda5-43bb-af6b-248d3748c6c1"), GadgetId = Guid.Parse("83a0e0ad-e849-423f-ae3d-af07576ba0c0"), Value = "Sony HT-S20R gồm có 1 loa thanh, 1 loa Subwoofer và 2 loa sau thiết kế hiện đại, bề mặt đều bao phủ sắc đen thuần khiết, sang trọng, kết nối hài hòa với thiết kế nội thất của phòng ngủ, phòng khách, phòng giải trí hay văn phòng làm việc của bạn.", Type = ParseGadgetDescriptionType("NormalText"), Index = 2 },
new GadgetDescription { Id = Guid.Parse("9f40f0a2-a9ad-447e-bd9a-563ccdf66b7d"), GadgetId = Guid.Parse("83a0e0ad-e849-423f-ae3d-af07576ba0c0"), Value = "Loa thanh có kiểu dáng thanh dài, bạn có thể sắp đặt trên kệ tivi, treo tường, 2 loa sau bố trí trên kệ, treo giá trong khi loa Subwoofer có thể đặt trên kệ tủ hoặc để ở dưới sàn đều phù hợp, gọn gàng.", Type = ParseGadgetDescriptionType("NormalText"), Index = 3 },
new GadgetDescription { Id = Guid.Parse("939beb4b-b7bd-4d2f-b026-f6180bc01b65"), GadgetId = Guid.Parse("83a0e0ad-e849-423f-ae3d-af07576ba0c0"), Value = "https://storage.googleapis.com/fbdemo-f9d5f.appspot.com/Gadgets/8743b38f-5baf-4474-97b7-42f9e186a479.jpg", Type = ParseGadgetDescriptionType("Image"), Index = 4 },
