new GadgetDescription { Id = Guid.Parse("a39ca0c6-d473-49a3-bc8e-a28be9710dd2"), GadgetId = Guid.Parse("32275508-5076-4a92-b92d-eb7207c32de2"), Value = "Loa Bluetooth Monster Sparkle với hệ thống đèn LED nổi bật cùng thiết kế thuôn dài đẹp mắt, công nghệ âm thanh sống động, tốc độ kết nối nhanh chóng, tiện lợi sử dụng trong những bữa tiệc cá nhân và các buổi tụ họp bạn bè hàng ngày.", Type = ParseGadgetDescriptionType("BoldText"), Index = 0 },
