new GadgetDescription { Id = Guid.Parse("03151991-a128-4222-a1aa-89a7539eef3c"), GadgetId = Guid.Parse("25e12d28-b8f2-416c-9fdb-fd44dca31f62"), Value = "Loa Bluetooth Monster Musicbox MS22150 sở hữu thiết kế gọn nhẹ, chất lượng âm thanh sống động, thiết kế đẹp mắt cùng thời lượng pin lâu dài, hứa hẹn mang đến cho bạn trải nghiệm sử dụng tuyệt vời.", Type = ParseGadgetDescriptionType("BoldText"), Index = 0 },
