new GadgetDescription { Id = Guid.Parse("50cb02d2-b0d2-450b-8fd1-891cdbdec3cc"), GadgetId = Guid.Parse("25e12d28-b8f2-416c-9fdb-fd44dca31f62"), Value = "Loa nổi bật với thiết kế tinh tế và sang trọng, kích thước nhỏ gọn, linh hoạt và rất dễ di chuyển.", Type = ParseGadgetDescriptionType("NormalText"), Index = 2 },
new GadgetDescription { Id = Guid.Parse("a760b039-b39d-4b51-97e9-60998251d1ea"), GadgetId = Guid.Parse("25e12d28-b8f2-416c-9fdb-fd44dca31f62"), Value = "Sản phẩm được làm từ nhựa cao cấp chắc chắn và bền bỉ, giúp tăng độ bền cho loa, chịu được những va đập, rung lắc trong quá trình sử dụng mà không dễ hỏng hóc.", Type = ParseGadgetDescriptionType("NormalText"), Index = 3 },
new GadgetDescription { Id = Guid.Parse("19b66987-93cb-4c57-b636-4dc02af8285a"), GadgetId = Guid.Parse("25e12d28-b8f2-416c-9fdb-fd44dca31f62"), Value = "Loa có công suất lên đến 40 W không chỉ đáp ứng nhu cầu nghe nhạc cá nhân mà còn có khả năng phục vụ cho những buổi tiệc nhỏ, giữa không gian mở, đem lại không khí sống động, nhộn nhịp, mang đến trải nghiệm âm thanh mạnh mẽ cho người dùng.", Type = ParseGadgetDescriptionType("NormalText"), Index = 4 },
new GadgetDescription { Id = Guid.Parse("fb7717a3-2b4a-4da2-8efd-57f33665d986"), GadgetId = Guid.Parse("25e12d28-b8f2-416c-9fdb-fd44dca31f62"), Value = "Loa Monster còn được trang bị công nghệ Pure Monster Sound và màng loa chất lượng, giúp tạo ra dải âm thanh rộng, từ âm trầm mạnh mẽ cho đến âm thanh tần số cao rõ ràng.", Type = ParseGadgetDescriptionType("NormalText"), Index = 5 },
new GadgetDescription { Id = Guid.Parse("7a3069c9-df2d-48a8-88c0-72b7924f93e0"), GadgetId = Guid.Parse("25e12d28-b8f2-416c-9fdb-fd44dca31f62"), Value = "Hỗ trợ kết nối Bluetooth 5.3 tiên tiến giúp tốc độ truyền dữ liệu nhanh hơn và phạm vi kết nối rộng hơn lên đến 10 m, bạn có thể dễ dàng phát nhạc từ thiết bị di động của mình hầu như ở bất kỳ nơi nào trong nhà mà không cần phải mang theo loa.", Type = ParseGadgetDescriptionType("NormalText"), Index = 6 },
new GadgetDescription { Id = Guid.Parse("c77ae361-8da0-489d-ac22-629332d4c221"), GadgetId = Guid.Parse("25e12d28-b8f2-416c-9fdb-fd44dca31f62"), Value = "Dung lượng pin lên tới 4000 mAh, loa Bluetooth có thể hoạt động liên tục lên đến 7 giờ cho phép bạn thưởng thức âm nhạc trong thời gian dài, sạc lại 2 tiếng qua cổng Type C.", Type = ParseGadgetDescriptionType("NormalText"), Index = 7 },
new GadgetDescription { Id = Guid.Parse("a4d32897-3601-4b2d-ade7-e657e27008f4"), GadgetId = Guid.Parse("25e12d28-b8f2-416c-9fdb-fd44dca31f62"), Value = "Loa được tích hợp chuẩn chống nước IPX5, có khả năng chống lại các tia nước từ mọi hướng, do đó bạn có thể yên tâm sử dụng loa ở những nơi như gần nguồn nước như: bên cạnh bể bơi, bãi biển hoặc thậm chí dưới cơn mưa nhẹ.", Type = ParseGadgetDescriptionType("NormalText"), Index = 8 },
new GadgetDescription { Id = Guid.Parse("3eeb2f03-20c7-4258-a990-cbc0575bb3dc"), GadgetId = Guid.Parse("0bb7cefd-4058-41f5-b97f-ed41bdda9aa8"), Value = "Loa Bluetooth Monster Adventurer MAX là một chiếc loa di động có kích thước lớn với công suất lên tới 60 W, được trang bị nhiều tính năng hiện đại và khả năng chống nước IPX7. Loa phù hợp với những người yêu thích âm nhạc, muốn có một trải nghiệm âm thanh chất lượng cao khi đi du lịch, dã ngoại hoặc tổ chức tiệc tùng.", Type = ParseGadgetDescriptionType("BoldText"), Index = 0 },
new GadgetDescription { Id = Guid.Parse("9ea8814a-b761-4032-bf31-8aded20568d5"), GadgetId = Guid.Parse("0bb7cefd-4058-41f5-b97f-ed41bdda9aa8"), Value = "Thiết kế độc đáo, có thể di chuyển đến nhiều nơi", Type = ParseGadgetDescriptionType("BoldText"), Index = 1 },
new GadgetDescription { Id = Guid.Parse("61f5788a-641a-4281-b896-2ae4bad7f2f7"), GadgetId = Guid.Parse("0bb7cefd-4058-41f5-b97f-ed41bdda9aa8"), Value = "Monster Adventurer MAX được làm từ chất liệu nhựa có khả năng chịu lực và chống va đập tốt. Mặt trước loa được trang bị lưới vừa bảo vệ các linh kiện bên trong vừa không cản trở âm thanh phát ra. Loa Bluetooth được trang bị tay cầm chắc chắn, khi mình cầm thì thấy bề mặt khá mịn, với khối lượng 2.25 kg thì việc di chuyển khá đơn giản.", Type = ParseGadgetDescriptionType("NormalText"), Index = 2 },
new GadgetDescription { Id = Guid.Parse("a51cab37-b8dc-4589-80ff-aae635bad4d2"), GadgetId = Guid.Parse("0bb7cefd-4058-41f5-b97f-ed41bdda9aa8"), Value = "https://storage.googleapis.com/fbdemo-f9d5f.appspot.com/Gadgets/6d350c5b-b462-4386-85d9-4163789d3a64.jpg", Type = ParseGadgetDescriptionType("Image"), Index = 3 },
new GadgetDescription { Id = Guid.Parse("e62f91f3-7710-4b8a-ba55-9323c9bf9581"), GadgetId = Guid.Parse("0bb7cefd-4058-41f5-b97f-ed41bdda9aa8"), Value = "Phía trên loa được trang bị 5 phím nhấn với các chức năng sau: Bật/tắt loa, kết nối Bluetooth, giảm âm lượng, tăng âm lượng và dừng/phát nhạc. Ở nút bật/tắt loa và kết nối Bluetooth được trang bị đèn LED báo hiệu trạng thái hoạt động, tiện lợi trong quá trình sử dụng.", Type = ParseGadgetDescriptionType("NormalText"), Index = 4 },
new GadgetDescription { Id = Guid.Parse("34fe2db0-5639-468f-b7ed-a948786d6ce2"), GadgetId = Guid.Parse("0bb7cefd-4058-41f5-b97f-ed41bdda9aa8"), Value = "Mặt sau của chiếc loa Monster này được tích hợp 5 cổng chức năng gồm: Jack cắm micro hỗ trợ khi hát karaoke, cổng AUX IN cho phép kết nối với các thiết bị khác, cổng sạc, cổng USB, khe gắn thẻ nhớ giúp phát nhạc trực tiếp từ thẻ nhớ SD và 1 nút reset. Các cổng và nút này được bảo vệ bởi một nắp đậy kín giúp hạn chế bụi xâm nhập.", Type = ParseGadgetDescriptionType("NormalText"), Index = 5 },
new GadgetDescription { Id = Guid.Parse("59c3ca95-afa3-469c-b568-4e2c074ae27f"), GadgetId = Guid.Parse("0bb7cefd-4058-41f5-b97f-ed41bdda9aa8"), Value = "https://storage.googleapis.com/fbdemo-f9d5f.appspot.com/Gadgets/e71a9811-5970-4d1e-a865-9717d68de4de.jpg", Type = ParseGadgetDescriptionType("Image"), Index = 6 },
new GadgetDescription { Id = Guid.Parse("6a7e0849-df29-463e-ace7-464cade03faf"), GadgetId = Guid.Parse("0bb7cefd-4058-41f5-b97f-ed41bdda9aa8"), Value = "Tận hưởng trọn vẹn từng giai điệu", Type = ParseGadgetDescriptionType("BoldText"), Index = 7 },
new GadgetDescription { Id = Guid.Parse("0cd43264-dd79-4922-9b33-fd380698d9ec"), GadgetId = Guid.Parse("0bb7cefd-4058-41f5-b97f-ed41bdda9aa8"), Value = "Loa Monster Adventurer MAX có âm thanh chất lượng cao, với âm trầm sâu và mạnh mẽ, âm trung rõ ràng và âm cao trong trẻo. Loa có thể tái tạo âm thanh của nhiều thể loại nhạc khác nhau, từ nhạc pop, rock, EDM đến nhạc cổ điển.", Type = ParseGadgetDescriptionType("NormalText"), Index = 8 },
new GadgetDescription { Id = Guid.Parse("f1387dc4-df19-4f7e-8ac2-41511d258f07"), GadgetId = Guid.Parse("0bb7cefd-4058-41f5-b97f-ed41bdda9aa8"), Value = "Loa Bluetooth Monster Adventurer MAX là một chiếc loa di động mạnh mẽ với công suất 60 W, mang đến âm thanh sống động và đầy cảm xúc. Âm thanh 360 độ từ loa giúp các giai điệu được lan tỏa đều khắp không gian. Loa có thể phát âm thanh ở mức âm lượng lớn mà không bị méo tiếng, giúp mình thưởng thức âm nhạc một cách trọn vẹn.", Type = ParseGadgetDescriptionType("NormalText"), Index = 9 },
new GadgetDescription { Id = Guid.Parse("ef80f315-1577-4975-9565-1cbfcf273b7b"), GadgetId = Guid.Parse("f4ddcdf1-890d-47bb-82e1-8cfed15a5bc1"), Value = "Loa Bluetooth Klipsch The Three II có kiểu thiết kế cổ điển, chất lượng âm thanh phát ra mạnh mẽ. Hơn nữa, loa còn hỗ trợ kết nối Bluetooth, giúp người dùng phát nhạc từ xa trên các thiết bị di động một cách nhanh chóng.", Type = ParseGadgetDescriptionType("BoldText"), Index = 0 },
new GadgetDescription { Id = Guid.Parse("d623ea19-17b6-4756-9f62-c228c2fcd641"), GadgetId = Guid.Parse("f4ddcdf1-890d-47bb-82e1-8cfed15a5bc1"), Value = "Thiết kế chỉn chu từ chất liệu cao cấp", Type = ParseGadgetDescriptionType("BoldText"), Index = 1 },
new GadgetDescription { Id = Guid.Parse("4c43dd8e-ba3d-4186-86f2-2e6b20591ebd"), GadgetId = Guid.Parse("f4ddcdf1-890d-47bb-82e1-8cfed15a5bc1"), Value = "Loa được thiết kế dạng hình hộp chữ nhật nằm ngang, có kích thước gọn với khối lượng loa khoảng 4.7 kg, dễ dàng di chuyển và không chiếm nhiều diện tích khi lắp đặt. Thùng loa được làm bằng gỗ MDF bền tốt và phủ veneer giúp cho đường vân gỗ trông tự nhiên, sang trọng hơn. Mặt lưới ê-căng làm bằng vải dù với màu sắc trang nhã.", Type = ParseGadgetDescriptionType("NormalText"), Index = 2 },
new GadgetDescription { Id = Guid.Parse("3c9dfe04-17dc-4f00-bb17-b34a39201e74"), GadgetId = Guid.Parse("f4ddcdf1-890d-47bb-82e1-8cfed15a5bc1"), Value = "Ngoài ra, màng loa được làm từ sợi vải dệt mịn màng và tinh tế, góp phần tạo nên vẻ đẹp cho sản phẩm ở mọi góc nhìn.", Type = ParseGadgetDescriptionType("NormalText"), Index = 3 },
