new GadgetDescription { Id = Guid.Parse("5d7e82d7-d1b3-4e8f-8545-cbf3907c348f"), GadgetId = Guid.Parse("f4ddcdf1-890d-47bb-82e1-8cfed15a5bc1"), Value = "Công suất lên tới 120 W với dải âm rộng, âm thanh rõ ràng", Type = ParseGadgetDescriptionType("BoldText"), Index = 5 },
new GadgetDescription { Id = Guid.Parse("2b9de216-f1fd-494d-89ba-bd6fe3151d69"), GadgetId = Guid.Parse("f4ddcdf1-890d-47bb-82e1-8cfed15a5bc1"), Value = "Loa có tổng công suất 120 W, phù hợp lắp đặt cho diện tích căn phòng từ 10 - 20 m2. Loa được tạo ra từ 1 loa bass và 2 loa treble, mang lại chất lượng âm trầm lắng đối với âm bass và dịu ngọt, không quá chói gắt đối với âm treble, nhờ đó người dùng có thể nghe được chi tiết âm thanh rõ hơn, thưởng thức nhạc trữ tình hay nhạc sôi động hoàn hảo.", Type = ParseGadgetDescriptionType("NormalText"), Index = 6 },
