new GadgetDescription { Id = Guid.Parse("233201f6-272d-43a1-a168-9e33fd4b668e"), GadgetId = Guid.Parse("f4ddcdf1-890d-47bb-82e1-8cfed15a5bc1"), Value = "Loa Klipsch này có khả năng kết nối Bluetooth với khoảng cách hỗ trợ lên đến 10 m, giúp người dùng phát nhạc từ xa một cách thuận tiện trên loa thông qua điện thoại, laptop cũng như một số thiết bị di động tương thích khác.", Type = ParseGadgetDescriptionType("NormalText"), Index = 9 },
new GadgetDescription { Id = Guid.Parse("3b4d4298-a525-4679-aa49-70696ad478cc"), GadgetId = Guid.Parse("f4ddcdf1-890d-47bb-82e1-8cfed15a5bc1"), Value = "Ngoài kết nối được với Bluetooth, loa còn có thể sử dụng một số cổng kết nối vật lý khác để tạo điều kiện cho người dùng ghép nối được với một số thiết bị phát nhạc để tạo nên một thiết bị âm thanh giải trí phù hợp cho gia đình.", Type = ParseGadgetDescriptionType("NormalText"), Index = 10 },
new GadgetDescription { Id = Guid.Parse("13992283-7daa-4c6d-9a37-bf0f1ae931a8"), GadgetId = Guid.Parse("f4ddcdf1-890d-47bb-82e1-8cfed15a5bc1"), Value = "Nhìn chung, loa Bluetooth Klipsch The Three II có khả năng phát ra âm thanh mạnh mẽ và kết nối với một số thiết bị âm thanh khác, để tạo nên dàn âm thanh sống động cho gia đình.", Type = ParseGadgetDescriptionType("NormalText"), Index = 11 },
new GadgetDescription { Id = Guid.Parse("2cd234a3-e76b-4fda-8d8b-65227bac76ad"), GadgetId = Guid.Parse("b391d5a2-ba71-40d4-8ec1-3c7ad7f44c7e"), Value = "Âm nhạc giúp chúng ta thư giãn, giải trí sau những giờ làm việc căng thẳng, hay tạo không khí sôi động cho những bữa tiệc. Nếu bạn đang tìm kiếm một chiếc loa Bluetooth có âm thanh sống động, mạnh mẽ, cùng thiết kế thời thượng thì loa Bluetooth Klipsch GIG XXL là một lựa chọn hoàn hảo dành cho bạn.", Type = ParseGadgetDescriptionType("BoldText"), Index = 0 },
new GadgetDescription { Id = Guid.Parse("711cc888-f4ea-44c9-b0c3-9d1f786da2c5"), GadgetId = Guid.Parse("b391d5a2-ba71-40d4-8ec1-3c7ad7f44c7e"), Value = "Kiểu dáng hiện đại, đèn LED chuyển màu sống động", Type = ParseGadgetDescriptionType("BoldText"), Index = 1 },
new GadgetDescription { Id = Guid.Parse("12738be7-2677-42f6-b61f-e7e3dbd8651d"), GadgetId = Guid.Parse("b391d5a2-ba71-40d4-8ec1-3c7ad7f44c7e"), Value = "Klipsch Sidekicks GIG XXL là một chiếc loa Bluetooth di động được thiết kế dành riêng cho các bữa tiệc sôi động. Loa sở hữu một ngoại hình hiện đại và năng động, với các đường nét bo cong mềm mại, dễ dàng hòa hợp với mọi không gian. Loa còn có một tay cầm, hỗ trợ tốt hơn bạn muốn di chuyển loa đến nhiều nơi khác nhau.", Type = ParseGadgetDescriptionType("NormalText"), Index = 2 },
new GadgetDescription { Id = Guid.Parse("e10c8f2b-14fc-4e09-b629-42b03f439477"), GadgetId = Guid.Parse("b391d5a2-ba71-40d4-8ec1-3c7ad7f44c7e"), Value = "Vỏ của loa được làm từ nhựa cao cấp, có độ bền cao và khả năng chịu lực tốt. Nhờ đó, loa có thể chịu được các tác động từ môi trường bên ngoài, đảm bảo loa luôn bền đẹp theo thời gian. Đồng thời, bề mặt nhám cũng giúp cho loa có khả năng chống trơn trượt tốt, mang lại cảm giác cầm nắm chắc chắn.", Type = ParseGadgetDescriptionType("NormalText"), Index = 3 },
new GadgetDescription { Id = Guid.Parse("52b2583b-5c9a-4339-8afb-5c93556435fc"), GadgetId = Guid.Parse("b391d5a2-ba71-40d4-8ec1-3c7ad7f44c7e"), Value = "https://storage.googleapis.com/fbdemo-f9d5f.appspot.com/Gadgets/4ad7f1a6-cdc8-4b06-b978-daf78a876870.jpg", Type = ParseGadgetDescriptionType("Image"), Index = 4 },
new GadgetDescription { Id = Guid.Parse("da9ea6e7-a1a5-4e36-8058-3e2ecb450bf7"), GadgetId = Guid.Parse("b391d5a2-ba71-40d4-8ec1-3c7ad7f44c7e"), Value = "Mặt trước của Klipsch Sidekicks GIG XXL nổi bật với khung lưới bảo vệ bằng hợp kim sơn tĩnh điện màu đen. Khung lưới này giúp bảo vệ các củ loa bên trong khỏi các tác động bên ngoài, đồng thời mang đến vẻ đẹp sang trọng và hiện đại cho loa. Ở chính giữa khung lưới là logo Klipsch vàng, tạo điểm nhấn cho mặt trước của loa.", Type = ParseGadgetDescriptionType("NormalText"), Index = 5 },
new GadgetDescription { Id = Guid.Parse("8ec77fcd-6fcd-457f-befe-8f4af78fd7fb"), GadgetId = Guid.Parse("b391d5a2-ba71-40d4-8ec1-3c7ad7f44c7e"), Value = "Mặt trên của Klipsch Sidekicks GIG XXL là bảng điều khiển loa được tích hợp cùng các cổng kết nối. Bảng điều khiển này được thiết kế đơn giản, dễ sử dụng, giúp bạn có thể nhanh chóng điều chỉnh âm thanh và kết nối loa với các thiết bị khác.", Type = ParseGadgetDescriptionType("NormalText"), Index = 6 },
new GadgetDescription { Id = Guid.Parse("f6c6802e-4d13-4d7b-a141-c0b1caf35dbc"), GadgetId = Guid.Parse("b391d5a2-ba71-40d4-8ec1-3c7ad7f44c7e"), Value = "Loa Bluetooth Klipsch không chỉ sở hữu thiết kế hiện đại, âm thanh mạnh mẽ mà còn có khả năng thắp sáng các bữa tiệc bởi hệ thống đèn LED RGB lung linh và huyền ảo. Hệ thống đèn này bao gồm hai vòng đèn nhiều màu nhảy theo nhịp điệu của bản nhạc, cộng với đèn nhấp nháy sống động. Bạn có thể chọn chế độ ánh sáng của hệ thống đèn LED RGB thông qua bảng điều khiển trên loa.", Type = ParseGadgetDescriptionType("NormalText"), Index = 7 },
