new GadgetDescription { Id = Guid.Parse("73e56031-539d-4c4e-9691-c5575577ebd6"), GadgetId = Guid.Parse("3c39182f-ae2c-4ded-b5bb-384b51423244"), Value = "Loa Bluetooth Klipsch Promedia Heritage 2.1 Walnut có thiết kế mang đậm chất hoài cổ của dòng loa Hi-end cổ điển Klipsch Heritage lừng danh. Thùng loa được làm từ gỗ MDF cao cấp, phủ lớp sơn màu nâu sang trọng, tinh tế. Ê-căng loa được làm từ vải giúp bảo vệ các driver của loa khỏi bụi bẩn và hư hỏng.", Type = ParseGadgetDescriptionType("NormalText"), Index = 2 },
new GadgetDescription { Id = Guid.Parse("3120111d-5496-4d05-b63b-58150e791c74"), GadgetId = Guid.Parse("3c39182f-ae2c-4ded-b5bb-384b51423244"), Value = "Thiết kế của loa Klipsch mang đến vẻ đẹp sang trọng, tinh tế cho không gian nội thất. Mình đã thử đặt loa ở các vị trí như bàn làm việc, kệ tivi,... ở nhiều không gian như phòng khách, phòng ngủ hay phòng làm việc thì đều rất hài hòa.", Type = ParseGadgetDescriptionType("NormalText"), Index = 3 },
new GadgetDescription { Id = Guid.Parse("d08cd96f-9af2-43e6-a60e-1615b04d9152"), GadgetId = Guid.Parse("3c39182f-ae2c-4ded-b5bb-384b51423244"), Value = "Sản phẩm gồm 3 phần là 1 loa subwoofer và 2 loa vệ tinh. Mặt sau của loa subwoofer là bộ kết nối và điều khiển âm thanh gồm: Cổng nguồn, nút bật/tắt, cổng kết nối với Amply, nguồn vào âm thanh, cổng USB, điều chỉnh âm lượng và điều chỉnh bass.", Type = ParseGadgetDescriptionType("NormalText"), Index = 4 },
new GadgetDescription { Id = Guid.Parse("abade5a1-2c18-4859-8415-decfda26b358"), GadgetId = Guid.Parse("3c39182f-ae2c-4ded-b5bb-384b51423244"), Value = "https://storage.googleapis.com/fbdemo-f9d5f.appspot.com/Gadgets/37891a2f-fc84-473e-9f28-55b8cb7e9e50.jpg", Type = ParseGadgetDescriptionType("Image"), Index = 5 },
new GadgetDescription { Id = Guid.Parse("91f3feb2-6d49-4cfb-a226-18fbbe710031"), GadgetId = Guid.Parse("3c39182f-ae2c-4ded-b5bb-384b51423244"), Value = "Còn ở 2 loa vệ tinh thì chỉ có cổng kết nối với Amply, bước đầu kết nối thiết bị khá dễ dàng. Theo mình thấy thì loa sử dụng khá đơn giản, mình chỉ cần xem qua hướng dẫn sử dụng là đã có thể điều khiển chính xác. Ngoài ra, loa Bluetooth còn kèm thêm một remote để người dùng có thể điều khiển dễ dàng ngay cả khi ở xa.", Type = ParseGadgetDescriptionType("NormalText"), Index = 6 },
new GadgetDescription { Id = Guid.Parse("ba2c171e-cb93-46b6-aa7c-b3b0959f3ee0"), GadgetId = Guid.Parse("3c39182f-ae2c-4ded-b5bb-384b51423244"), Value = "Tính riêng loa Subwoofer sẽ có khối lượng 6.35 kg, không quá nặng so với những sản phẩm cùng dòng khác. Mình có thể di chuyển loa đến vị trí mong muốn mà không cần công cụ và người khác hỗ trợ. Mỗi chiếc loa vệ tinh có khối lượng 0.68 kg có thể dễ dàng nhấc lên bằng một tay.", Type = ParseGadgetDescriptionType("NormalText"), Index = 7 },
new GadgetDescription { Id = Guid.Parse("679d228d-c0f1-4cce-aee9-069ce9da1f9d"), GadgetId = Guid.Parse("3c39182f-ae2c-4ded-b5bb-384b51423244"), Value = "https://storage.googleapis.com/fbdemo-f9d5f.appspot.com/Gadgets/19c4f0d7-a1ab-4b0c-a181-adef0c5db625.jpg", Type = ParseGadgetDescriptionType("Image"), Index = 8 },
new GadgetDescription { Id = Guid.Parse("13904630-5dce-4397-8341-fd6853d4ff42"), GadgetId = Guid.Parse("3c39182f-ae2c-4ded-b5bb-384b51423244"), Value = "Nghe nhạc hay, xem phim đã với âm thanh sống động", Type = ParseGadgetDescriptionType("BoldText"), Index = 9 },
new GadgetDescription { Id = Guid.Parse("ec3f250b-8d0b-49a2-a6c6-762501399946"), GadgetId = Guid.Parse("3c39182f-ae2c-4ded-b5bb-384b51423244"), Value = "Tổng công suất của bộ loa Bluetooth Klipsch này là 220 W nên có thể chơi nhiều thể loại nhạc khác nhau. Mình đã thử loa với một đoạn nhạc Jazz thì cảm nhận được chất âm của loa siêu trầm mạnh mẽ, chắc chắn, mang đến trải nghiệm âm nhạc sôi động, bùng nổ. Âm trầm của loa không bị lấn át các dải âm khác, tạo ra âm thanh cân bằng, hài hòa.", Type = ParseGadgetDescriptionType("NormalText"), Index = 10 },
new GadgetDescription { Id = Guid.Parse("9c0322f5-6ccc-48fd-aca9-3721d8ea002b"), GadgetId = Guid.Parse("3c39182f-ae2c-4ded-b5bb-384b51423244"), Value = "Âm treble của loa được tái tạo tốt, không bị chói gắt. Âm mid của loa cân bằng, hài hòa, mang đến cảm giác ấm áp, tự nhiên. Âm thanh của loa vệ tinh rõ ràng, sắc nét, mang đến cảm giác chân thực cho người nghe. Kết hợp cùng công nghệ Tracktrix Horn cho khả năng phân tán âm thanh trong không gian cực tốt.", Type = ParseGadgetDescriptionType("NormalText"), Index = 11 },
new GadgetDescription { Id = Guid.Parse("339f6cb7-e0a5-4642-9ef0-b048e851bebc"), GadgetId = Guid.Parse("00893c7e-c512-4fb4-9407-5e8ba54c49bf"), Value = "Loa Bluetooth Xiaomi Sound Pocket là sản phẩm mang đến sự tiện lợi và đẳng cấp trong thiết kế lẫn chất lượng âm thanh với thiết kế bỏ túi, cùng âm thanh mạnh mẽ và nhiều tính năng vượt trội, đây là một sự lựa chọn hoàn hảo cho những người yêu thích âm nhạc di động.", Type = ParseGadgetDescriptionType("BoldText"), Index = 0 },
new GadgetDescription { Id = Guid.Parse("f0ddb7a1-e013-4dfc-be3f-f5df06c838fe"), GadgetId = Guid.Parse("00893c7e-c512-4fb4-9407-5e8ba54c49bf"), Value = "https://storage.googleapis.com/fbdemo-f9d5f.appspot.com/Gadgets/a3e72427-5e38-4cd1-a37a-0cc463848899.jpg", Type = ParseGadgetDescriptionType("Image"), Index = 1 },
new GadgetDescription { Id = Guid.Parse("d14b4627-6f42-4319-bff8-3b570f8e1078"), GadgetId = Guid.Parse("00893c7e-c512-4fb4-9407-5e8ba54c49bf"), Value = "Điểm thu hút đầu tiên của Xiaomi Sound Pocket chính là thiết kế nhỏ gọn, chỉ nặng 0.2 kg. Với kích thước này, bạn có thể dễ dàng mang theo loa mọi lúc, mọi nơi. Dù là đi dạo, đi làm hay đi du lịch, Sound Pocket đều trở thành người bạn đồng hành lý tưởng, giúp bạn đắm chìm trong những giai điệu yêu thích bất kể thời điểm nào.", Type = ParseGadgetDescriptionType("NormalText"), Index = 2 },
new GadgetDescription { Id = Guid.Parse("d91123b6-bc60-4e8f-b674-b16b1a24dc76"), GadgetId = Guid.Parse("00893c7e-c512-4fb4-9407-5e8ba54c49bf"), Value = "Loa còn được trang bị dây đeo chống thất lạc làm từ cao su chắc chắn, giúp bạn dễ dàng cầm nắm và mang theo bên mình mà không lo bị rơi mất. Dây đeo này không chỉ là một phụ kiện tiện ích, mà còn tăng thêm phần cá tính cho sản phẩm.", Type = ParseGadgetDescriptionType("NormalText"), Index = 3 },
new GadgetDescription { Id = Guid.Parse("728062bc-8de8-4da9-9fbf-bb5f3a2318d3"), GadgetId = Guid.Parse("00893c7e-c512-4fb4-9407-5e8ba54c49bf"), Value = "Với bề mặt nhẵn bóng và tông màu đen chủ đạo, loa Xiaomi toát lên vẻ đẹp hiện đại và sang trọng. Điểm nhấn cam rực rỡ tạo nên một sự kết hợp màu sắc ấn tượng, giúp loa nổi bật và thu hút ánh nhìn. Loa có thể đặt được cả chiều ngang và chiều dọc, thích nghi linh hoạt với mọi hoàn cảnh sử dụng.", Type = ParseGadgetDescriptionType("NormalText"), Index = 4 },
new GadgetDescription { Id = Guid.Parse("8e7920ca-bdcc-4662-9825-d8350c2edb9f"), GadgetId = Guid.Parse("00893c7e-c512-4fb4-9407-5e8ba54c49bf"), Value = "Với công suất 5W, loa bluetooth mang đến âm thanh rõ ràng, sống động và cân bằng. Vùng lưới lớn trên bề mặt loa giúp tối ưu hoá âm thanh, mang đến chất âm trong trẻo và âm trầm sâu lắng.", Type = ParseGadgetDescriptionType("NormalText"), Index = 5 },
new GadgetDescription { Id = Guid.Parse("0d0b2880-be72-4325-aa98-e8b6bb8d08df"), GadgetId = Guid.Parse("00893c7e-c512-4fb4-9407-5e8ba54c49bf"), Value = "Sound Pocket tự động cân bằng các dải âm thanh khác nhau, điều chỉnh âm lượng dựa trên nhạc đang phát để đảm bảo từng chi tiết âm nhạc được tái hiện chân thực. Ngay cả khi nghe ở mức âm lượng thấp, bạn vẫn có thể thưởng thức từng nốt nhạc một cách rõ ràng và bắt tai.", Type = ParseGadgetDescriptionType("NormalText"), Index = 6 },
new GadgetDescription { Id = Guid.Parse("2f4c7982-b892-4d42-8d81-cd1ba20f2cbf"), GadgetId = Guid.Parse("00893c7e-c512-4fb4-9407-5e8ba54c49bf"), Value = "Với dung lượng pin 1000 mAh, Xiaomi Sound Pocket cho phép bạn thưởng thức âm nhạc liên tục lên đến 10 giờ chỉ sau một lần sạc đầy.", Type = ParseGadgetDescriptionType("NormalText"), Index = 7 },
new GadgetDescription { Id = Guid.Parse("a6110596-5644-4736-866b-02e2655356a3"), GadgetId = Guid.Parse("00893c7e-c512-4fb4-9407-5e8ba54c49bf"), Value = "Sử dụng Bluetooth 5.4, Xiaomi Sound Pocket mang đến khả năng kết nối nhanh chóng và ổn định. Bluetooth 5.4 còn giúp tiết kiệm năng lượng, giảm thiểu tiêu thụ pin, đảm bảo thời gian sử dụng lâu hơn so với các phiên bản Bluetooth trước.", Type = ParseGadgetDescriptionType("NormalText"), Index = 8 },
new GadgetDescription { Id = Guid.Parse("60afa2a8-1341-47a8-929a-ffcd9092efb6"), GadgetId = Guid.Parse("00893c7e-c512-4fb4-9407-5e8ba54c49bf"), Value = "Với chứng nhận IP67, loa Bluetooth này có khả năng chống nước và chống bụi xuất sắc. Bạn có thể mang loa đến bãi biển, hồ bơi hoặc thậm chí là dưới trời mưa mà không lo bị hư hỏng.", Type = ParseGadgetDescriptionType("NormalText"), Index = 9 },
new GadgetDescription { Id = Guid.Parse("9c5cfd1c-f296-4ca2-927d-46d90ed3ab68"), GadgetId = Guid.Parse("00893c7e-c512-4fb4-9407-5e8ba54c49bf"), Value = "Nhờ vào micrô tích hợp, bạn có thể thực hiện cuộc gọi rảnh tay mà không cần phải dừng lại những gì đang làm. Âm thanh trong các cuộc gọi cũng được xử lý sắc nét và rõ ràng, mang đến trải nghiệm giao tiếp không hề thua kém so với khi sử dụng tai nghe chuyên dụng.", Type = ParseGadgetDescriptionType("NormalText"), Index = 10 },
new GadgetDescription { Id = Guid.Parse("118524ac-b0ef-475d-b568-0d1a9da10d81"), GadgetId = Guid.Parse("50818b55-64d9-427f-80e9-787913366a60"), Value = "Mẫu loa Bluetooth Xiaomi Sound Outdoor là một sản phẩm đáng chú ý trong dòng loa di động của Xiaomi, được thiết kế đặc biệt để sử dụng ngoài trời. Với thiết kế nhỏ gọn, chất lượng âm thanh tốt và khả năng chống nước IP67, loa này hứa hẹn đem đến trải nghiệm âm nhạc chất lượng nhất.", Type = ParseGadgetDescriptionType("BoldText"), Index = 0 },
new GadgetDescription { Id = Guid.Parse("57e0c31e-1362-4918-9b2d-5ed7a495adc5"), GadgetId = Guid.Parse("50818b55-64d9-427f-80e9-787913366a60"), Value = "Trải nghiệm âm thanh theo một cách chân thực nhất", Type = ParseGadgetDescriptionType("BoldText"), Index = 1 },
new GadgetDescription { Id = Guid.Parse("e05a6801-0ade-4788-94e5-af0926a1bddb"), GadgetId = Guid.Parse("50818b55-64d9-427f-80e9-787913366a60"), Value = "Mẫu loa Bluetooth này được trang bị bộ tản âm trầm thụ động cỡ lớn ở cả hai bên, loa mang đến âm trầm rõ nét và phong phú hơn, làm cho âm gõ trở nên sống động, tự nhiên. Với công suất siêu cao 30 W, âm thanh của loa trở nên sống động và mạnh mẽ.", Type = ParseGadgetDescriptionType("NormalText"), Index = 2 },
new GadgetDescription { Id = Guid.Parse("c8d91364-b961-46c8-9097-3183fd87e984"), GadgetId = Guid.Parse("50818b55-64d9-427f-80e9-787913366a60"), Value = "https://storage.googleapis.com/fbdemo-f9d5f.appspot.com/Gadgets/1455f47b-1dab-4936-8c21-104d7a725972.jpg", Type = ParseGadgetDescriptionType("Image"), Index = 3 },
new GadgetDescription { Id = Guid.Parse("fb23d8c3-52c1-4c93-a3c0-40ca41bffa4d"), GadgetId = Guid.Parse("50818b55-64d9-427f-80e9-787913366a60"), Value = "Thiết kế vùng lưới lớn giúp loa đem đến chất âm trong và cân bằng hơn. Bất kể đặt loa ở đâu, bạn đều sẽ bị ấn tượng với âm thanh rõ nét và mạnh mẽ, ngay cả khi loa có kích thước nhỏ gọn.", Type = ParseGadgetDescriptionType("NormalText"), Index = 4 },
new GadgetDescription { Id = Guid.Parse("933108a8-0d02-4348-9eaa-49154bc2795b"), GadgetId = Guid.Parse("50818b55-64d9-427f-80e9-787913366a60"), Value = "Phối màu ấn tượng tạo nên vẻ ngoài cuốn hút", Type = ParseGadgetDescriptionType("BoldText"), Index = 5 },
new GadgetDescription { Id = Guid.Parse("00941c54-46cf-42c0-a0e0-79fa196410fa"), GadgetId = Guid.Parse("50818b55-64d9-427f-80e9-787913366a60"), Value = "Loa được thiết kế theo khái niệm \"Sống động\", hòa nhịp cùng cuộc sống hàng ngày của bạn. Với thiết kế thon dài, loa có thể đặt theo cả chiều ngang và chiều dọc, linh hoạt trong mọi hoàn cảnh. Tông màu đen tinh tế kết hợp với chút sắc cam rực rỡ tạo nên sự ấn tượng và độc đáo, thể hiện rõ cá tính của bạn.", Type = ParseGadgetDescriptionType("NormalText"), Index = 6 },
new GadgetDescription { Id = Guid.Parse("c0939b09-874c-4d5e-99a6-2b270ef68678"), GadgetId = Guid.Parse("50818b55-64d9-427f-80e9-787913366a60"), Value = "https://storage.googleapis.com/fbdemo-f9d5f.appspot.com/Gadgets/6c384b69-0a00-46cb-bb82-06b623dcd236.jpg", Type = ParseGadgetDescriptionType("Image"), Index = 7 },
new GadgetDescription { Id = Guid.Parse("d377aeb9-2bae-4324-8356-dce6d9d94c90"), GadgetId = Guid.Parse("50818b55-64d9-427f-80e9-787913366a60"), Value = "Đường cong tinh tế và kiểu dáng nhỏ gọn giúp loa dễ dàng cầm nắm bằng một tay. Với thiết kế gọn nhẹ, bạn có thể dễ dàng mang theo và thưởng thức âm nhạc bất cứ đâu, dù là ở nhà, ngoài trời hay đang di chuyển.", Type = ParseGadgetDescriptionType("NormalText"), Index = 8 },
new GadgetDescription { Id = Guid.Parse("3934eb66-08f1-49ed-b47d-ef9fac3acec4"), GadgetId = Guid.Parse("50818b55-64d9-427f-80e9-787913366a60"), Value = "Kết nối nhanh chóng với công nghệ hiện đại", Type = ParseGadgetDescriptionType("BoldText"), Index = 9 },
new GadgetDescription { Id = Guid.Parse("f5b4cef9-c6cf-4e9c-bdff-c0088a88a22e"), GadgetId = Guid.Parse("50818b55-64d9-427f-80e9-787913366a60"), Value = "Loa Xiaomi được trang bị công nghệ Bluetooth 5.4 cải tiến, mang đến kết nối nhanh chóng và ổn định hơn bao giờ hết. Nhờ vậy, bạn có thể tận hưởng chất lượng âm thanh hoàn hảo và chân thực nhất, mở ra trải nghiệm nghe nhạc chưa từng có.", Type = ParseGadgetDescriptionType("NormalText"), Index = 10 },
new GadgetDescription { Id = Guid.Parse("a9b68dfd-0950-4dda-b948-684404d82f96"), GadgetId = Guid.Parse("43cf2e92-fdf0-4f2d-9048-55fe5b4af032"), Value = "Loa Bluetooth AVA+ Go Z12 sở hữu ngoại hình nhỏ gọn với đèn LED ấn tượng, công nghệ Bluetooth 5.3 hiện đại và khả năng kết nối cùng lúc 2 loa giúp không gian giải trí của bạn thêm phần sống động.", Type = ParseGadgetDescriptionType("BoldText"), Index = 0 },
