new GadgetDescription { Id = Guid.Parse("05025d8c-6f12-41a6-978b-b7146da135db"), GadgetId = Guid.Parse("43cf2e92-fdf0-4f2d-9048-55fe5b4af032"), Value = "Loa Bluetooth được hoàn thiện từ chất liệu nhựa cao cấp, thiết kế nhỏ gọn giúp bạn dễ dàng mang theo mọi lúc mọi nơi.", Type = ParseGadgetDescriptionType("NormalText"), Index = 2 },
new GadgetDescription { Id = Guid.Parse("bbd512f6-fc3e-41fd-8442-f152faf73910"), GadgetId = Guid.Parse("43cf2e92-fdf0-4f2d-9048-55fe5b4af032"), Value = "Loa AVA+ có công suất 5 W, cho âm thanh rõ ràng và sống động trong không gian nhỏ như phòng ngủ, bàn làm việc hay nhu cầu sử dụng cá nhân.", Type = ParseGadgetDescriptionType("NormalText"), Index = 3 },
new GadgetDescription { Id = Guid.Parse("4d956292-f5a1-4542-b184-2a8cc330593b"), GadgetId = Guid.Parse("43cf2e92-fdf0-4f2d-9048-55fe5b4af032"), Value = "Công nghệ Bluetooth 5.3 hiện đại giúp kết nối thêm phần nhanh chóng, giảm thiểu tối đa độ nhiễu, rè.", Type = ParseGadgetDescriptionType("NormalText"), Index = 4 },
new GadgetDescription { Id = Guid.Parse("ab08fa4f-5d63-4c88-8bb6-26e00ef36429"), GadgetId = Guid.Parse("43cf2e92-fdf0-4f2d-9048-55fe5b4af032"), Value = "Cho phép bạn kết nối cùng lúc 2 loa nhằm khuếch đại âm thanh, cho không gian giải trí thêm phần thư giãn.", Type = ParseGadgetDescriptionType("NormalText"), Index = 5 },
new GadgetDescription { Id = Guid.Parse("664ac5a6-396b-40bc-b1e4-4e7fa2a27357"), GadgetId = Guid.Parse("43cf2e92-fdf0-4f2d-9048-55fe5b4af032"), Value = "Hỗ trợ nhiều phím điều khiển giúp bạn dễ dàng thao tác như: Chuyển bài hát, phát/dừng chơi nhạc, tăng/giảm âm lượng, thay đổi chế độ đèn, thay đổi chế độ kết nối.", Type = ParseGadgetDescriptionType("NormalText"), Index = 6 },
new GadgetDescription { Id = Guid.Parse("d1abbb4a-5f7c-4a1a-ba30-ee99f31500ab"), GadgetId = Guid.Parse("1aa44102-5329-486a-8096-d33ee9c96986"), Value = "Loa Bluetooth AVA+ MiniPod Y23 sở hữu thiết kế nhỏ nhắn vừa vặn tay cầm, trang bị âm thanh sống động và mạnh mẽ, hỗ trợ chống nước IPX7, tiện lợi sử dụng cho không gian phòng ngủ hay không gian nhỏ.", Type = ParseGadgetDescriptionType("BoldText"), Index = 0 },
new GadgetDescription { Id = Guid.Parse("13ed8cb1-6987-4bae-9ba0-4614a7e92d7a"), GadgetId = Guid.Parse("1aa44102-5329-486a-8096-d33ee9c96986"), Value = "Ngoại hình nhỏ gọn, màu sắc bắt mắt", Type = ParseGadgetDescriptionType("BoldText"), Index = 1 },
new GadgetDescription { Id = Guid.Parse("bcb2891e-c352-40cb-a114-9eefb6d73357"), GadgetId = Guid.Parse("1aa44102-5329-486a-8096-d33ee9c96986"), Value = "AVA+ MiniPod Y23 có thiết kế nhỏ gọn, hình trụ tròn, chỉ nặng 0.16 kg, nằm gọn trong lòng bàn tay của mình. Loa được làm từ chất liệu nhựa cao cấp có độ bền cao, có khả năng chống va đập tốt. Chiếc loa này được trang bị thêm một sợi dây để mình treo vào balo hay túi xách khi muốn đem theo đến nhiều nơi.", Type = ParseGadgetDescriptionType("NormalText"), Index = 2 },
new GadgetDescription { Id = Guid.Parse("0a5acd24-a51c-4a26-aaab-27fd08c1a65d"), GadgetId = Guid.Parse("1aa44102-5329-486a-8096-d33ee9c96986"), Value = "Khi cầm trên tay mình đã quan sát chiếc loa AVA+ này rất kĩ nhưng vẫn không thấy bất kỳ chi tiết nào bị thừa hay cắt lỗi cả, nên độ hoàn thiện của sản phẩm này rất cao. Loa có màu sắc khá độc đáo khi được sơn theo kiểu gradient hai màu ở trên và ở dưới vô cùng nổi bật.", Type = ParseGadgetDescriptionType("NormalText"), Index = 3 },
new GadgetDescription { Id = Guid.Parse("1ae5104e-7759-431a-9efa-5fc23d3a371e"), GadgetId = Guid.Parse("1aa44102-5329-486a-8096-d33ee9c96986"), Value = "https://storage.googleapis.com/fbdemo-f9d5f.appspot.com/Gadgets/83d69a3f-cf5a-4987-b56e-1b917a44b2b9.jpg", Type = ParseGadgetDescriptionType("Image"), Index = 4 },
new GadgetDescription { Id = Guid.Parse("24d200cb-3cab-44a1-944d-8456f61672ea"), GadgetId = Guid.Parse("1aa44102-5329-486a-8096-d33ee9c96986"), Value = "Màn loa được đặt hướng lên trên, còn mặt dưới của loa là cụm các nút chức năng như: Nút tắt/bật loa, nút dừng/phát nhạc, nút qua bài hát kế tiếp, nút lùi lại bài hát trước đó và nút nhận cuộc gọi. Theo trải nghiệm của mình thì các nút nhấn khá êm, độ nhạy cao và khá dễ sử dụng. Cạnh bên nút tắt/bật loa được trang bị một chiếc đèn giúp người dùng nhận biết trạng thái của loa.", Type = ParseGadgetDescriptionType("NormalText"), Index = 5 },
new GadgetDescription { Id = Guid.Parse("5cb35ee5-6051-41ae-91ad-049af5caa637"), GadgetId = Guid.Parse("1aa44102-5329-486a-8096-d33ee9c96986"), Value = "https://storage.googleapis.com/fbdemo-f9d5f.appspot.com/Gadgets/6a72466c-77bd-4522-926f-77733be9e2cb.jpg", Type = ParseGadgetDescriptionType("Image"), Index = 6 },
new GadgetDescription { Id = Guid.Parse("adb16ca1-da73-49d2-8973-171d01d9ab98"), GadgetId = Guid.Parse("1aa44102-5329-486a-8096-d33ee9c96986"), Value = "Xung quanh cụm nút bấm thì loa Bluetooth được trang bị một viền cao su giúp loa đứng vững trên các mặt phẳng, hạn chế trầy xước phần nút bấm và mặt nhựa bên dưới của loa. Chiếc loa này được trang bị một cổng sạc Type-C, phía trước cổng sạc có một nắp đậy bằng cao su, mỗi khi sạc loa xong mình sẽ đóng nắp lại để tránh bụi bẩn xâm nhập vào.", Type = ParseGadgetDescriptionType("NormalText"), Index = 7 },
new GadgetDescription { Id = Guid.Parse("5d46c6d9-920a-4345-9d52-e32a4eac1647"), GadgetId = Guid.Parse("1aa44102-5329-486a-8096-d33ee9c96986"), Value = "https://storage.googleapis.com/fbdemo-f9d5f.appspot.com/Gadgets/3b4df63a-417a-4667-ac36-ef0db45d25e3.jpg", Type = ParseGadgetDescriptionType("Image"), Index = 8 },
new GadgetDescription { Id = Guid.Parse("ac1a4916-ef60-474b-bebb-e6ac7d05dbaf"), GadgetId = Guid.Parse("1aa44102-5329-486a-8096-d33ee9c96986"), Value = "Bluetooth 5.1 kết nối ổn định và tương thích với nhiều thiết bị", Type = ParseGadgetDescriptionType("BoldText"), Index = 9 },
new GadgetDescription { Id = Guid.Parse("a8d2d212-a6c7-44e5-a1de-eb75907914ef"), GadgetId = Guid.Parse("1aa44102-5329-486a-8096-d33ee9c96986"), Value = "Loa Bluetooth AVA+ MiniPod Y23 được trang bị công nghệ Bluetooth 5.1, mang lại khả năng kết nối ổn định và truyền tải âm thanh chất lượng cao. Phạm vi kết nối của loa lên đến 10m, mình đã thoải mái di chuyển trong phạm vi đó mà không lo bị mất kết nối.", Type = ParseGadgetDescriptionType("NormalText"), Index = 10 },
new GadgetDescription { Id = Guid.Parse("fb64eb9c-b84c-42c3-9f02-534a98d08e6f"), GadgetId = Guid.Parse("1aa44102-5329-486a-8096-d33ee9c96986"), Value = "Quá trình ghép nối diễn ra nhanh chóng và dễ dàng, mình chỉ cần bật Bluetooth trên điện thoại và tìm kiếm tên Bluetooth của loa. Sau khi tìm thấy, mình chỉ cần nhấn vào tên loa để ghép nối. Sau khi kết nối hoàn tất thì mình có thể chọn bài hát trên điện thoại và phát ra loa.", Type = ParseGadgetDescriptionType("NormalText"), Index = 11 },
new GadgetDescription { Id = Guid.Parse("583c66cc-e0c7-4ae7-84fd-6cbbbc6b26c4"), GadgetId = Guid.Parse("6702edca-b046-4f4f-8471-e111bd38cfe3"), Value = "Loa Bluetooth AVA+ FreeGo F13 sở hữu vẻ ngoài đẹp mắt, chất âm vô cùng sống động, kích thước nhỏ dễ dàng mang theo bất cứ đâu, sản phẩm hứa hẹn mở ra những trải nghiệm thú vị cho không gian nhỏ của bạn.", Type = ParseGadgetDescriptionType("BoldText"), Index = 0 },
new GadgetDescription { Id = Guid.Parse("bdccaa67-0086-4cd1-99af-e723f84ea6dc"), GadgetId = Guid.Parse("6702edca-b046-4f4f-8471-e111bd38cfe3"), Value = "Kiểu dáng nhỏ gọn, dãy LED RGB độc đáo", Type = ParseGadgetDescriptionType("BoldText"), Index = 1 },
new GadgetDescription { Id = Guid.Parse("face0a62-804b-497a-b321-5791e8572f80"), GadgetId = Guid.Parse("6702edca-b046-4f4f-8471-e111bd38cfe3"), Value = "Chiếc loa Bluetooth này có hình trụ tròn khá nhỏ gọn, nằm vừa vặn trong lòng bàn tay của mình. Loa được trang bị một dây treo bằng vải dù polyester chống thấm, chịu lực tốt và có độ bền cao. Trong quá trình sử dụng loa mình thường luồn tay vào dây để thêm phần chắc chắn khi cầm hoặc gài vào balo, túi xách mang đi dã ngoại.", Type = ParseGadgetDescriptionType("NormalText"), Index = 2 },
new GadgetDescription { Id = Guid.Parse("1f4a28e4-c881-4398-9924-562e9cd6a1cb"), GadgetId = Guid.Parse("6702edca-b046-4f4f-8471-e111bd38cfe3"), Value = "Logo AVA+ nằm dọc trên thân loa vô cùng nổi bật. Vỏ của loa được chế tác từ nhựa cao cấp, bề mặt hơi nhám, khi tay mình đổ mồ hôi mà cầm loa cũng không bị trơn tuột. Khi loa bị dính bẩn thì mình có thể dùng khăn mềm hoặc khăn ướt để lau, rất tiện lợi trong quá trình sử dụng.", Type = ParseGadgetDescriptionType("NormalText"), Index = 3 },
new GadgetDescription { Id = Guid.Parse("d64f7283-5fb3-48a0-854d-831d023d04dc"), GadgetId = Guid.Parse("6702edca-b046-4f4f-8471-e111bd38cfe3"), Value = "https://storage.googleapis.com/fbdemo-f9d5f.appspot.com/Gadgets/4e10eedc-7b6c-4448-accf-46d2cef8445a.jpg", Type = ParseGadgetDescriptionType("Image"), Index = 4 },
new GadgetDescription { Id = Guid.Parse("a3eebbf0-e7d8-473e-b8ff-5c2e85e59c5b"), GadgetId = Guid.Parse("6702edca-b046-4f4f-8471-e111bd38cfe3"), Value = "Ở phía bên dưới của loa có một nắp cao su, bên trong có các cổng kết gồm: AUX IN, USB A và cổng sạc Type-C. Ở bên dưới cổng sạc được trang bị một đèn thông báo, khi mình cắm sạc cho loa thì đèn sẽ sáng lên, khi loa đầy pin thì đèn sẽ tự tắt.", Type = ParseGadgetDescriptionType("NormalText"), Index = 5 },
new GadgetDescription { Id = Guid.Parse("f9dafc13-a0b0-4f60-aad2-511568db39d3"), GadgetId = Guid.Parse("6702edca-b046-4f4f-8471-e111bd38cfe3"), Value = "https://storage.googleapis.com/fbdemo-f9d5f.appspot.com/Gadgets/996c8658-2f1d-44b8-ab11-d614e6917be1.jpg", Type = ParseGadgetDescriptionType("Image"), Index = 6 },
new GadgetDescription { Id = Guid.Parse("d8c20ebf-762c-42f8-8fd5-e305f8cba77a"), GadgetId = Guid.Parse("6702edca-b046-4f4f-8471-e111bd38cfe3"), Value = "Các nút chức năng được đặt ở dưới đáy của vô cùng gọn gàng. Gồm 5 nút như nút nguồn, tăng/giảm âm lượng, chọn chế độ và bật/tắt đèn. Một điều mà bạn cần lưu ý khi sử dụng các nút tăng/giảm âm lượng là khi nhấn hai lần thì sẽ tăng/giảm âm lượng như bình thường còn khi nhấn một lần thì loa sẽ qua bài hoặc lùi lại bài nhạc trước đó.", Type = ParseGadgetDescriptionType("NormalText"), Index = 7 },
new GadgetDescription { Id = Guid.Parse("7ca9a461-ba2e-470e-8542-25b8c9884093"), GadgetId = Guid.Parse("6702edca-b046-4f4f-8471-e111bd38cfe3"), Value = "https://storage.googleapis.com/fbdemo-f9d5f.appspot.com/Gadgets/a957c131-cac7-4bfc-9d8b-0c6c12f97277.jpg", Type = ParseGadgetDescriptionType("Image"), Index = 8 },
new GadgetDescription { Id = Guid.Parse("9dfc1872-1aba-420d-82f2-a57ae6f875b5"), GadgetId = Guid.Parse("6702edca-b046-4f4f-8471-e111bd38cfe3"), Value = "Khi mà mình đặt loa lên bàn loa đứng khá vững và không bị xê dịch dù bề mặt khá trơn và phẳng do được trang bị đế cao su có độ bám cao. Nên các bạn cứ yên tâm đặt ở nhiều nơi mà không phải lo lắng loa sẽ bị lăn đến chỗ khác hay rơi xuống đất khi có lực tác động nhẹ.", Type = ParseGadgetDescriptionType("NormalText"), Index = 9 },
new GadgetDescription { Id = Guid.Parse("00212ef0-0bf8-4838-8a4d-2fc71476972a"), GadgetId = Guid.Parse("6702edca-b046-4f4f-8471-e111bd38cfe3"), Value = "Ở phía trên là một màng loa chính được tích hợp thêm đèn LED RGB chạy xung quanh phần viền vô cùng nổi bật. Đèn có nhiều chế độ sáng cho mình lựa chọn như sáng bình thường, sáng loang, sáng nhấp nháy và sáng nhấp nháy theo nhạc. Khi đặt trong môi trường tối thì nhìn cực kì đẹp, tạo một không gian thư giãn tuyệt vời cho mọi người dùng.", Type = ParseGadgetDescriptionType("NormalText"), Index = 10 },
new GadgetDescription { Id = Guid.Parse("4aff2215-b167-4019-aaed-6579e3d93846"), GadgetId = Guid.Parse("9fa0f9ca-1d9d-454a-b612-490518aba193"), Value = "Loa Bluetooth Rezo Play Đen với thiết kế tinh tế, âm thanh mạnh mẽ và tính linh hoạt cao, chiếc loa này hứa hẹn sẽ mang lại trải nghiệm âm nhạc tuyệt vời cho bạn ở mọi nơi và mọi lúc.", Type = ParseGadgetDescriptionType("BoldText"), Index = 0 },
new GadgetDescription { Id = Guid.Parse("4ffd0af3-369b-4d20-8ede-999f0f1652c2"), GadgetId = Guid.Parse("9fa0f9ca-1d9d-454a-b612-490518aba193"), Value = "https://storage.googleapis.com/fbdemo-f9d5f.appspot.com/Gadgets/d6e305d1-f7c5-4626-af8c-c23b228dc1cb.jpg", Type = ParseGadgetDescriptionType("Image"), Index = 1 },
new GadgetDescription { Id = Guid.Parse("c80f07cb-7dec-4a6e-b0e7-5eda792759cb"), GadgetId = Guid.Parse("9fa0f9ca-1d9d-454a-b612-490518aba193"), Value = "Loa Bluetooth Rezo Play với vẻ ngoài hiện đại và sang trọng, màu đen bóng kết hợp cùng một chút cam ở vòng loa từ đó tạo nên điểm nhấn tinh tế cho bất kỳ không gian nào", Type = ParseGadgetDescriptionType("NormalText"), Index = 2 },
new GadgetDescription { Id = Guid.Parse("b55f71fe-7d92-426d-ad3e-811ed56246ab"), GadgetId = Guid.Parse("9fa0f9ca-1d9d-454a-b612-490518aba193"), Value = "Với tổng công năng mạnh mẽ lên đến 15 W, loa Bluetooth này hứa hẹn mang đến trải nghiệm âm thanh đỉnh cao cho bạn", Type = ParseGadgetDescriptionType("NormalText"), Index = 3 },
new GadgetDescription { Id = Guid.Parse("7e213c9a-1675-4907-b118-97a369671726"), GadgetId = Guid.Parse("9fa0f9ca-1d9d-454a-b612-490518aba193"), Value = "Loa này được trang bị công nghệ Bluetooth 5.1 tiên tiến, mang lại sự ổn định và hiệu suất cao trong công việc kết nối không dây", Type = ParseGadgetDescriptionType("NormalText"), Index = 4 },
new GadgetDescription { Id = Guid.Parse("4cd8da27-644b-4b32-8d14-502662d62a3f"), GadgetId = Guid.Parse("9fa0f9ca-1d9d-454a-b612-490518aba193"), Value = "Một lần sạc đầy trên loa Bluetooth Rezo có thể hoạt động trong khoảng từ 3 đến 5 tiếng, tùy thuộc vào mức độ âm lượng sử dụng", Type = ParseGadgetDescriptionType("NormalText"), Index = 5 },
new GadgetDescription { Id = Guid.Parse("d38742c4-4771-4826-b5e2-98b474a513a8"), GadgetId = Guid.Parse("9fa0f9ca-1d9d-454a-b612-490518aba193"), Value = "Tính năng kết nối 2 loa cùng lúc, bạn có thể tận hưởng một trải nghiệm âm nhạc đa chiều, mạnh mẽ hơn bao giờ hết", Type = ParseGadgetDescriptionType("NormalText"), Index = 6 },
new GadgetDescription { Id = Guid.Parse("54984aff-7351-4df0-815f-3132e7a0aefa"), GadgetId = Guid.Parse("f9963bd8-2666-42ff-95b2-d2a836d51a48"), Value = "Loa Bluetooth Rezo Play Pro Đen là một trong những mẫu loa Bluetooth đỉnh cao của thương hiệu Rezo vừa được ra mắt tại thị trường Việt Nam. Với thiết kế hiện đại và sang trọng, chiếc loa này không chỉ là một thiết bị nghe nhạc chất lượng mà còn là một phụ kiện trang trí đẹp mắt trong không gian sống của bạn.", Type = ParseGadgetDescriptionType("BoldText"), Index = 0 },
new GadgetDescription { Id = Guid.Parse("8bc79367-ac94-4b50-a9d6-c455cdc649f5"), GadgetId = Guid.Parse("f9963bd8-2666-42ff-95b2-d2a836d51a48"), Value = "https://storage.googleapis.com/fbdemo-f9d5f.appspot.com/Gadgets/ddf456a7-b9ae-4beb-9a14-aa92aeb9e712.jpg", Type = ParseGadgetDescriptionType("Image"), Index = 1 },
new GadgetDescription { Id = Guid.Parse("332a8659-f0df-430e-a745-fa1eec93712f"), GadgetId = Guid.Parse("f9963bd8-2666-42ff-95b2-d2a836d51a48"), Value = "Thiết kế của loa Bluetooth đem đến sự thuận tiện và linh hoạt cho việc di chuyển. Với kích thước nhỏ gọn nhưng không kém phần sang trọng, chiếc loa này dễ dàng đặt ở bất kỳ vị trí nào trong không gian của bạn mà không làm xấu đi vẻ đẹp của nó", Type = ParseGadgetDescriptionType("NormalText"), Index = 2 },
new GadgetDescription { Id = Guid.Parse("e2780aec-210f-4dcf-99b0-da459ef8df04"), GadgetId = Guid.Parse("f9963bd8-2666-42ff-95b2-d2a836d51a48"), Value = "Khả năng kết nối không dây thông qua công nghệ Bluetooth 5.1 trên loa không chỉ nhanh chóng và ổn định mà còn mang lại khoảng cách kết nối lớn hơn giúp bạn dễ dàng thưởng thức âm nhạc mà không cần lo lắng về việc bị gián đoạn", Type = ParseGadgetDescriptionType("NormalText"), Index = 3 },
new GadgetDescription { Id = Guid.Parse("95793080-3c65-4de1-b3e1-7b4c143b5d07"), GadgetId = Guid.Parse("f9963bd8-2666-42ff-95b2-d2a836d51a48"), Value = "Với tổng công suất lên đến 30 W, loa Rezo đem lại sức mạnh âm thanh đáng kinh ngạc trong một thiết bị nhỏ gọn. Điều này cho phép loa tái tạo âm thanh mạnh mẽ và rõ ràng, với độ chi tiết cao và bass sâu hơn", Type = ParseGadgetDescriptionType("NormalText"), Index = 4 },
new GadgetDescription { Id = Guid.Parse("5a89aa10-6e91-4654-8a25-3174d8c30b8d"), GadgetId = Guid.Parse("f9963bd8-2666-42ff-95b2-d2a836d51a48"), Value = "Với thời gian sử dụng từ 4 đến 6 tiếng loa Bluetooth Rezo Play Pro là một người bạn đồng hành chất lượng cho mọi hoạt động của bạn. Dù bạn đang tổ chức một buổi tiệc nhỏ, thư giãn tại công viên, loa có thể hoạt động liên tục trong thời gian dài mà không cần phải lo lắng về việc sạc pin", Type = ParseGadgetDescriptionType("NormalText"), Index = 5 },
new GadgetDescription { Id = Guid.Parse("97b9a1c2-8acd-4b87-8661-38aacb514388"), GadgetId = Guid.Parse("f9963bd8-2666-42ff-95b2-d2a836d51a48"), Value = "Loa Bluetooth có khả năng kết nối 2 loa cùng lúc, từ đó có thể thưởng thức âm nhạc mạnh mẽ hơn bao giờ hết. Đồng thời, tính năng chống nước IPX6 giúp bảo vệ loa trước nước và bụi bẩn, mang lại sự linh hoạt cho mọi hoạt động, từ nhà đến ngoài trời", Type = ParseGadgetDescriptionType("NormalText"), Index = 6 },
new GadgetDescription { Id = Guid.Parse("158806a2-8125-47e1-bc1e-1f0ca5e0be26"), GadgetId = Guid.Parse("c84711f9-e388-4958-a89c-5dc289d2b931"), Value = "Loa Bluetooth Rezo Light Motion K118 sở hữu thiết kế sang trọng, nổi bật với đèn LED đa sắc, âm thanh 360 độ chân thực và sống động, thời lượng pin lớn, mang đến cho bạn những buổi tiệc âm nhạc hoàn hảo", Type = ParseGadgetDescriptionType("BoldText"), Index = 0 },
new GadgetDescription { Id = Guid.Parse("7188e3fa-1b4e-4a83-9486-fd20c6d993b3"), GadgetId = Guid.Parse("c84711f9-e388-4958-a89c-5dc289d2b931"), Value = "Kiểu dáng bắt mắt, kích thước nhỏ gọn", Type = ParseGadgetDescriptionType("BoldText"), Index = 1 },
new GadgetDescription { Id = Guid.Parse("a5dd40ee-0bff-451d-83f1-c924893f7f17"), GadgetId = Guid.Parse("c84711f9-e388-4958-a89c-5dc289d2b931"), Value = "Mình khá thích thiết kế tròn của loa, bởi nó mang đến cảm giác mềm mại, tinh tế. Xung quanh loa được bọc bởi lớp sợi dệt tổng hợp giúp loa trông sang trọng và hiện đại hơn. Đồng thời lớp sợi dệt này còn hạn chế những tác động vật lý ảnh hưởng trực tiếp đến củ loa bên trong", Type = ParseGadgetDescriptionType("NormalText"), Index = 2 },
new GadgetDescription { Id = Guid.Parse("5761240a-c3e9-4f11-8609-c24695ed5667"), GadgetId = Guid.Parse("c84711f9-e388-4958-a89c-5dc289d2b931"), Value = "Chiếc loa Bluetooth này còn được trang bị một sợi dây nho nhỏ để mình có thể treo hay móc vào đâu đó, rất thuận tiện cho việc mang theo khi đi dã ngoại. Bên cạnh dây treo là nơi đặt cổng sạc và bằng điều khiển gồm: Nút tăng âm lượng, giảm âm lượng, bật/tắt loa, nút chọn chế độ, nút bật tắt đèn và một đèn báo pin", Type = ParseGadgetDescriptionType("NormalText"), Index = 3 },
new GadgetDescription { Id = Guid.Parse("d4f8c39f-16b8-470c-aeba-8c6e8fc477ba"), GadgetId = Guid.Parse("c84711f9-e388-4958-a89c-5dc289d2b931"), Value = "https://storage.googleapis.com/fbdemo-f9d5f.appspot.com/Gadgets/ae051173-51f4-4ce2-988e-187efac226d4.jpg", Type = ParseGadgetDescriptionType("Image"), Index = 4 },
new GadgetDescription { Id = Guid.Parse("10795f4e-f990-401f-83b8-2363002c5fea"), GadgetId = Guid.Parse("c84711f9-e388-4958-a89c-5dc289d2b931"), Value = "Một điều khá đặc biệt xảy ra trong quá trình sử dụng là, khi nhấn nút tăng và giảm âm lượng là mình cần phải nhấn giữ trong vài giây để điều chỉnh âm lượng của loa. Nếu mình nhấn nút tăng âm lượng mà buông ra liền thì loa sẽ chuyển bài hát, còn nhấn nút giảm âm lượng thì loa sẽ lùi về bài hát trước đó.", Type = ParseGadgetDescriptionType("NormalText"), Index = 5 },
new GadgetDescription { Id = Guid.Parse("16de5827-fd7a-4f1c-8624-f833d2996f42"), GadgetId = Guid.Parse("c84711f9-e388-4958-a89c-5dc289d2b931"), Value = "Bên cạnh cổng sạc có một nắp silicone khi mình mở ra thì thấy bên trong có các cổng kết nối khác gồm: Cổng USB, AUX IN và thẻ nhớ TF thuận tiện hơn cho quá trình sử dụng.", Type = ParseGadgetDescriptionType("NormalText"), Index = 6 },
new GadgetDescription { Id = Guid.Parse("fb22477c-bed1-4994-886d-16dd5d138bf6"), GadgetId = Guid.Parse("c84711f9-e388-4958-a89c-5dc289d2b931"), Value = "Loa có dạng hình trụ tròn, kích thước khá nhỏ gọn chỉ lớn hơn chiếc iPhone của mình một chút. Khi cầm khá vừa tay, khối lượng của loa cũng chỉ khoảng 0.54 kg khá nhẹ, mình có thể cầm hoàn toàn bằng một tay và di chuyển đến những chỗ khác một cách dễ dàng.", Type = ParseGadgetDescriptionType("NormalText"), Index = 7 },
new GadgetDescription { Id = Guid.Parse("306546de-4c22-47cc-93f9-0646d9f14cbe"), GadgetId = Guid.Parse("c84711f9-e388-4958-a89c-5dc289d2b931"), Value = "https://storage.googleapis.com/fbdemo-f9d5f.appspot.com/Gadgets/8d5f5315-88b5-46a4-9705-d16894151a14.jpg", Type = ParseGadgetDescriptionType("Image"), Index = 8 },
new GadgetDescription { Id = Guid.Parse("3bf6b896-6c8c-4d2d-8f07-8872fce232c5"), GadgetId = Guid.Parse("c84711f9-e388-4958-a89c-5dc289d2b931"), Value = "Loa Rezo có thể đặt đứng hoặc nằm thì theo không gian và sở thích của người dùng. Tuy nhiên, khi đặt đứng thì các bạn cần lưu ý để đầu có các gờ cao su xuống phía dưới để đảm bảo loa được ổn định và phát ra âm thanh tốt nhất. Mình đã thử đặt đầu loa không có gờ cao su xuống mặt bàn và phát nhạc thì loa rung dữ dội do âm thanh bị cản lại, nên các bạn cần đặt đúng chiều loa khi sử dụng.", Type = ParseGadgetDescriptionType("NormalText"), Index = 9 },
new GadgetDescription { Id = Guid.Parse("c8d811f8-8ed3-4aea-bc47-da721f1539c0"), GadgetId = Guid.Parse("c84711f9-e388-4958-a89c-5dc289d2b931"), Value = "https://storage.googleapis.com/fbdemo-f9d5f.appspot.com/Gadgets/33dfbc4a-93c0-4870-9d35-367d1cb3924a.jpg", Type = ParseGadgetDescriptionType("Image"), Index = 10 },
new GadgetDescription { Id = Guid.Parse("51781f15-f6a0-4b96-a48c-1d1734cf5859"), GadgetId = Guid.Parse("2c9eaf6a-8005-41ae-932f-136170e1ced3"), Value = "Loa Bluetooth Rezo Home Series One là sự kết hợp hài hòa giữa âm và sắc với âm thanh 360 độ vô cùng sống động và đèn LED RGB rực rỡ. Loa sở hữu thiết kế đẹp mắt, âm thanh chất lượng, thời lượng pin lâu, mang đến cho người dùng những trải nghiệm âm nhạc tuyệt vời", Type = ParseGadgetDescriptionType("BoldText"), Index = 0 },
new GadgetDescription { Id = Guid.Parse("5bcda3af-0679-4a75-9788-c04e05967fe5"), GadgetId = Guid.Parse("2c9eaf6a-8005-41ae-932f-136170e1ced3"), Value = "Kiểu dáng độc đáo, LED RGB rực rỡ", Type = ParseGadgetDescriptionType("BoldText"), Index = 1 },
new GadgetDescription { Id = Guid.Parse("f7ea23ce-5dd2-4f8a-aa7c-1c87b12dc96c"), GadgetId = Guid.Parse("2c9eaf6a-8005-41ae-932f-136170e1ced3"), Value = "Loa có thiết kế hình vòm rất lạ mắt, kết hợp cùng đèn LED RGB khi nhìn vào mình cứ ngỡ đây là một chiếc đèn ngủ xinh xắn. Khối lượng của loa khoảng 0.751 kg khá nhẹ nên thuận tiện cho việc di chuyển trong quá trình sử dụng. Loa có kích thước vừa phải, không chiếm quá nhiều không gian bài trí nên mình thường để trên bàn làm việc và tủ đầu giường", Type = ParseGadgetDescriptionType("NormalText"), Index = 2 },
new GadgetDescription { Id = Guid.Parse("53df6d93-3a18-4ca0-bd41-712c80e57700"), GadgetId = Guid.Parse("2c9eaf6a-8005-41ae-932f-136170e1ced3"), Value = "Phần nắp phía trên của loa được làm trong suốt ánh đen giúp mình có thể thấy được dải đèn LED hình một như một ngọn núi tuyết vô cùng độc đáo ở bên trong. Bên cạnh đó, đèn LED của loa có 5 chế độ phù hợp với nhiều bầu không khí", Type = ParseGadgetDescriptionType("NormalText"), Index = 3 },
new GadgetDescription { Id = Guid.Parse("67a25e1d-440d-4a51-89ca-284d1ce2d161"), GadgetId = Guid.Parse("2c9eaf6a-8005-41ae-932f-136170e1ced3"), Value = "Để chuyển chế độ mình chỉ cần nhấn nút bật/tắt đèn. Đèn có thể chuyển động theo nhịp nhạc, đổi màu theo thời gian mặc định, một màu chớp tắt hoặc một màu đứng yên thích hợp dùng trong phòng ngủ", Type = ParseGadgetDescriptionType("NormalText"), Index = 4 },
new GadgetDescription { Id = Guid.Parse("6071511b-35d3-4b17-9df3-01ad00479af3"), GadgetId = Guid.Parse("2c9eaf6a-8005-41ae-932f-136170e1ced3"), Value = "https://storage.googleapis.com/fbdemo-f9d5f.appspot.com/Gadgets/e6d5b27c-a365-45b9-af04-90b667996819.jpg", Type = ParseGadgetDescriptionType("Image"), Index = 5 },
new GadgetDescription { Id = Guid.Parse("f7379d12-0961-46f8-bedd-e3ec0325080e"), GadgetId = Guid.Parse("2c9eaf6a-8005-41ae-932f-136170e1ced3"), Value = "Loa được đặt ở phần bên dưới với bề mặt vải mềm mịn, màu đen sang trọng. Bảng điều khiển được đặt ở giữa phần nắp nhựa và loa tạo cảm giác liền mạch cho tổng thể. Từ bên trái qua là nút nguồn, nút bật/tắt đèn, tăng âm lượng, giảm âm lượng và nút chế độ radio. Khi nhấn mình cảm thấy khá êm, bề mặt được làm nhám nên sẽ không để lại quá nhiều vân tay, giữ cho vẻ ngoài luôn sạch đẹp.", Type = ParseGadgetDescriptionType("NormalText"), Index = 6 },
new GadgetDescription { Id = Guid.Parse("4b3f41a4-0d5d-40e2-a5f7-f5b6a8dac9a7"), GadgetId = Guid.Parse("2c9eaf6a-8005-41ae-932f-136170e1ced3"), Value = "Lật ra mặt sau của loa Bluetooth là nơi đặt các cổng kết nối được đặt thẳng hàng trong rất gọn gàng. Các cổng kết nối gồm: Khe cắm thẻ nhớ TF, cổng USB-A, cổng sạc Type-C và AUX IN. Loa được trang bị 3 chân đế cao su ở phía dưới, với thiết kế nhô xa khỏi thân loa giúp loa đứng vững chắc trên nhiều bề mặt khác nhau, kể cả bề mặt gồ ghề. Ngoài ra, thiết kế chân đế nhô cao cũng giúp loa phát ra âm bass trầm ấm hơn.", Type = ParseGadgetDescriptionType("NormalText"), Index = 7 },
new GadgetDescription { Id = Guid.Parse("11fcaf70-c525-42cc-ba99-2dd107ced953"), GadgetId = Guid.Parse("2c9eaf6a-8005-41ae-932f-136170e1ced3"), Value = "https://storage.googleapis.com/fbdemo-f9d5f.appspot.com/Gadgets/4e20a541-1fdd-41cd-856c-c5d1fba55570.jpg", Type = ParseGadgetDescriptionType("Image"), Index = 8 },
new GadgetDescription { Id = Guid.Parse("a93dcc37-b78a-4565-908b-28b2b4a0fa03"), GadgetId = Guid.Parse("2c9eaf6a-8005-41ae-932f-136170e1ced3"), Value = "Âm thanh 360 độ lan tỏa mạnh mẽ", Type = ParseGadgetDescriptionType("BoldText"), Index = 9 },
new GadgetDescription { Id = Guid.Parse("a94369b8-5d11-41e9-981f-4aace17490ef"), GadgetId = Guid.Parse("2c9eaf6a-8005-41ae-932f-136170e1ced3"), Value = "Loa Bluetooth Rezo Home Series One được trang bị củ loa có công suất 10 W với công nghệ âm thanh Hi-Fi, mang đến âm thanh 360 độ sống động, đầy đủ chi tiết. Khi mình sử dụng ở mức âm lượng trung bình thì loa có thể phát ra âm thanh rõ ràng, sắc nét, không bị méo tiếng. Âm trầm của loa được thể hiện khá tốt, có độ sâu và uy lực, âm cao cũng được tái tạo tốt, không bị chói gắt.", Type = ParseGadgetDescriptionType("NormalText"), Index = 10 },
new GadgetDescription { Id = Guid.Parse("10256b40-0594-41d5-9a44-ed0e964b1779"), GadgetId = Guid.Parse("2c9eaf6a-8005-41ae-932f-136170e1ced3"), Value = "https://storage.googleapis.com/fbdemo-f9d5f.appspot.com/Gadgets/e9f9e319-1db0-4c39-a463-470c91265a59.jpg", Type = ParseGadgetDescriptionType("Image"), Index = 11 },
new GadgetDescription { Id = Guid.Parse("5b2a4da8-c6d4-4a6f-9f7f-1942df581831"), GadgetId = Guid.Parse("c40e7e06-d0ee-40ab-953e-08241175db55"), Value = "Trong thế giới công nghệ âm thanh hiện đại, việc tìm kiếm một chiếc loa Bluetooth hoàn hảo để đồng hành trong mọi chuyến đi, mọi bữa tiệc hay đơn giản là những phút giây thư giãn tại nhà là điều không hề dễ dàng. Đáp ứng nhu cầu này, Alpha Works đã giới thiệu mẫu loa Bluetooth AW-Ride, một sản phẩm hội tụ đầy đủ các yếu tố về thiết kế, chất lượng âm thanh và thời gian sử dụng,", Type = ParseGadgetDescriptionType("BoldText"), Index = 0 },
new GadgetDescription { Id = Guid.Parse("b1125903-fc1d-4149-ae19-3ee706910082"), GadgetId = Guid.Parse("c40e7e06-d0ee-40ab-953e-08241175db55"), Value = "Thiết kế hiện đại", Type = ParseGadgetDescriptionType("BoldText"), Index = 1 },
new GadgetDescription { Id = Guid.Parse("90e18d98-b2cf-4cd0-b899-46e82c3ad8a2"), GadgetId = Guid.Parse("c40e7e06-d0ee-40ab-953e-08241175db55"), Value = "Loa Bluetooth được thiết kế nhỏ gọn với kích thước nhỏ gọn và khối lượng chỉ 0.5 kg, giúp bạn dễ dàng mang theo bất cứ đâu. Loa còn được trang bị dây treo tiện lợi, cho phép bạn treo nó lên balo, xe đạp, hoặc bất cứ nơi nào bạn muốn.", Type = ParseGadgetDescriptionType("NormalText"), Index = 2 },
new GadgetDescription { Id = Guid.Parse("0251c4c1-b368-401d-9a14-474e77d1bfda"), GadgetId = Guid.Parse("c40e7e06-d0ee-40ab-953e-08241175db55"), Value = "https://storage.googleapis.com/fbdemo-f9d5f.appspot.com/Gadgets/68e71290-a174-4155-b58b-b415bb2aee3c.jpg", Type = ParseGadgetDescriptionType("Image"), Index = 3 },
new GadgetDescription { Id = Guid.Parse("2359ed94-c673-4398-b883-81c74f648177"), GadgetId = Guid.Parse("c40e7e06-d0ee-40ab-953e-08241175db55"), Value = "Các nút bấm điều khiển được bố trí thông minh ở mặt trên của loa, dễ dàng thao tác ngay cả khi đang di chuyển. Các nút này bao gồm nút nguồn, nút điều chỉnh âm lượng, và nút phát dừng chơi nhạc, tất cả đều có kích thước vừa phải và phản hồi nhanh, mang lại trải nghiệm sử dụng thuận tiện.", Type = ParseGadgetDescriptionType("NormalText"), Index = 4 },
new GadgetDescription { Id = Guid.Parse("619e9d46-13b8-4fb1-9e01-6932b70dd2f1"), GadgetId = Guid.Parse("c40e7e06-d0ee-40ab-953e-08241175db55"), Value = "Kết nối linh hoạt và nhanh chóng với nhiều thiết bị", Type = ParseGadgetDescriptionType("BoldText"), Index = 5 },
new GadgetDescription { Id = Guid.Parse("aa68f535-0bbc-4c97-b006-f5af0b719b43"), GadgetId = Guid.Parse("c40e7e06-d0ee-40ab-953e-08241175db55"), Value = "Loa Alpha Works được thiết kế với khả năng kết nối mạnh mẽ và linh hoạt, đáp ứng mọi nhu cầu nghe nhạc. Sản phẩm được trang bị công nghệ Bluetooth 5.3 tiên tiến, đảm bảo kết nối ổn định và mượt mà với các thiết bị di động trong phạm vi rộng. Dù bạn đang ở nhà, trong văn phòng hay ngoài trời, kết nối Bluetooth 5.3 sẽ giúp bạn truyền tải âm thanh chất lượng cao mà không gặp hiện tượng giật, lag.", Type = ParseGadgetDescriptionType("NormalText"), Index = 6 },
new GadgetDescription { Id = Guid.Parse("364cdd5d-0771-4808-b322-f9e73b9f1f25"), GadgetId = Guid.Parse("c40e7e06-d0ee-40ab-953e-08241175db55"), Value = "https://storage.googleapis.com/fbdemo-f9d5f.appspot.com/Gadgets/45a4127f-61cf-4b4e-8f67-e00501e7b66b.jpg", Type = ParseGadgetDescriptionType("Image"), Index = 7 },
new GadgetDescription { Id = Guid.Parse("69a3ca4c-245a-46bd-848f-3f67a7875148"), GadgetId = Guid.Parse("c40e7e06-d0ee-40ab-953e-08241175db55"), Value = "Ngoài ra, loa Alpha Works AW-Ride còn tích hợp cổng kết nối Aux, cho phép bạn dễ dàng kết nối với các thiết bị hoặc nguồn phát không hỗ trợ Bluetooth. Điều này mang lại sự tiện lợi tối đa, giúp bạn linh hoạt sử dụng loa, từ việc kết nối với máy tính, máy nghe nhạc, đến các thiết bị âm thanh khác.", Type = ParseGadgetDescriptionType("NormalText"), Index = 8 },
new GadgetDescription { Id = Guid.Parse("3f6d4a0b-5b03-4b3a-86e1-f739ff78cbbc"), GadgetId = Guid.Parse("c40e7e06-d0ee-40ab-953e-08241175db55"), Value = "Tận hưởng âm thanh một cách chân thật nhất", Type = ParseGadgetDescriptionType("BoldText"), Index = 9 },
new GadgetDescription { Id = Guid.Parse("d1b30ded-a039-4bf1-9c57-f76c938c4606"), GadgetId = Guid.Parse("c40e7e06-d0ee-40ab-953e-08241175db55"), Value = "Loa được trang bị củ loa full range 2 inch, mang lại công suất 10W (RMS) và có thể đạt đỉnh lên đến 20W (Peak). Điều này giúp loa tái tạo âm thanh một cách sống động và chân thực, với khả năng cân bằng hoàn hảo giữa các dải âm. m bổng được thể hiện chi tiết, trong trẻo, mang lại những nốt cao rõ ràng, trong khi âm trầm sâu lắng, mạnh mẽ, tạo ra những nốt thấp vang dội.", Type = ParseGadgetDescriptionType("NormalText"), Index = 10 },
new GadgetDescription { Id = Guid.Parse("8e45f717-3e59-4963-8bb5-593ec933cd8e"), GadgetId = Guid.Parse("c40e7e06-d0ee-40ab-953e-08241175db55"), Value = "https://storage.googleapis.com/fbdemo-f9d5f.appspot.com/Gadgets/1aa3d759-6e3e-4f7c-b9a5-c5fc2d0b7812.jpg", Type = ParseGadgetDescriptionType("Image"), Index = 11 },
new GadgetDescription { Id = Guid.Parse("51d0237e-88f4-448d-9631-a7fc85e33940"), GadgetId = Guid.Parse("c40e7e06-d0ee-40ab-953e-08241175db55"), Value = "Không chỉ dừng lại ở đó, loa Alpha Works AW-Ride còn tích hợp công nghệ True Wireless Stereo (TWS), cho phép bạn ghép đôi hai loa để tăng cường công suất đầu ra và mở rộng không gian âm nhạc. Với TWS, âm thanh trở nên rộng rãi và phong phú hơn, tạo ra hiệu ứng âm thanh vòm tuyệt vời, lý tưởng cho các bữa tiệc ngoài trời hoặc không gian giải trí gia đình.", Type = ParseGadgetDescriptionType("NormalText"), Index = 12 },
