new GadgetDescription { Id = Guid.Parse("9b092660-d37e-4d0e-9a11-9a9ab64ed55f"), GadgetId = Guid.Parse("a48e46d5-2fb9-42fb-ad5e-aee3754f1e96"), Value = "Có thể thấy rằng, loa vi tính Microlab B26 có kiểu dáng năng động, công suất 4 W, 2.0 kênh, dễ chỉnh âm lượng, sử dụng được với nhiều thiết bị, giá cả phải chăng, là thiết bị âm thanh tuyệt vời cho bạn để tận hưởng âm nhạc yêu thích mỗi ngày.", Type = ParseGadgetDescriptionType("NormalText"), Index = 9 },
new GadgetDescription { Id = Guid.Parse("b7885ac9-06df-418b-aad7-4d9a5258e570"), GadgetId = Guid.Parse("1e609c2e-be50-48cd-ac96-3c89d9806586"), Value = "Loa vi tính Microlab TMN3 4.1 là một lựa chọn tuyệt vời cho những ai đam mê âm nhạc và muốn tận hưởng chất lượng âm thanh sống động ngay tại nhà. Với thiết kế hiện đại và tinh tế, trang bị công nghệ âm thanh tiên tiến, Microlab TMN3 4.1 hứa hẹn mang đến trải nghiệm nghe nhạc, xem phim và chơi game tuyệt vời.", Type = ParseGadgetDescriptionType("BoldText"), Index = 0 },
