new GadgetDescription { Id = Guid.Parse("c58efeed-7723-45a9-893a-5662023a6dcd"), GadgetId = Guid.Parse("1e609c2e-be50-48cd-ac96-3c89d9806586"), Value = "Loa vi tính Microlab TMN3 4.1 sở hữu thiết kế hiện đại và tinh tế, phù hợp với nhiều không gian nội thất khác nhau. Bộ loa gồm một loa sub lớn và bốn loa vệ tinh nhỏ gọn, tất cả đều được chế tác từ chất liệu cao cấp, đảm bảo độ bền và tính thẩm mỹ cao.", Type = ParseGadgetDescriptionType("NormalText"), Index = 2 },
new GadgetDescription { Id = Guid.Parse("25cc32f6-16bf-4071-8cce-eef8258a1235"), GadgetId = Guid.Parse("1e609c2e-be50-48cd-ac96-3c89d9806586"), Value = "https://storage.googleapis.com/fbdemo-f9d5f.appspot.com/Gadgets/86361d82-c4b0-4ff8-96eb-99bdc83de307.jpg", Type = ParseGadgetDescriptionType("Image"), Index = 3 },
new GadgetDescription { Id = Guid.Parse("368190ca-9c4d-4fcf-a3e0-13b5271001f6"), GadgetId = Guid.Parse("1e609c2e-be50-48cd-ac96-3c89d9806586"), Value = "Loa sub có kích thước lớn và mặt lưới chất liệu cao, tạo cảm giác chắc chắn và mạnh mẽ, đồng thời giúp tái tạo âm bass sâu và mạnh mẽ. Các loa vệ tinh có thiết kế nhỏ gọn, dễ dàng bố trí xung quanh phòng, mang lại trải nghiệm âm thanh vòm sống động. Ngoài ra, hệ thống điều khiển được tích hợp trực quan và dễ sử dụng, giúp bạn dễ dàng điều chỉnh âm lượng và các thiết lập âm thanh theo ý muốn.", Type = ParseGadgetDescriptionType("NormalText"), Index = 4 },
new GadgetDescription { Id = Guid.Parse("81188853-c3cd-473d-95ab-f498ed20ad6d"), GadgetId = Guid.Parse("1e609c2e-be50-48cd-ac96-3c89d9806586"), Value = "Thư giãn với chất âm ấn tượng", Type = ParseGadgetDescriptionType("BoldText"), Index = 5 },
new GadgetDescription { Id = Guid.Parse("c8f0b4e4-2dba-4074-9751-92160bd628c2"), GadgetId = Guid.Parse("1e609c2e-be50-48cd-ac96-3c89d9806586"), Value = "Loa Microlab không chỉ nổi bật với thiết kế hiện đại mà còn ấn tượng bởi hiệu suất mạnh mẽ. Tổng công suất đạt 32 W, mang lại âm thanh rõ ràng và sống động. Loa trầm (subwoofer) có công suất 12 W đảm bảo tái tạo âm bass sâu và mạnh mẽ, tạo nền âm thanh vững chắc cho mọi thể loại nhạc.", Type = ParseGadgetDescriptionType("NormalText"), Index = 6 },
new GadgetDescription { Id = Guid.Parse("c60efd8d-d6a7-4e5b-9ed0-66a0c74f14e6"), GadgetId = Guid.Parse("1e609c2e-be50-48cd-ac96-3c89d9806586"), Value = "https://storage.googleapis.com/fbdemo-f9d5f.appspot.com/Gadgets/ac54244b-b834-4294-b541-be262172a659.jpg", Type = ParseGadgetDescriptionType("Image"), Index = 7 },
new GadgetDescription { Id = Guid.Parse("602c14fe-f201-48bc-a90a-ebab1e9949d9"), GadgetId = Guid.Parse("1e609c2e-be50-48cd-ac96-3c89d9806586"), Value = "Các loa vệ tinh có kích thước nhỏ gọn với công suất loa vệ tinh lần lượt là 7 W*2 và 3 W*2, hệ thống này mang lại âm thanh trung thực và chi tiết, phủ sóng âm thanh rộng khắp phòng. Sự kết hợp giữa loa trầm và các loa vệ tinh tạo nên trải nghiệm âm thanh vòm chất lượng, lý tưởng cho việc nghe nhạc, xem phim hay chơi game.", Type = ParseGadgetDescriptionType("NormalText"), Index = 8 },
new GadgetDescription { Id = Guid.Parse("bfe6816e-9c35-45c6-8ed4-a53bfbd3a64c"), GadgetId = Guid.Parse("1e609c2e-be50-48cd-ac96-3c89d9806586"), Value = "Kết nối ổn định không giật lag", Type = ParseGadgetDescriptionType("BoldText"), Index = 9 },
new GadgetDescription { Id = Guid.Parse("ce08a46f-b4ca-40bc-8d80-e85f96dd7cfb"), GadgetId = Guid.Parse("1e609c2e-be50-48cd-ac96-3c89d9806586"), Value = "Loa vi tính không chỉ ấn tượng về thiết kế và hiệu suất âm thanh, mà còn rất linh hoạt với các cổng kết nối tiện lợi. Hệ thống loa này được trang bị hai cổng phát nhạc phổ biến là RCA và AUX 3.5 mm, giúp bạn dễ dàng kết nối với nhiều thiết bị khác nhau như máy tính, điện thoại, máy nghe nhạc và các thiết bị phát âm thanh khác.", Type = ParseGadgetDescriptionType("NormalText"), Index = 10 },
new GadgetDescription { Id = Guid.Parse("a609a30b-7da7-4aff-bc75-b64af72c5b12"), GadgetId = Guid.Parse("1e609c2e-be50-48cd-ac96-3c89d9806586"), Value = "https://storage.googleapis.com/fbdemo-f9d5f.appspot.com/Gadgets/81b18702-a1df-4f60-8b48-6332f6cbae8c.jpg", Type = ParseGadgetDescriptionType("Image"), Index = 11 },
new GadgetDescription { Id = Guid.Parse("a01d2a04-7516-4088-ad98-d92fcc408f08"), GadgetId = Guid.Parse("1e609c2e-be50-48cd-ac96-3c89d9806586"), Value = "Cổng RCA cung cấp kết nối ổn định và chất lượng âm thanh cao, trong khi cổng AUX 3.5 mm mang lại sự tiện lợi và tương thích rộng rãi. Điều này cho phép bạn tận hưởng âm nhạc một cách thuận tiện và nhanh chóng, đồng thời tối ưu hóa trải nghiệm âm thanh trong mọi tình huống.", Type = ParseGadgetDescriptionType("NormalText"), Index = 12 },
new GadgetDescription { Id = Guid.Parse("612e5448-4d5a-4516-b348-c3e3811a461e"), GadgetId = Guid.Parse("1e609c2e-be50-48cd-ac96-3c89d9806586"), Value = "Nhìn chung, loa vi tính Microlab TMN3 4.1 là một sản phẩm loa máy tính có thiết kế hiện đại, chất lượng âm thanh vượt trội so với tầm giá. Sản phẩm này phù hợp với những người yêu thích âm nhạc, xem phim và chơi game với âm lượng mạnh mẽ và rõ nét. Microlab TMN3 4.1 là lựa chọn lý tưởng cho những ai muốn sở hữu một hệ thống âm thanh chất lượng cao cho máy tính của mình với mức giá hợp lý.", Type = ParseGadgetDescriptionType("NormalText"), Index = 13 },
