new GadgetDescription { Id = Guid.Parse("01ae80f8-0f26-4b54-9a21-7e028637b7c7"), GadgetId = Guid.Parse("696991f2-cb00-47fa-8fd2-0a418d95859f"), Value = "Sản phẩm còn sở hữu gam màu vàng thời trang nổi bật, có kiểu dáng hình hộp chữ nhật nhỏ gọn với khối lượng 7 kg. Không những thế, phần quai xách loa được làm bằng da nhám êm thoáng, hỗ trợ tốt cho bạn di chuyển thiết bị dễ dàng và tránh tình trạng đau tay.", Type = ParseGadgetDescriptionType("NormalText"), Index = 3 },
