new GadgetDescription { Id = Guid.Parse("7f7f6e06-8c2c-49a5-84fc-19f006fca17a"), GadgetId = Guid.Parse("e594769e-6e73-41fb-b9b9-841b431f1ee7"), Value = "Sản phẩm giúp bạn có thể nghe tốt mọi thể loại ca nhạc, phim ảnh, âm thanh phát ra hoàn hảo, vừa tinh khiết vừa mạnh mẽ rắn chắc.", Type = ParseGadgetDescriptionType("NormalText"), Index = 8 },
new GadgetDescription { Id = Guid.Parse("57412943-452b-4807-b6ff-8a9d15ae5c4f"), GadgetId = Guid.Parse("e594769e-6e73-41fb-b9b9-841b431f1ee7"), Value = "https://storage.googleapis.com/fbdemo-f9d5f.appspot.com/Gadgets/f5eba861-5255-4ed2-aaca-1f9a9f7c020f.png", Type = ParseGadgetDescriptionType("Image"), Index = 9 },
new GadgetDescription { Id = Guid.Parse("e4d71ed4-5f94-40f5-8ce4-ceb7d9d251eb"), GadgetId = Guid.Parse("e594769e-6e73-41fb-b9b9-841b431f1ee7"), Value = "Công nghệ âm thanh", Type = ParseGadgetDescriptionType("BoldText"), Index = 10 },
new GadgetDescription { Id = Guid.Parse("1fd7f0ed-f2a2-488b-a9c9-121d4fd261a9"), GadgetId = Guid.Parse("e594769e-6e73-41fb-b9b9-841b431f1ee7"), Value = "Tính năng Voice Enhancement tự động tăng âm lượng thoại theo môi trường, với EQ tối ưu âm thanh theo nội dung giúp bạn nghe rõ lời thoại của nhân vật, không bỏ lỡ những chi tiết đặc sắc trong phim.", Type = ParseGadgetDescriptionType("NormalText"), Index = 11 },
new GadgetDescription { Id = Guid.Parse("76a7ed98-4e84-451b-87ee-cbf68868f346"), GadgetId = Guid.Parse("e594769e-6e73-41fb-b9b9-841b431f1ee7"), Value = "Chế độ Surround Sound Expansion cho âm thanh đa chiều lan tỏa, mang đến trải nghiệm nghe chân thực như đang ở trong rạp chiếu phim, người dùng thỏa thích tận hưởng các bộ phim yêu thích.", Type = ParseGadgetDescriptionType("NormalText"), Index = 12 },
new GadgetDescription { Id = Guid.Parse("9ace71b4-4015-45a9-83a1-5768bc4736a7"), GadgetId = Guid.Parse("e594769e-6e73-41fb-b9b9-841b431f1ee7"), Value = "Tính năng Night Mode tự động giảm âm, nén âm trầm giúp bạn thỏa sức xem phim mà không ảnh hưởng đến không gian sinh hoạt, nghỉ ngơi của các thành viên khác trong gia đình.", Type = ParseGadgetDescriptionType("NormalText"), Index = 13 },
new GadgetDescription { Id = Guid.Parse("94980f18-875e-48b5-a26e-8a94db544c6d"), GadgetId = Guid.Parse("8a65df48-78ea-4e38-bd17-3fc62b26731b"), Value = "Loa Vi Tính 2.1 Fenda F670X sở hữu kiểu dáng mạnh mẽ, gam màu sang trọng, âm thanh sống động, hiệu ứng đèn LED RGB đẹp mắt, kết nối không dây nhanh chóng mang đến cho bạn những trải nghiệm tuyệt vời.", Type = ParseGadgetDescriptionType("BoldText"), Index = 0 },
new GadgetDescription { Id = Guid.Parse("6aec8a0d-468c-4f74-84dd-e325d7236e5a"), GadgetId = Guid.Parse("8a65df48-78ea-4e38-bd17-3fc62b26731b"), Value = "Phong cách gaming mạnh mẽ", Type = ParseGadgetDescriptionType("BoldText"), Index = 1 },
new GadgetDescription { Id = Guid.Parse("5a0d8e6d-4f0d-4396-97ff-9f1fd485d900"), GadgetId = Guid.Parse("8a65df48-78ea-4e38-bd17-3fc62b26731b"), Value = "Sau khi “đập hộp” thì mình thấy bên trong có 2 loa vệ tinh và 1 loa Sub. Loa Sub sẽ có kích thước lớn nhất và nặng 3.2 kg, còn mỗi loa vệ tinh chỉ nặng 0.5 kg. Theo cảm nhận của mình thì bộ loa vi tính này không quá nặng, một người có thể nhấc lên và di chuyển dễ dàng, thuận tiện cho việc sắp xếp.", Type = ParseGadgetDescriptionType("NormalText"), Index = 2 },
new GadgetDescription { Id = Guid.Parse("95f1e04f-be35-4440-a17e-1276447999d1"), GadgetId = Guid.Parse("8a65df48-78ea-4e38-bd17-3fc62b26731b"), Value = "Phần vỏ của loa Fenda được làm từ nhựa cao cấp, bề mặt hơi nhám nên khi mình chạm tay vào cũng không để lại dấu vân tay. Khi loa bị bám bụi thì mình chỉ cần dùng khăn lau sơ qua là đã sạch, đẹp như ban đầu. Mặt trước của phần loa Sub có những đường cắt vô cùng cá tính, kết hợp cùng đèn LED RGB tạo nên một ngoại hình nổi bật.", Type = ParseGadgetDescriptionType("NormalText"), Index = 3 },
new GadgetDescription { Id = Guid.Parse("ad693970-00da-49d2-a81a-3b327667e923"), GadgetId = Guid.Parse("8a65df48-78ea-4e38-bd17-3fc62b26731b"), Value = "https://storage.googleapis.com/fbdemo-f9d5f.appspot.com/Gadgets/8cac1b23-d9fd-4585-88d0-9dc3179dfb63.jpg", Type = ParseGadgetDescriptionType("Image"), Index = 4 },
new GadgetDescription { Id = Guid.Parse("f97e4221-1a1d-403f-a25a-eb833866ca4f"), GadgetId = Guid.Parse("8a65df48-78ea-4e38-bd17-3fc62b26731b"), Value = "Mặt trên của loa Sub là nơi bố trí bảng điều khiển, gồm các nút như: Nút nguồn, chọn nguồn phát, bật/tắt đèn, phát/dừng chơi nhạc, lùi bài hát/giảm âm lượng và nút chuyển bài hát/tăng âm lượng. Tuy nút bấm khá nhạy nhưng cảm giác khi mình thao tác các nút này khá là ọp ẹp, do các phím được làm theo dạng tứ giác tự do và nối liền với nhau nên khi bấm mình cảm thấy hơi lệch một chút, cần nhiều thời gian để làm quen hơn.", Type = ParseGadgetDescriptionType("NormalText"), Index = 5 },
new GadgetDescription { Id = Guid.Parse("e6efb560-624b-48d1-8a7d-678dbee1506e"), GadgetId = Guid.Parse("8a65df48-78ea-4e38-bd17-3fc62b26731b"), Value = "Phía cạnh bên loa được trang bị một cổng USB-A. Mặt sau của loa sub là nơi bố trí các cổng kết nối, gồm: Cổng Coaxial, nguồn ra âm thanh, nguồn vào âm thanh, nút bật/tắt loa và một dây nguồn. Cạnh bên cổng kết nối là Bass Reflex giúp cải thiện hiệu suất của loa trầm ở tần số thấp.", Type = ParseGadgetDescriptionType("NormalText"), Index = 6 },
new GadgetDescription { Id = Guid.Parse("d7c3e9ad-e9f1-435d-9de7-b6d4acc402a3"), GadgetId = Guid.Parse("8a65df48-78ea-4e38-bd17-3fc62b26731b"), Value = "https://storage.googleapis.com/fbdemo-f9d5f.appspot.com/Gadgets/e3a80ff0-61fb-450a-9da9-c9ec28af332f.jpg", Type = ParseGadgetDescriptionType("Image"), Index = 7 },
new GadgetDescription { Id = Guid.Parse("d9b73251-de7c-462a-8de8-408f6be70f19"), GadgetId = Guid.Parse("8a65df48-78ea-4e38-bd17-3fc62b26731b"), Value = "Còn củ loa sẽ được bố trí ở mặt bên dưới của loa Sub giúp âm thanh trầm khuếch tán rộng và đều hơn ra môi trường. Tuy nhiên, khi mình đặt chiếc loa này trên bề mặt thảm thì chất lượng âm thanh sẽ bị giảm đáng kể. Vì vậy, khi sử dụng bạn nên đặt loa ở trên bề mặt cứng như gỗ hay gạch men để đảm bảo chất lượng âm thanh.", Type = ParseGadgetDescriptionType("NormalText"), Index = 8 },
new GadgetDescription { Id = Guid.Parse("7430d260-e458-45e6-b2c6-bd6552f854d1"), GadgetId = Guid.Parse("8a65df48-78ea-4e38-bd17-3fc62b26731b"), Value = "Về phần loa vệ tinh thì có kích thước khá nhỏ gọn, các góc được bo cong tinh tế khi mình cầm lên cảm giác khá đầm tay. Mặt trước của loa vệ tinh có một màng loa và Bass Reflex. Còn ở phía sau loa được trang bị một jack RCA riêng chứ không gộp chung như những dòng loa vi tính khác, mình khá thích điểm này bởi sẽ thuận tiện hơn khi bố trí.", Type = ParseGadgetDescriptionType("NormalText"), Index = 9 },
new GadgetDescription { Id = Guid.Parse("341d6315-ef9c-4ad2-ad10-2fd0572f8917"), GadgetId = Guid.Parse("8a65df48-78ea-4e38-bd17-3fc62b26731b"), Value = "https://storage.googleapis.com/fbdemo-f9d5f.appspot.com/Gadgets/6819f3a1-f665-4eb1-a43b-d03c36386987.jpg", Type = ParseGadgetDescriptionType("Image"), Index = 10 },
new GadgetDescription { Id = Guid.Parse("682e5027-d410-4270-be3a-59e9f2f92a7f"), GadgetId = Guid.Parse("8a65df48-78ea-4e38-bd17-3fc62b26731b"), Value = "Âm thanh được tái hiện một cách chân thật, nội lực", Type = ParseGadgetDescriptionType("BoldText"), Index = 11 },
new GadgetDescription { Id = Guid.Parse("fa8db674-410e-4033-a78f-6453e58ae804"), GadgetId = Guid.Parse("8a65df48-78ea-4e38-bd17-3fc62b26731b"), Value = "Loa vi tính 2.1 Fenda F670X sở hữu hệ thống âm thanh 2.1 với loa siêu trầm 40 W và 2 loa vệ tinh 15 W. Tổng công suất của hệ thống là 70W, đủ để đáp ứng nhu cầu nghe nhạc, xem phim và chơi game trong không gian vừa và lớn.", Type = ParseGadgetDescriptionType("NormalText"), Index = 12 },
new GadgetDescription { Id = Guid.Parse("1bb85344-0597-4252-9d91-2574f2448590"), GadgetId = Guid.Parse("a1e9eba8-c6e3-4c91-9f2f-55d995d38fe2"), Value = "Loa kéo karaoke Mobell MK-7080 phát ra âm thanh mạnh mẽ, rõ ràng và có thể chơi tốt mọi loại nhạc. Hơn nữa, thời lượng sử dụng loa có thể đạt đến 5 tiếng và hỗ trợ điều khiển từ xa thuận tiện.", Type = ParseGadgetDescriptionType("BoldText"), Index = 0 },
new GadgetDescription { Id = Guid.Parse("8d21a47e-0ba8-420b-a05d-8352a1013db0"), GadgetId = Guid.Parse("a1e9eba8-c6e3-4c91-9f2f-55d995d38fe2"), Value = "Thiết kế", Type = ParseGadgetDescriptionType("BoldText"), Index = 1 },
new GadgetDescription { Id = Guid.Parse("397bbd8f-7789-474f-8259-50e807641467"), GadgetId = Guid.Parse("a1e9eba8-c6e3-4c91-9f2f-55d995d38fe2"), Value = "Mobell MK-7080 được thiết kế hình hộp chữ nhật dạng đứng với khối lượng khoảng 32 kg, thuận tiện cho người sử dụng khi di chuyển nhờ trang bị 4 bánh xe phía dưới và 2 tay cầm 2 bên thùng loa.", Type = ParseGadgetDescriptionType("NormalText"), Index = 2 },
new GadgetDescription { Id = Guid.Parse("882a98cf-8228-4e49-aa4a-d293efc0aedd"), GadgetId = Guid.Parse("a1e9eba8-c6e3-4c91-9f2f-55d995d38fe2"), Value = "Vỏ thùng loa được làm bằng gỗ sơn gai, có phủ lớp sơn màu đen chống trầy xước và góp phần sang trọng cho sản phẩm. Hơn nữa, phần màn loa còn được khắc thêm họa tiết bắt mắt, làm tăng thêm phần tinh tế cho sản phẩm khi đặt trong phòng khách, sảnh tiệc, sảnh văn phòng công ty,…", Type = ParseGadgetDescriptionType("NormalText"), Index = 3 },
