new GadgetDescription { Id = Guid.Parse("0e24dd3f-2717-4be9-aa4d-c8378ba3fbe1"), GadgetId = Guid.Parse("a1e9eba8-c6e3-4c91-9f2f-55d995d38fe2"), Value = "Sở hữu công suất đến 1000W, loa kéo Mobell có thể phát ra âm thanh mạnh mẽ và chắc chắn.", Type = ParseGadgetDescriptionType("NormalText"), Index = 6 },
new GadgetDescription { Id = Guid.Parse("efa0928d-13fa-4434-8dfa-febc725ef15e"), GadgetId = Guid.Parse("a1e9eba8-c6e3-4c91-9f2f-55d995d38fe2"), Value = "Không những vậy, chiếc loa kéo này còn có khả năng phát ra âm tiếng rõ ràng nhờ 3 đường tiếng từ hệ thống 3 loa bên trong - gồm có 1 loa Treble đường kính khoảng 8 cm, 1 loa Mid đường kính 16.6 cm và 1 loa Bass đường kính 38.8 cm.", Type = ParseGadgetDescriptionType("NormalText"), Index = 7 },
new GadgetDescription { Id = Guid.Parse("c066094e-615f-4272-8f62-b1b04de4aca9"), GadgetId = Guid.Parse("a1e9eba8-c6e3-4c91-9f2f-55d995d38fe2"), Value = "Nhờ trang bị công suất 1000W mạnh mẽ, mẫu loa Karaoke Mobell MK-7080 phù hợp cho diện tích căn phòng rộng lớn, trên 50m2 hoặc các bữa tiệc ngoài trời mà vẫn đảm bảo được chất lượng âm thanh phát ra chân thực, vang dội.", Type = ParseGadgetDescriptionType("NormalText"), Index = 8 },
