new GadgetDescription { Id = Guid.Parse("eaa345c2-77d6-4bc7-9317-19f4324ac6fb"), GadgetId = Guid.Parse("a1e9eba8-c6e3-4c91-9f2f-55d995d38fe2"), Value = "Thời gian sử dụng", Type = ParseGadgetDescriptionType("BoldText"), Index = 10 },
new GadgetDescription { Id = Guid.Parse("5c3491f0-383d-40da-85e1-8e6674a42851"), GadgetId = Guid.Parse("a1e9eba8-c6e3-4c91-9f2f-55d995d38fe2"), Value = "Chiếc loa hát karaoke này có thể đáp ứng thời lượng sử dụng từ 3 - 5 tiếng với thời gian sạc thiết bị khoảng 6 - 8 tiếng. Bên cạnh đó, bạn có thể vừa sử dụng loa vừa sạc để không làm gián đoạn cuộc vui trong bữa tiệc.", Type = ParseGadgetDescriptionType("NormalText"), Index = 11 },
new GadgetDescription { Id = Guid.Parse("4f6f52ba-ec85-4bb0-92c2-18fc3369fb00"), GadgetId = Guid.Parse("a02886a8-f16b-4dd0-a645-41cd52a2fefb"), Value = "Loa kéo karaoke Mobell MK-2120C có kiểu thiết kế nhỏ gọn với tay kéo thu ngắn dài tùy chỉnh, thuận tiện cho việc di chuyển. Hơn nữa, loa kéo phát ra âm thanh mạnh mẽ với đường truyền tín hiệu bluetooth ổn định và trang bị remote từ xa thuận tiện cho việc sử dụng.", Type = ParseGadgetDescriptionType("BoldText"), Index = 0 },
