new GadgetDescription { Id = Guid.Parse("5c3d1a2f-10ca-4932-bc3d-a7e4f9791259"), GadgetId = Guid.Parse("a02886a8-f16b-4dd0-a645-41cd52a2fefb"), Value = "Mobell MK-2120C có kiểu thiết kế hình hộp chữ nhật đứng, nhỏ gọn với khối lượng 5.3 kg, giống chiếc vali thu nhỏ với 2 bánh xe và được trang bị tay cầm, tay kéo để thuận tiện cho việc di chuyển.", Type = ParseGadgetDescriptionType("NormalText"), Index = 2 },
new GadgetDescription { Id = Guid.Parse("434317c5-4125-40b4-aa70-157551ceeba6"), GadgetId = Guid.Parse("a02886a8-f16b-4dd0-a645-41cd52a2fefb"), Value = "Vỏ thùng loa kéo được làm bằng gỗ, thể hiện sắc nâu và đường vân vốn có của gỗ, nhờ đó thể hiện được sự tinh tế và thanh lịch cho sản phẩm.", Type = ParseGadgetDescriptionType("NormalText"), Index = 3 },
