new GadgetDescription { Id = Guid.Parse("ef8ab7af-0280-4672-a2ef-7f65517f3b4a"), GadgetId = Guid.Parse("a02886a8-f16b-4dd0-a645-41cd52a2fefb"), Value = "Loa Karaoke Mobell MK-2120C có khả năng phát ra 2 đường tiếng từ hệ thống loa bên trong gồm có 1 loa Bass với đường kính khoảng 20 cm và 1 loa Treble với đường kính 8 cm, nhờ đó mang lại âm Bass sâu chắc, giúp người dùng đắm chìm trong nhiều giai điệu của các thể loại bài hát khác nhau.", Type = ParseGadgetDescriptionType("NormalText"), Index = 6 },
