new GadgetDescription { Id = Guid.Parse("edeaf1cb-57c3-41eb-acc6-e8a4f67bbaeb"), GadgetId = Guid.Parse("45a8ab8a-5662-444d-a275-8f87fd9869c3"), Value = "Tai nghe Bluetooth AirPods Pro (2nd Gen) được Apple trình làng vào tháng 09/2023 với nhiều sự mong đợi, Apple vẫn giữ nguyên thiết kế AirPods Pro Gen 2 giống với phiên bản tiền nhiệm của mình như: kích thước nhỏ gọn, đường bo góc tinh tế, gam màu trắng sang trọng.", Type = ParseGadgetDescriptionType("NormalText"), Index = 2 },
new GadgetDescription { Id = Guid.Parse("d4f4c478-9b4a-4a6a-b6d3-1ba20a030c74"), GadgetId = Guid.Parse("45a8ab8a-5662-444d-a275-8f87fd9869c3"), Value = "Tuy nhiên trong lần ra mắt này, Apple đã thay mới cổng sạc cho các thiết bị của mình, tương tự như cổng sạc trên các mẫu iPhone 15 series, AirPods Pro Gen 2 được tích hợp cổng sạc Type-C nhằm tối ưu phụ kiện sạc giữa các thiết bị. Khi hết pin bạn có thể sạc lại hộp sạc bằng bộ sạc Apple Watch hay MagSafe. Ngoài ra bạn còn có thể sử dụng đầu nối Type-C hoặc bộ sạc chuẩn Qi để nạp lại pin cho chiếc AirPods của mình.", Type = ParseGadgetDescriptionType("NormalText"), Index = 3 },
