new GadgetDescription { Id = Guid.Parse("bdd08d05-7d67-45d7-a62e-d098cafd7d42"), GadgetId = Guid.Parse("d38ea524-ef07-43e3-b040-1af24b26acb6"), Value = "Thiết kế tai nghe nhỏ gọn với phần thân tai nghe ngắn hơn AirPods Gen 2, gam màu trắng sang trọng vốn quen thuộc trên các dòng tai nghe Apple, đường bo góc tinh tế, giúp cầm nắm vừa vặn, êm tay, đồng thời dễ dàng bỏ vào túi xách, balo mang theo ra ngoài mọi lúc, mọi nơi.", Type = ParseGadgetDescriptionType("NormalText"), Index = 2 },
new GadgetDescription { Id = Guid.Parse("b8810137-1f5e-42a7-be76-6203a45b929c"), GadgetId = Guid.Parse("d38ea524-ef07-43e3-b040-1af24b26acb6"), Value = "AirPods 3 có khối lượng khá nhẹ chỉ 4.28 g cho mỗi bên, kết hợp đệm tai được làm bằng silicone mềm mại, cùng dạng tai nghe Earbuds cho cảm giác thoải mái khi đeo.", Type = ParseGadgetDescriptionType("NormalText"), Index = 3 },
new GadgetDescription { Id = Guid.Parse("ea8f3444-ac3e-4e4d-8338-101f4b9f4af0"), GadgetId = Guid.Parse("d38ea524-ef07-43e3-b040-1af24b26acb6"), Value = "https://storage.googleapis.com/fbdemo-f9d5f.appspot.com/Gadgets/9bb3e5b5-9ad3-4969-a025-6a20f08c85cf.jpg ", Type = ParseGadgetDescriptionType("Image"), Index = 4 },
new GadgetDescription { Id = Guid.Parse("5065cda1-2c3e-4446-aae4-233649f49b8a"), GadgetId = Guid.Parse("d38ea524-ef07-43e3-b040-1af24b26acb6"), Value = "Hộp sạc AirPods 3 trang bị đèn báo pin nằm ở mặt trước của hộp sạc bao gồm các màu xanh lá, trắng, vàng tương đương tai nghe sạc đầy pin, tai nghe sẵn sàng kết nối với thiết bị, tai nghe chưa được sạc đầy pin, giúp người dùng nhận biết tình trạng pin một cách dễ dàng. Tai nghe hỗ trợ sạc Lightning tương thích với cáp sạc của các sản phẩm của Apple.", Type = ParseGadgetDescriptionType("NormalText"), Index = 5 },
new GadgetDescription { Id = Guid.Parse("d9699a99-0d67-4aac-b0ce-5346761a93ea"), GadgetId = Guid.Parse("d38ea524-ef07-43e3-b040-1af24b26acb6"), Value = "Chip Apple H1 thông minh cùng nhiều công nghệ tiên tiến", Type = ParseGadgetDescriptionType("BoldText"), Index = 6 },
new GadgetDescription { Id = Guid.Parse("1e7fa3da-0c8e-43cf-9d1e-37e3ddc0fccf"), GadgetId = Guid.Parse("d38ea524-ef07-43e3-b040-1af24b26acb6"), Value = "AirPods 3 được trang bị nhiều công nghệ âm thanh tiên tiến độc quyền của hãng. Một trong những công nghệ đó là Adaptive EQ, cho phép tai nghe phân tích âm thanh trong tai của người dùng và điều chỉnh độ cao và trầm của âm thanh phát ra cho phù hợp. Nhờ vậy, người dùng có thể nghe được âm thanh rõ ràng, chi tiết và cân bằng.", Type = ParseGadgetDescriptionType("NormalText"), Index = 7 },
new GadgetDescription { Id = Guid.Parse("c18f63bb-d8d1-4458-821a-fadaee20f57c"), GadgetId = Guid.Parse("d38ea524-ef07-43e3-b040-1af24b26acb6"), Value = "Trang bị chip Apple H1 thông minh, đóng vai trò điều khiển các hoạt động của tai nghe, như kết nối Bluetooth, khởi chạy Siri bằng giọng nói, xử lý thông tin cảm biến,... và hỗ trợ các chế độ âm thanh khác nhau. Chip Apple H1 cũng giúp giảm độ trễ của âm thanh hiệu quả hơn so với thế hệ trước.", Type = ParseGadgetDescriptionType("NormalText"), Index = 8 },
new GadgetDescription { Id = Guid.Parse("206a0eb0-62b8-4a05-88f1-082c2bbc5451"), GadgetId = Guid.Parse("d38ea524-ef07-43e3-b040-1af24b26acb6"), Value = "Tai nghe tích hợp Âm thanh không gian (Spatial Audio), cho phép người dùng nghe nhạc với âm thanh bao quanh họ, giống như đang ở trong rạp hát. Âm thanh sẽ thay đổi theo hướng và vị trí của đầu người dùng, đem đến trải nghiệm siêu chân thực. Đồng thời AirPods 3 còn được trang bị micro, hỗ trợ người dùng facetime, nghe gọi với người thân trơn tru và rõ ràng.", Type = ParseGadgetDescriptionType("NormalText"), Index = 9 },
new GadgetDescription { Id = Guid.Parse("08201785-b839-477d-ae00-df13d42a4acc"), GadgetId = Guid.Parse("d38ea524-ef07-43e3-b040-1af24b26acb6"), Value = "https://storage.googleapis.com/fbdemo-f9d5f.appspot.com/Gadgets/b14097f6-2bac-4cc9-8100-8074d5404fa3.jpg ", Type = ParseGadgetDescriptionType("Image"), Index = 10 },
new GadgetDescription { Id = Guid.Parse("de8583c6-7358-402c-aa2a-bc90f14bfea7"), GadgetId = Guid.Parse("d38ea524-ef07-43e3-b040-1af24b26acb6"), Value = "Điều khiển dễ dàng chỉ với thao tác chạm", Type = ParseGadgetDescriptionType("BoldText"), Index = 11 },
new GadgetDescription { Id = Guid.Parse("00fa6a5d-b796-4506-8b3c-48d3ceb6f4ea"), GadgetId = Guid.Parse("d38ea524-ef07-43e3-b040-1af24b26acb6"), Value = "Người dùng có thể dễ dàng điều khiển tai nghe Apple này thông qua các thao tác vuốt, chạm trên mỗi bên tai nghe với các chức năng sau: Nhận/ngắt cuộc gọi, Phát/dừng chơi nhạc, Chuyển bài hát, Bật trợ lí ảo.", Type = ParseGadgetDescriptionType("NormalText"), Index = 12 },
new GadgetDescription { Id = Guid.Parse("e6b6e933-a8f0-4fe3-97e1-09fa490c11f4"), GadgetId = Guid.Parse("d38ea524-ef07-43e3-b040-1af24b26acb6"), Value = "Dung lượng pin lớn, thoải mái sử dụng", Type = ParseGadgetDescriptionType("BoldText"), Index = 13 },
