new GadgetDescription { Id = Guid.Parse("084378c5-b6ac-437a-8916-d7ac5ad31197"), GadgetId = Guid.Parse("d2c78828-db52-427a-b74f-1716bc070685"), Value = "Bạn có thể thoải mái sử dụng tai nghe mà không phải lo lắng về việc bị gián đoạn do hết pin nhờ thời lượng dùng liên tục lên tới 7 giờ, sạc đầy trong 1.5 giờ bằng cách đặt tai nghe trở lại trong hộp sạc, dùng tai nghe kết hợp cùng hộp sạc cho tổng thời gian lên đến 20 giờ.", Type = ParseGadgetDescriptionType("NormalText"), Index = 5 },
new GadgetDescription { Id = Guid.Parse("c550486e-0b66-4e53-8a5e-fc86fc191627"), GadgetId = Guid.Parse("0c5eee1f-f301-41fd-992f-6fe85627e451"), Value = "Xiaomi vừa trình làng mẫu tai nghe True Wireless mới nhất của mình mang tên Xiaomi Redmi Buds 5 Pro. Mang nhiều công nghệ đáng chú ý như giảm tiếng ồn lên đến 52 dB và chặn 99.5% tiếng ồn xung quanh, tích hợp thuật toán AI, sạc siêu nhanh, đi cùng thời lượng pin ấn tượng,... hứa hẹn đáp ứng tối ưu mọi nhu cầu sử dụng của người dùng.", Type = ParseGadgetDescriptionType("BoldText"), Index = 0 },
new GadgetDescription { Id = Guid.Parse("b9c74606-39dc-4220-aa9e-463c1facf2c9"), GadgetId = Guid.Parse("0c5eee1f-f301-41fd-992f-6fe85627e451"), Value = "Xiaomi Redmi Buds 5 Pro có diện mạo nhỏ gọn với thiết kế hộp sạc hình chữ nhật bo cong mềm mại, dễ dàng cầm gọn trong lòng bàn tay hay bỏ vào túi quần.", Type = ParseGadgetDescriptionType("NormalText"), Index = 1 },
new GadgetDescription { Id = Guid.Parse("4e18629e-6c0d-4f7f-a708-8ce087e54ac4"), GadgetId = Guid.Parse("0c5eee1f-f301-41fd-992f-6fe85627e451"), Value = "Tai nghe Xiaomi sở hữu thiết kế củ loa kép đồng trục, với loa trầm 11 mm có màng ngăn bằng titan và loa tweeter gốm áp điện 10 mm, đảm bảo hiệu suất cân bằng với âm cao mượt mà, âm trung phong phú và âm trầm chắc chắn, cho phép bạn trải nghiệm âm thanh Hi-Fi bất kỳ lúc nào.", Type = ParseGadgetDescriptionType("NormalText"), Index = 2 },
