new GadgetDescription { Id = Guid.Parse("fc1489d8-d792-4057-9c6a-a7a53bc91f64"), GadgetId = Guid.Parse("0c5eee1f-f301-41fd-992f-6fe85627e451"), Value = "Mang một thời lượng pin ấn tượng với 10 giờ phát nhạc liên tục và tổng thời lượng lên đến 38 giờ khi kết hợp với hộp sạc, bạn có thể thoải mái sử dụng tai nghe cả ngày mà không lo hết pin giữa chừng. Bên cạnh đó, tai nghe cũng sở hữu khả năng sạc siêu nhanh chỉ với 5 phút sạc cung cấp 2 giờ nghe nhạc.", Type = ParseGadgetDescriptionType("NormalText"), Index = 10 },
new GadgetDescription { Id = Guid.Parse("3afbeb63-cacb-4aec-82c7-47352d4e7902"), GadgetId = Guid.Parse("685405fc-dc8b-448f-a529-d6629867fd8d"), Value = "Tai nghe Bluetooth TWS Xiaomi Redmi Buds 6 Lite một sản phẩm hoàn hảo mang đến cho người dùng trải nghiệm âm thanh tuyệt vời với mức giá phải chăng. Với thiết kế nhỏ gọn, dễ dàng sử dụng, và kết nối Bluetooth ổn định, Redmi Buds 6 Lite phù hợp cho mọi nhu cầu từ nghe nhạc, gọi điện đến luyện tập thể thao.", Type = ParseGadgetDescriptionType("BoldText"), Index = 0 },
