new GadgetDescription { Id = Guid.Parse("1494dfa1-f3cc-4451-a0d4-c0ba955fdb52"), GadgetId = Guid.Parse("685405fc-dc8b-448f-a529-d6629867fd8d"), Value = "Đầu tiên không thể không nhắc đến công nghệ Hybrid ANC được trang bị trên tai nghe Xiaomi, mang đến khả năng giảm tiếng ồn thông minh với mức độ khử tiếng ồn chủ động lên đến 40dB. Không những thế tai nghe còn hỗ trợ khử tiếng ồn tần số rộng lên đến 2kHz, giúp loại bỏ hiệu quả các tạp âm trong nhiều môi trường khác nhau.", Type = ParseGadgetDescriptionType("NormalText"), Index = 2 },
