new GadgetDescription { Id = Guid.Parse("a7be40dd-9625-4127-92ef-6debdc745bff"), GadgetId = Guid.Parse("685405fc-dc8b-448f-a529-d6629867fd8d"), Value = "Với hệ thống micrô kép tích hợp công nghệ giảm tiếng ồn AI, tai nghe có thể chống lại tiếng ồn do gió ở vận tốc 6 m/giây, đảm bảo cuộc gọi luôn rõ ràng và mạch lạc. Bên cạnh đó, ống tai nghe Xiaomi được thiết kế độc đáo cùng với thuật toán khử tiếng ồn do Xiaomi phát triển, mang lại cảm giác tự nhiên như đang trò chuyện trực tiếp với người đối diện.", Type = ParseGadgetDescriptionType("NormalText"), Index = 4 },
new GadgetDescription { Id = Guid.Parse("8c6e0e2a-b1b6-4659-b12c-40f49945ca0f"), GadgetId = Guid.Parse("685405fc-dc8b-448f-a529-d6629867fd8d"), Value = "Âm thanh chất lượng đem đến trải nghiệm âm nhạc cuốn hút", Type = ParseGadgetDescriptionType("BoldText"), Index = 5 },
new GadgetDescription { Id = Guid.Parse("7c256328-f923-4668-bc1d-d748e901a19a"), GadgetId = Guid.Parse("685405fc-dc8b-448f-a529-d6629867fd8d"), Value = "Mẫu tai nghe Bluetooth mang đến trải nghiệm âm thanh ấn tượng với âm bass sâu, âm trung rõ ràng và âm cao sắc nét, tạo nên không gian âm nhạc sống động và chi tiết. Tai nghe được tinh chỉnh để cân bằng các dải âm, giúp bạn thưởng thức âm nhạc với chất lượng cao, từ những giai điệu nhẹ nhàng đến các bản nhạc sôi động.", Type = ParseGadgetDescriptionType("NormalText"), Index = 6 },
