new GadgetDescription { Id = Guid.Parse("f2bf46d0-d7a3-4c61-b805-34b935ae62ec"), GadgetId = Guid.Parse("685405fc-dc8b-448f-a529-d6629867fd8d"), Value = "Thiết kế trẻ trung cùng màu sắc thanh lịch", Type = ParseGadgetDescriptionType("BoldText"), Index = 9 },
new GadgetDescription { Id = Guid.Parse("a3874bd4-f871-4e9f-ac9a-03a7271d469c"), GadgetId = Guid.Parse("685405fc-dc8b-448f-a529-d6629867fd8d"), Value = "Buds 6 Lite sở hữu thiết kế nhỏ gọn và hiện đại, dễ dàng mang theo và sử dụng trong nhiều tình huống khác nhau. Với khối lượng nhẹ và kiểu dáng nhỏ gọn, tai nghe ôm sát tai mà không gây cảm giác khó chịu ngay cả khi đeo trong thời gian dài. Ống tai nghe được thiết kế độc đáo, không chỉ giúp cải thiện chất lượng âm thanh mà còn đảm bảo độ ổn định khi vận động. Bề mặt tai nghe được phủ lớp hoàn thiện mịn màng, chống bám vân tay, cùng với màu sắc trẻ trung và thanh lịch, tạo nên một phụ kiện thời trang không thể thiếu cho những ai yêu thích sự tiện lợi và phong cách.", Type = ParseGadgetDescriptionType("NormalText"), Index = 10 },
new GadgetDescription { Id = Guid.Parse("f7bd238e-3b73-4bdc-8aa1-08fd626c2472"), GadgetId = Guid.Parse("685405fc-dc8b-448f-a529-d6629867fd8d"), Value = "https://storage.googleapis.com/fbdemo-f9d5f.appspot.com/Gadgets/9f4a6211-d5b6-4bd0-be4f-52602d5fb0af.jpg", Type = ParseGadgetDescriptionType("Image"), Index = 11 },
new GadgetDescription { Id = Guid.Parse("2ec433f2-222b-4397-91a8-e0d244c03c71"), GadgetId = Guid.Parse("685405fc-dc8b-448f-a529-d6629867fd8d"), Value = "Nghe nhạc dài lâu với viên pin lớn", Type = ParseGadgetDescriptionType("BoldText"), Index = 12 },
new GadgetDescription { Id = Guid.Parse("e19e43ff-9ef5-4c9e-9db9-f6e818f3eda9"), GadgetId = Guid.Parse("685405fc-dc8b-448f-a529-d6629867fd8d"), Value = "Xiaomi Redmi Buds 6 Lite mang đến thời lượng pin ấn tượng, đáp ứng nhu cầu sử dụng hàng ngày mà không lo ngại về việc hết pin giữa chừng. Tai nghe có thể hoạt động liên tục lên đến 7 giờ chỉ với một lần sạc trong 1.5 giờ, giúp bạn tận hưởng âm nhạc, cuộc gọi và giải trí mà không bị gián đoạn. Hộp sạc đi kèm cũng không kém phần mạnh mẽ, cung cấp thêm 38 giờ sử dụng với thời gian sạc khoảng 2 giờ, đảm bảo rằng bạn luôn có đủ năng lượng để sử dụng tai nghe trong suốt cả tuần mà không cần phải sạc quá thường xuyên.", Type = ParseGadgetDescriptionType("NormalText"), Index = 13 },
new GadgetDescription { Id = Guid.Parse("f16a6012-dcad-4fda-8dfc-c54869cbdced"), GadgetId = Guid.Parse("685405fc-dc8b-448f-a529-d6629867fd8d"), Value = "Việc được trang bị khả năng chống nước và bụi đạt chuẩn IP54, giúp bảo vệ tai nghe khỏi các tác động của môi trường như mồ hôi, nước mưa nhẹ và bụi bẩn. Với tính năng này, bạn có thể yên tâm sử dụng tai nghe trong các hoạt động hàng ngày, từ luyện tập thể thao đến di chuyển ngoài trời, mà không lo ngại về độ bền của sản phẩm.", Type = ParseGadgetDescriptionType("NormalText"), Index = 14 },
new GadgetDescription { Id = Guid.Parse("8c4045c0-4756-4a39-80e3-91fd0656e97d"), GadgetId = Guid.Parse("c383447f-406e-4bfc-bb8c-3024f23dac06"), Value = "Nếu bạn là một tín đồ âm nhạc đích thực, luôn tìm kiếm những trải nghiệm nghe nhạc đỉnh cao, thì đừng bỏ qua Xiaomi Redmi Buds 5. Với thiết kế thời thượng, cảm giác đeo thoải mái, chất âm sống động và thời lượng pin dài, Redmi Buds 5 sẽ mang đến cho bạn những phút giây thư giãn tuyệt vời.", Type = ParseGadgetDescriptionType("BoldText"), Index = 0 },
new GadgetDescription { Id = Guid.Parse("f591a439-c0c4-4f85-9d27-f273aee06638"), GadgetId = Guid.Parse("c383447f-406e-4bfc-bb8c-3024f23dac06"), Value = "Kiểu dáng nhỏ gọn, thoải mái khi đeo", Type = ParseGadgetDescriptionType("NormalText"), Index = 1 },
new GadgetDescription { Id = Guid.Parse("8fe203e9-6854-4ff4-964a-2e80f699ba8a"), GadgetId = Guid.Parse("c383447f-406e-4bfc-bb8c-3024f23dac06"), Value = "Xiaomi Redmi Buds 5 sở hữu hộp sạc nhỏ gọn, thanh lịch với thiết kế hình chữ nhật bo cong mềm mại. Chất liệu nhựa nhám mờ hạn chế bám dấu vân tay, giúp hộp sạc luôn sạch sẽ, đẹp mắt. Chỉ với một chiếc khăn ẩm, bạn đã có thể dễ dàng lau chùi hộp sạc mà không lo trầy xước.", Type = ParseGadgetDescriptionType("NormalText"), Index = 2 },
new GadgetDescription { Id = Guid.Parse("e6c6e58b-3c9f-4172-9907-6e22594ca5a8"), GadgetId = Guid.Parse("c383447f-406e-4bfc-bb8c-3024f23dac06"), Value = "Phần bản lề của hộp sạc Redmi Buds 5 được làm từ chất liệu bền bỉ, có độ hoàn thiện cao. Thao tác đóng, mở nắp hộp rất nhẹ nhàng, êm ái, không gây tiếng ồn. Một điểm cộng lớn của chiếc tai nghe này là Xiaomi đã thiết kế phần nam châm trên chuôi tai nghe và trong hộp sạc rất chắc chắn. Nhờ đó, tai nghe có thể hít chặt vào hộp sạc, không lo rơi ra ngoài.", Type = ParseGadgetDescriptionType("NormalText"), Index = 3 },
new GadgetDescription { Id = Guid.Parse("708beb60-5220-4c8c-8c8d-dd3d50dc352e"), GadgetId = Guid.Parse("c383447f-406e-4bfc-bb8c-3024f23dac06"), Value = "Tai nghe in-ear với housing thuôn dài, đệm cao su mềm mại ôm sát vào tai mang lại cảm giác đeo vừa vặn, thoải mái, độ bám tốt hạn chế rơi khỏi tai khi vận động mạnh hay chơi thể thao.", Type = ParseGadgetDescriptionType("NormalText"), Index = 4 },
new GadgetDescription { Id = Guid.Parse("b8194073-b506-458f-a85f-4da0595e35a2"), GadgetId = Guid.Parse("c383447f-406e-4bfc-bb8c-3024f23dac06"), Value = "https://storage.googleapis.com/fbdemo-f9d5f.appspot.com/Gadgets/83fb6066-7434-455a-a34a-a40f1e7139fa.jpg", Type = ParseGadgetDescriptionType("Image"), Index = 5 },
new GadgetDescription { Id = Guid.Parse("80ed46a6-ec99-4c58-82d1-f501c8f4de5b"), GadgetId = Guid.Parse("c383447f-406e-4bfc-bb8c-3024f23dac06"), Value = "Mang đến âm thanh chân thật khi nghe", Type = ParseGadgetDescriptionType("BoldText"), Index = 6 },
new GadgetDescription { Id = Guid.Parse("54875fef-b909-4e67-908b-1cbcf96d671e"), GadgetId = Guid.Parse("c383447f-406e-4bfc-bb8c-3024f23dac06"), Value = "Tai nghe Xiaomi có khả năng tái hiện đầy đủ các dải tần âm thanh, từ những bản nhạc ballad nhẹ nhàng, sâu lắng đến những bản nhạc rock sôi động, mạnh mẽ. Đặc biệt, âm trường rộng rãi giúp người dùng cảm nhận được sự chân thực, sống động của âm nhạc.", Type = ParseGadgetDescriptionType("NormalText"), Index = 7 },
new GadgetDescription { Id = Guid.Parse("33a74a71-a007-4f93-b13d-42ce7f23d731"), GadgetId = Guid.Parse("c383447f-406e-4bfc-bb8c-3024f23dac06"), Value = "Tai nghe True Wireless giảm tiếng ồn có thể tạo ra một không gian yên tĩnh, thoải mái ngay cả trong những môi trường ồn ào. Với khả năng giảm tiếng ồn lên đến 46 dB và chặn 99.5% tiếng ồn xung quanh, tai nghe này có thể giúp bạn tập trung vào công việc, nghe nhạc hoặc đơn giản là thư giãn mà không bị phân tâm bởi tiếng ồn bên ngoài.", Type = ParseGadgetDescriptionType("NormalText"), Index = 8 },
new GadgetDescription { Id = Guid.Parse("535551d6-5b71-4bd9-9753-71d6eb316799"), GadgetId = Guid.Parse("c383447f-406e-4bfc-bb8c-3024f23dac06"), Value = "Đặc biệt, chiếc tai nghe Xiaomi Redmi Buds 5 này còn có thể giảm tiếng ồn tần số thấp một cách hiệu quả, giúp bạn thoải mái hơn khi di chuyển bằng máy bay hoặc tàu điện ngầm. Nhờ đó, bạn có thể tận hưởng trọn vẹn chuyến đi của mình mà không bị tiếng ồn làm phiền. Tính năng chống ồn \"thích ứng\" bằng AI của Redmi Buds 5 cũng mang đến cho người dùng sự linh hoạt trong việc lựa chọn mức độ chống ồn, phù hợp với từng nhu cầu sử dụng.", Type = ParseGadgetDescriptionType("NormalText"), Index = 9 },
new GadgetDescription { Id = Guid.Parse("4b5796b0-a618-46ae-8f47-3c485e0160f8"), GadgetId = Guid.Parse("c383447f-406e-4bfc-bb8c-3024f23dac06"), Value = "https://storage.googleapis.com/fbdemo-f9d5f.appspot.com/Gadgets/45167074-700e-4104-bc6c-473a7278b0e9.jpg", Type = ParseGadgetDescriptionType("Image"), Index = 10 },
new GadgetDescription { Id = Guid.Parse("3aa415bf-3ca3-479c-afd2-7120a8270b71"), GadgetId = Guid.Parse("c383447f-406e-4bfc-bb8c-3024f23dac06"), Value = "Xiaomi Redmi Buds 5 mang đến trải nghiệm âm thanh sống động nhờ driver có kích thước 12.4 mm và màng loa được phủ titan. Driver có kích thước lớn hơn mang lại vùng rung lớn hơn, giúp tái tạo âm trầm sâu và có kết cấu hơn. Màng loa được phủ titan có độ cứng cao, tái tạo âm treble trong trẻo và sắc nét hơn.", Type = ParseGadgetDescriptionType("NormalText"), Index = 11 },
new GadgetDescription { Id = Guid.Parse("96b33425-6e80-4452-ae22-be631ae34b21"), GadgetId = Guid.Parse("c383447f-406e-4bfc-bb8c-3024f23dac06"), Value = "Redmi Buds 5 là phiên bản đầu tiên của tai nghe Redmi sử dụng thuật toán AI kênh đôi tự phát triển, giúp giảm tiếng ồn của gió lên đến 6 m/s. Thuật toán này sử dụng hai micro để thu âm thanh của gió từ hai hướng khác nhau, sau đó sử dụng thuật toán AI để lọc tiếng ồn của gió một cách hiệu quả.", Type = ParseGadgetDescriptionType("NormalText"), Index = 12 },
new GadgetDescription { Id = Guid.Parse("d16afee2-9d32-4396-976d-e3a565ae587f"), GadgetId = Guid.Parse("c383447f-406e-4bfc-bb8c-3024f23dac06"), Value = "Nhờ thuật toán này, Xiaomi Redmi Buds 5 có thể mang lại chất lượng đàm thoại rõ ràng và tự nhiên ngay cả khi bạn đang đi xe hoặc chạy ngoài trời. Bạn sẽ có cảm giác như đang giao tiếp mặt đối mặt với người đối diện, không bị tiếng ồn của gió làm phiền.", Type = ParseGadgetDescriptionType("NormalText"), Index = 13 },
new GadgetDescription { Id = Guid.Parse("99230f1f-3c46-4bfb-9190-33ac52e21a06"), GadgetId = Guid.Parse("c383447f-406e-4bfc-bb8c-3024f23dac06"), Value = "Thao tác điều khiển đơn giản, kết nối ổn định", Type = ParseGadgetDescriptionType("BoldText"), Index = 14 },
new GadgetDescription { Id = Guid.Parse("751028a4-1a91-4cf9-a29b-ec91a7b80cbe"), GadgetId = Guid.Parse("c383447f-406e-4bfc-bb8c-3024f23dac06"), Value = "Tai nghe Xiaomi Redmi Buds 5 cho phép người dùng dễ dàng tùy chỉnh trải nghiệm nghe nhạc với các thao tác chạm đơn giản. Bạn có thể phát/dừng nhạc, nhận/ngắt cuộc gọi,... chỉ với vài động tác chạm nhẹ trên tai nghe.", Type = ParseGadgetDescriptionType("NormalText"), Index = 15 },
new GadgetDescription { Id = Guid.Parse("094fabf0-7f84-4ca3-b42e-8983aec30277"), GadgetId = Guid.Parse("c383447f-406e-4bfc-bb8c-3024f23dac06"), Value = "Công nghệ Bluetooth 5.3 tiên tiến giúp kết nối tai nghe với thiết bị di động mượt mà, ổn định, giảm thiểu độ trễ, cho bạn tận hưởng âm nhạc và các cuộc gọi video liền mạch. Ngoài ra, công nghệ này còn giúp tiết kiệm năng lượng, kéo dài thời gian sử dụng tai nghe.", Type = ParseGadgetDescriptionType("NormalText"), Index = 16 },
new GadgetDescription { Id = Guid.Parse("63e80e58-193f-409b-8c12-16bc98964f37"), GadgetId = Guid.Parse("c383447f-406e-4bfc-bb8c-3024f23dac06"), Value = "Redmi Buds 5 được trang bị tính năng Google Fast Pair, giúp người dùng có thể kết nối với các thiết bị Android qua Bluetooth một cách vô cùng nhanh chóng và dễ dàng. Chỉ cần mở nắp hộp đựng tai nghe, cửa sổ ghép đôi sẽ tự động bật lên trên điện thoại. Nhấn vào cửa sổ này và chạm vào tai nghe để kết nối. Toàn bộ quá trình chỉ mất vài giây, không cần phải thực hiện các bước rườm rà như trước đây.", Type = ParseGadgetDescriptionType("NormalText"), Index = 17 },
new GadgetDescription { Id = Guid.Parse("128590e3-2b3b-44de-8109-e14504678b23"), GadgetId = Guid.Parse("c383447f-406e-4bfc-bb8c-3024f23dac06"), Value = "Chuẩn chống nước IP54 và thời lượng pin vượt trội", Type = ParseGadgetDescriptionType("BoldText"), Index = 18 },
new GadgetDescription { Id = Guid.Parse("0c102ebe-5a85-411c-9a90-860f2d3080b6"), GadgetId = Guid.Parse("c383447f-406e-4bfc-bb8c-3024f23dac06"), Value = "Chuẩn chống nước chống bụi IP54 giúp bảo vệ tai nghe Bluetooth True Wireless Xiaomi an toàn khi dính nước hay bụi bẩn trong quá trình sử dụng, nhưng khả năng này có hạn chế và sẽ giảm dần theo thời gian. Do đó, người dùng nên bảo quản tai nghe thật tốt để kéo dài tuổi thọ của sản phẩm.", Type = ParseGadgetDescriptionType("NormalText"), Index = 19 },
new GadgetDescription { Id = Guid.Parse("8a262cc8-17ca-44c4-8937-7298b7511513"), GadgetId = Guid.Parse("c383447f-406e-4bfc-bb8c-3024f23dac06"), Value = "Redmi Buds 5 sở hữu thời lượng pin ấn tượng, với 10 giờ phát nhạc liên tục và tổng thời lượng lên đến 40 giờ khi kết hợp với hộp sạc. Đây là một con số đáng nể, giúp người dùng thoải mái sử dụng tai nghe cả ngày dài mà không cần lo lắng về vấn đề hết pin đột ngột.", Type = ParseGadgetDescriptionType("NormalText"), Index = 20 },
new GadgetDescription { Id = Guid.Parse("d3045f84-7a30-4830-8a37-35f2040719ab"), GadgetId = Guid.Parse("c383447f-406e-4bfc-bb8c-3024f23dac06"), Value = "Nếu bật tính năng khử tiếng ồn, thời lượng pin của Redmi Buds 5 sẽ giảm xuống còn 8 giờ cho việc nghe nhạc và 30 giờ khi kết hợp với hộp sạc. Tuy nhiên, đây vẫn là một con số khá tốt so với mặt bằng chung của các tai nghe Bluetooth có tính năng khử tiếng ồn.", Type = ParseGadgetDescriptionType("NormalText"), Index = 21 },
new GadgetDescription { Id = Guid.Parse("048d80a9-72da-4eed-adbd-20e5fa091225"), GadgetId = Guid.Parse("c383447f-406e-4bfc-bb8c-3024f23dac06"), Value = "Tính năng sạc nhanh của tai nghe Bluetooth True Wireless Xiaomi này là một điểm cộng lớn, giúp người dùng có thể nhanh chóng tiếp tục sử dụng tai nghe mà không cần phải chờ đợi lâu. Chỉ với 5 phút sạc, tai nghe đã có thể nghe nhạc liên tục trong 2 giờ.", Type = ParseGadgetDescriptionType("NormalText"), Index = 22 },
new GadgetDescription { Id = Guid.Parse("540f05d2-dd64-40b5-a5bb-bb248be6b995"), GadgetId = Guid.Parse("c383447f-406e-4bfc-bb8c-3024f23dac06"), Value = "https://storage.googleapis.com/fbdemo-f9d5f.appspot.com/Gadgets/58a121f3-b196-4acc-8418-cee240a05f65.jpg", Type = ParseGadgetDescriptionType("Image"), Index = 23 },
new GadgetDescription { Id = Guid.Parse("9851d848-2392-4bda-ad81-477db2f6f421"), GadgetId = Guid.Parse("03ca6c72-f433-4750-a4b9-2c9d944ae5f7"), Value = "Xiaomi Redmi Buds 6 Active mẫu tai nghe không dây mới nhất từ Xiaomi, nổi bật với thiết kế hiện đại và hiệu suất âm thanh vượt trội trong phân khúc. Sản phẩm sở hữu thời lượng pin ấn tượng, khả năng kết nối ổn định, và chất lượng âm thanh rõ ràng, Redmi Buds 6 Active hứa hẹn mang đến trải nghiệm âm nhạc tuyệt vời cho người dùng.", Type = ParseGadgetDescriptionType("BoldText"), Index = 0 },
new GadgetDescription { Id = Guid.Parse("94f9c5a4-7e85-42cc-baae-96fdab87dd34"), GadgetId = Guid.Parse("03ca6c72-f433-4750-a4b9-2c9d944ae5f7"), Value = "Đa dạng phong cách màu sắc xinh đẹp", Type = ParseGadgetDescriptionType("BoldText"), Index = 1 },
new GadgetDescription { Id = Guid.Parse("fe1e36e0-cbb8-4b45-b50a-73f8cc5263c9"), GadgetId = Guid.Parse("03ca6c72-f433-4750-a4b9-2c9d944ae5f7"), Value = "Tai nghe Bluetooth TWS Xiaomi Redmi Buds 6 Active có thiết kế nhỏ gọn và thanh lịch, phù hợp với mọi phong cách và hoàn cảnh sử dụng. Vỏ tai nghe được làm từ chất liệu khá nhẹ nhưng bền, với bề mặt mịn màng và các góc bo tròn, tạo cảm giác thoải mái khi đeo trong thời gian dài.", Type = ParseGadgetDescriptionType("NormalText"), Index = 2 },
new GadgetDescription { Id = Guid.Parse("91544c3f-2fe0-4053-b674-235f84f0d55e"), GadgetId = Guid.Parse("03ca6c72-f433-4750-a4b9-2c9d944ae5f7"), Value = "Tai nghe được thiết kế dạng Earbuds mang lại cảm giác đeo chắc chắn mà không gây đau tai. Hộp sạc của Redmi Buds 6 Active cũng được thiết kế nhỏ gọn, dễ dàng mang theo bên mình. Redmi Buds 6 Active cũng được thiết kế với khả năng chống nước theo tiêu chuẩn IPX4, giúp bảo vệ tai nghe khỏi các tác động từ môi trường xung quanh.", Type = ParseGadgetDescriptionType("NormalText"), Index = 3 },
new GadgetDescription { Id = Guid.Parse("4c11c004-a36e-4267-b3ae-a5913ccf2de1"), GadgetId = Guid.Parse("03ca6c72-f433-4750-a4b9-2c9d944ae5f7"), Value = "https://storage.googleapis.com/fbdemo-f9d5f.appspot.com/Gadgets/f1593d8f-5613-426a-805a-67917c559c82.jpg", Type = ParseGadgetDescriptionType("Image"), Index = 4 },
new GadgetDescription { Id = Guid.Parse("e27d11a0-3dc3-4713-97f0-f07f60e005e0"), GadgetId = Guid.Parse("03ca6c72-f433-4750-a4b9-2c9d944ae5f7"), Value = "Không những thế Buds 6 Active được giới thiệu với các tùy chọn màu sắc tinh tế và hiện đại, phù hợp với nhiều phong cách khác nhau. Những màu sắc như: đen, hồng,... đều mang lại vẻ ngoài thanh lịch và sang trọng cho tai nghe. Sự đa dạng về màu sắc giúp bạn dễ dàng chọn lựa theo sở thích cá nhân, đồng thời mỗi màu sắc trên tai nghe Bluetooth đều được hoàn thiện với lớp phủ giữ cho tai nghe luôn sạch sẽ và mới mẻ.", Type = ParseGadgetDescriptionType("Image"), Index = 5 },
new GadgetDescription { Id = Guid.Parse("94a8f01a-c1ab-4edc-8e4a-4fbc8575c08a"), GadgetId = Guid.Parse("03ca6c72-f433-4750-a4b9-2c9d944ae5f7"), Value = "Âm thanh cuốn hút nghe gọi không tiếng ồn", Type = ParseGadgetDescriptionType("BoldText"), Index = 6 },
new GadgetDescription { Id = Guid.Parse("0571e17b-4913-4ed4-b1bd-0fb1541aec07"), GadgetId = Guid.Parse("03ca6c72-f433-4750-a4b9-2c9d944ae5f7"), Value = "Tai nghe Xiaomi được trang bị công nghệ âm thanh tiên tiến, mang đến trải nghiệm nghe nhạc chất lượng cao. Với Driver 14.2 mm, tai nghe này tái tạo âm thanh sống động, mạnh mẽ. Ngoài ra, hệ thống 2 micro chống ồn giúp đảm bảo giọng nói của bạn được truyền tải rõ ràng trong các cuộc gọi, ngay cả trong môi trường ồn ào.", Type = ParseGadgetDescriptionType("NormalText"), Index = 7 },
new GadgetDescription { Id = Guid.Parse("8bf6ce6f-39d0-49d4-800e-a03dc67d51f3"), GadgetId = Guid.Parse("03ca6c72-f433-4750-a4b9-2c9d944ae5f7"), Value = "https://storage.googleapis.com/fbdemo-f9d5f.appspot.com/Gadgets/b04d985e-0737-45d1-a4ff-5e11681edb68.jpg", Type = ParseGadgetDescriptionType("Image"), Index = 8 },
new GadgetDescription { Id = Guid.Parse("10d7d7a2-f733-4475-b597-d101e17bc2cf"), GadgetId = Guid.Parse("03ca6c72-f433-4750-a4b9-2c9d944ae5f7"), Value = "Khi nghe nhạc với Xiaomi Redmi Buds 6 Active, bạn sẽ được đắm chìm trong không gian âm nhạc sống động và đầy màu sắc. Tai nghe này tái hiện âm nhạc một cách rõ ràng và chi tiết, từ những giai điệu nhẹ nhàng, du dương của các bản ballad đến những tiết tấu mạnh mẽ, sôi động.", Type = ParseGadgetDescriptionType("NormalText"), Index = 9 },
new GadgetDescription { Id = Guid.Parse("5dfca9dc-10f1-4c4b-a7d9-d04e51918e0d"), GadgetId = Guid.Parse("03ca6c72-f433-4750-a4b9-2c9d944ae5f7"), Value = "Sử dụng dài lâu với viên pin lớn", Type = ParseGadgetDescriptionType("BoldText"), Index = 10 },
new GadgetDescription { Id = Guid.Parse("9ec23d1b-0a0e-4746-b1d4-f1d13a02a5a1"), GadgetId = Guid.Parse("03ca6c72-f433-4750-a4b9-2c9d944ae5f7"), Value = "Xiaomi Redmi Buds 6 Active sở hữu thời lượng pin ấn tượng, đáp ứng tốt nhu cầu sử dụng hàng ngày. Tai nghe có thể hoạt động liên tục trong 6 giờ chỉ với một lần sạc, cho phép bạn thoải mái nghe nhạc, xem phim, hoặc gọi điện mà không lo hết pin giữa chừng. Thời gian sạc tai nghe cũng rất nhanh, chỉ khoảng 1.5 giờ để sạc đầy, giúp bạn nhanh chóng sẵn sàng cho những trải nghiệm âm thanh tiếp theo.", Type = ParseGadgetDescriptionType("NormalText"), Index = 11 },
new GadgetDescription { Id = Guid.Parse("41067f7a-9cce-475b-89a6-583bd3141b51"), GadgetId = Guid.Parse("03ca6c72-f433-4750-a4b9-2c9d944ae5f7"), Value = "https://storage.googleapis.com/fbdemo-f9d5f.appspot.com/Gadgets/e970f743-7dc4-4a6d-abd0-a84cf2577e60.jpg", Type = ParseGadgetDescriptionType("Image"), Index = 12 },
new GadgetDescription { Id = Guid.Parse("b0f73ee0-1b43-4db4-9417-56f9829b376e"), GadgetId = Guid.Parse("03ca6c72-f433-4750-a4b9-2c9d944ae5f7"), Value = "Hộp sạc đi kèm không chỉ bảo vệ tai nghe mà còn cung cấp thêm 30 giờ sử dụng, đảm bảo rằng bạn luôn có nguồn năng lượng dự trữ khi cần thiết. Với thời gian sạc hộp chỉ khoảng 2 giờ, bạn có thể dễ dàng duy trì tai nghe trong trạng thái hoạt động suốt cả ngày dài, thậm chí cả tuần.", Type = ParseGadgetDescriptionType("NormalText"), Index = 13 },
new GadgetDescription { Id = Guid.Parse("a65a98ff-bc68-4f1f-b968-046e516a4586"), GadgetId = Guid.Parse("03ca6c72-f433-4750-a4b9-2c9d944ae5f7"), Value = "Kết nối nhanh chóng không gián đoạn", Type = ParseGadgetDescriptionType("BoldText"), Index = 14 },
new GadgetDescription { Id = Guid.Parse("8f8a540c-e79f-4b15-8d86-630fffea5e24"), GadgetId = Guid.Parse("03ca6c72-f433-4750-a4b9-2c9d944ae5f7"), Value = "Xiaomi Redmi Buds 6 Active được trang bị công nghệ kết nối Bluetooth 5.4, mang lại trải nghiệm kết nối nhanh chóng và ổn định hơn so với các phiên bản trước. Với Bluetooth 5.4, tai nghe này không chỉ đảm bảo phạm vi kết nối rộng hơn, mà còn giảm thiểu độ trễ, giúp âm thanh và hình ảnh luôn đồng bộ khi xem video hoặc chơi game.", Type = ParseGadgetDescriptionType("NormalText"), Index = 15 },
new GadgetDescription { Id = Guid.Parse("25ef330e-85b5-4264-ad9c-70b30ab3e71e"), GadgetId = Guid.Parse("d28248e7-74d5-4092-8314-0131a22d0f13"), Value = "Redmi Buds 4 Active là mẫu tai nghe True Wireless được Xiaomi cho ra mắt vào năm 2023. Sở hữu một mức giá vô cùng dễ tiếp cận nhưng được trang bị rất nhiều tính năng hiện đại như Bluetooth 5.3, mic khử ồn, điều khiển bằng ứng dụng, độ trễ thấp,... phục vụ tối ưu nhu cầu sử dụng cơ bản của người dùng.", Type = ParseGadgetDescriptionType("BoldText"), Index = 0 },
new GadgetDescription { Id = Guid.Parse("4e94c1f3-4cb2-44ca-a721-8c622667d9fc"), GadgetId = Guid.Parse("d28248e7-74d5-4092-8314-0131a22d0f13"), Value = "Thiết kế tối giản, gọn gàng", Type = ParseGadgetDescriptionType("BoldText"), Index = 1 },
new GadgetDescription { Id = Guid.Parse("36be3580-1737-42f8-a680-1aea2bd994e7"), GadgetId = Guid.Parse("d28248e7-74d5-4092-8314-0131a22d0f13"), Value = "Về phần hộp sạc, Redmi Buds 4 Active mang ngôn ngữ thiết kế tối giản với hình dáng oval dẹp, khi cầm rất ôm tay. Khối lượng tổng thể vô cùng nhẹ chỉ 42 gram, dễ dàng bỏ vào túi quần khi đi bộ hay chạy bộ mà không lo bị cấn.", Type = ParseGadgetDescriptionType("NormalText"), Index = 2 },
new GadgetDescription { Id = Guid.Parse("861b899d-92a3-43be-86a3-9bc699d3430d"), GadgetId = Guid.Parse("d28248e7-74d5-4092-8314-0131a22d0f13"), Value = "https://storage.googleapis.com/fbdemo-f9d5f.appspot.com/Gadgets/990d8b97-0185-4cab-ba1e-b9ea6b9a2c89.jpg ", Type = ParseGadgetDescriptionType("Image"), Index = 3 },
new GadgetDescription { Id = Guid.Parse("439e70ec-cfb4-428f-ad17-d93d90b83ea9"), GadgetId = Guid.Parse("d28248e7-74d5-4092-8314-0131a22d0f13"), Value = "Bề mặt được hoàn thiện từ chất liệu nhựa nhám giúp hạn chế tình trạng bám mồ hôi và dấu vân tay. Trên thân vỏ cũng được thiết kế thêm một dải nhựa bóng tạo điểm nhấn khi nhìn ngang.", Type = ParseGadgetDescriptionType("NormalText"), Index = 4 },
new GadgetDescription { Id = Guid.Parse("42a6d46a-b8bd-4ed3-b34e-5b68cc087f12"), GadgetId = Guid.Parse("d28248e7-74d5-4092-8314-0131a22d0f13"), Value = "Mặt trên của hộp sạc còn có logo Redmi được làm theo dạng dập nổi, tăng độ nhận diện cho tai nghe. Phần bản lề theo trải nghiệm của mình thì nó được hoàn thiện chắc chắn, hơn nhiều sản phẩm trong cùng phân khúc giá khác mà mình từng dùng, nên bạn có thể hoàn toàn yên tâm trong thao tác sử dụng thường ngày.", Type = ParseGadgetDescriptionType("NormalText"), Index = 5 },
new GadgetDescription { Id = Guid.Parse("47aae67b-bfaa-4608-ae58-e8ea39ea81a9"), GadgetId = Guid.Parse("d28248e7-74d5-4092-8314-0131a22d0f13"), Value = "Mặt đáy của hộp sạc là cổng sạc USB Type-C và một đèn báo hiệu để thông báo tình trạng pin của hộp sạc. Khi đèn báo hộp sạc tắt thì có nghĩa tai nghe của bạn đã được sạc đầy.", Type = ParseGadgetDescriptionType("NormalText"), Index = 6 },
new GadgetDescription { Id = Guid.Parse("c373314c-7ad7-45d1-b3ea-a268fda6a4f5"), GadgetId = Guid.Parse("d28248e7-74d5-4092-8314-0131a22d0f13"), Value = "Phần tai nghe cũng được hoàn thiện từ chất liệu nhựa nhám tạo cảm giác đồng bộ màu sắc lẫn thiết kế cho tổng thể. Housing được làm dạng hình bầu dục, có rãnh microphone ở đuôi chân tai nghe và khu vực điều khiển cảm ứng ở dọc phần thân.", Type = ParseGadgetDescriptionType("NormalText"), Index = 7 },
new GadgetDescription { Id = Guid.Parse("86893423-ca02-4a8f-8807-907ccde9e8ee"), GadgetId = Guid.Parse("d28248e7-74d5-4092-8314-0131a22d0f13"), Value = "Ngoài ra ở mỗi tai nghe đều được trang bị đèn báo hiệu luôn nên chúng ta có thể biết được tai nghe đang ở mức pin như thế nào và có cần sạc hay không.", Type = ParseGadgetDescriptionType("NormalText"), Index = 8 },
new GadgetDescription { Id = Guid.Parse("50ef92f8-5ba9-484f-ab7c-4b8957536976"), GadgetId = Guid.Parse("d28248e7-74d5-4092-8314-0131a22d0f13"), Value = "Khả năng điều khiển, kết nối", Type = ParseGadgetDescriptionType("BoldText"), Index = 9 },
new GadgetDescription { Id = Guid.Parse("e1d0c6a9-4321-44bf-94d0-29c24d389208"), GadgetId = Guid.Parse("d28248e7-74d5-4092-8314-0131a22d0f13"), Value = "Redmi Buds 4 Active được trang bị đầy đủ các tùy chỉnh thao tác chạm như: Nhấn 2 lần để phát/tạm dừng nhạc hoặc trả lời cuộc gọi, nhấn 3 lần để chuyển sang bài hát tiếp theo hoặc từ chối/kết thúc cuộc gọi. Thêm nữa là thao tác nhấn giữ tai nghe trong vòng 3 giây để bật/tắt chế độ Độ trễ thấp.", Type = ParseGadgetDescriptionType("NormalText"), Index = 10 },
new GadgetDescription { Id = Guid.Parse("3ea6d23a-e81a-42f1-8214-69c89e86978f"), GadgetId = Guid.Parse("d28248e7-74d5-4092-8314-0131a22d0f13"), Value = "Theo trải nghiệm thực tế của mình thì những tính năng này khá nhạy với tốc độ phản hồi nhanh. Và với yếu tố cảm biến này nếu đem so sánh với các sản phẩm khác trong cùng tầm giá thì chắc chắn đây sẽ là một điểm cộng lớn.", Type = ParseGadgetDescriptionType("NormalText"), Index = 11 },
new GadgetDescription { Id = Guid.Parse("0490e598-5e38-4c48-884d-d8992b44689a"), GadgetId = Guid.Parse("d28248e7-74d5-4092-8314-0131a22d0f13"), Value = " https://storage.googleapis.com/fbdemo-f9d5f.appspot.com/Gadgets/78eb1dfb-71c9-4ad4-be62-d0043ae4f8d5.jpg ", Type = ParseGadgetDescriptionType("Image"), Index = 12 },
new GadgetDescription { Id = Guid.Parse("c2e4d565-fd81-4d52-8348-2ea1cdaa9325"), GadgetId = Guid.Parse("d28248e7-74d5-4092-8314-0131a22d0f13"), Value = "Hơn thế nữa, Redmi Buds 4 Active còn được trang bị tính năng Google Fast Pair, cho phép chúng ta mở nắp cho cửa sổ ghép đôi bật lên và chạm vào tai nghe để kết nối với các thiết bị Android qua Bluetooth, vô cùng nhanh chóng và dễ dàng mà không phải thực hiện các bước rườm rà như trước đây.", Type = ParseGadgetDescriptionType("NormalText"), Index = 13 },
new GadgetDescription { Id = Guid.Parse("8dc9de3a-2e11-4c9d-95b4-19aeaa180b25"), GadgetId = Guid.Parse("d28248e7-74d5-4092-8314-0131a22d0f13"), Value = "Trải nghiệm đeo trên tai", Type = ParseGadgetDescriptionType("BoldText"), Index = 14 },
new GadgetDescription { Id = Guid.Parse("21d98dc2-c6c2-4587-81bc-6def6fd9fca7"), GadgetId = Guid.Parse("d28248e7-74d5-4092-8314-0131a22d0f13"), Value = "Về cảm giác đeo, dù là tai nghe in-ear nhưng theo đánh giá của mình thì Redmi Buds 4 Active đeo rất thoải mái. Nhờ phần housing đi sâu vào hốc tai mà chiếc tai nghe này bám rất chắc chắn trên tai mình. Dù có chạy bộ nhanh, mồ hôi lẫn vào bên trong thì sản phẩm cũng rất hiếm khi trượt khỏi tai. Không những thế, mỗi bên tai chỉ nặng 3.65 gram nên cảm giác khi đeo rất nhẹ nhàng và dễ chịu.", Type = ParseGadgetDescriptionType("NormalText"), Index = 15 },
new GadgetDescription { Id = Guid.Parse("f0968463-3845-4265-a646-bccd2daf4abc"), GadgetId = Guid.Parse("d28248e7-74d5-4092-8314-0131a22d0f13"), Value = "Với eartips được gắn mặc định trên thiết bị, mình đeo vào thì thấy nó vừa với hốc tai của mình. Xiaomi cũng đã trang bị thêm cho người dùng 2 cặp eartips khác đi kèm, giúp bạn có thể lựa chọn kích thước phù hợp với tai mình.", Type = ParseGadgetDescriptionType("NormalText"), Index = 16 },
new GadgetDescription { Id = Guid.Parse("7777b234-e8b4-46a5-a004-1ebd8f0311dc"), GadgetId = Guid.Parse("d28248e7-74d5-4092-8314-0131a22d0f13"), Value = "Âm thanh chất lượng cao, hỗ trợ mic khử ồn", Type = ParseGadgetDescriptionType("BoldText"), Index = 17 },
new GadgetDescription { Id = Guid.Parse("62aae054-20af-405d-bb04-cf348a2d4cc5"), GadgetId = Guid.Parse("d28248e7-74d5-4092-8314-0131a22d0f13"), Value = "Về phần chất lượng âm thanh, Redmi Buds 4 Active trang bị driver động 12 mm, theo thông tin từ hãng thì sẽ giúp tai nghe có âm trầm mạnh mẽ hơn.", Type = ParseGadgetDescriptionType("NormalText"), Index = 18 },
new GadgetDescription { Id = Guid.Parse("3ede0d5d-4feb-4d6f-8049-7c1893b00a7e"), GadgetId = Guid.Parse("d28248e7-74d5-4092-8314-0131a22d0f13"), Value = "Mình cũng có thử trải nghiệm trên nhiều thể loại nhạc khác nhau, thì mình cảm nhận được tai nghe mang thiên hướng âm bass nhiều, cường độ bass đánh rất có lực, rất phù hợp cho những bạn thích nghe âm trầm, dòng nhạc Pop, Ballad.", Type = ParseGadgetDescriptionType("NormalText"), Index = 19 },
new GadgetDescription { Id = Guid.Parse("be52403b-793d-4920-9fda-ca1a7113e21d"), GadgetId = Guid.Parse("d28248e7-74d5-4092-8314-0131a22d0f13"), Value = "Ngoài ra, các hiệu ứng âm thanh cũng được tái tạo rõ ràng, không có cảm giác tù và bí như khi mình trải nghiệm ở các tai nghe khác trong tầm giá.", Type = ParseGadgetDescriptionType("NormalText"), Index = 20 },
new GadgetDescription { Id = Guid.Parse("dc03bff5-69d6-4fd1-8618-770bd170f26e"), GadgetId = Guid.Parse("d28248e7-74d5-4092-8314-0131a22d0f13"), Value = "Thời lượng pin dài, tốc độ sạc nhanh", Type = ParseGadgetDescriptionType("BoldText"), Index = 21 },
new GadgetDescription { Id = Guid.Parse("5ad48363-64bb-4279-be66-453bbb862f97"), GadgetId = Guid.Parse("d28248e7-74d5-4092-8314-0131a22d0f13"), Value = "Và cuối cùng, một trong những yếu tố quan trọng nhất để một người dùng quyết định có mua một sản phẩm tai nghe hay không đó là thời gian sử dụng.", Type = ParseGadgetDescriptionType("NormalText"), Index = 22 },
new GadgetDescription { Id = Guid.Parse("02e1d09b-9941-4bec-ad2f-61302ba12189"), GadgetId = Guid.Parse("d28248e7-74d5-4092-8314-0131a22d0f13"), Value = "Theo thông tin công bố từ hãng, tai nghe được trang bị dung lượng pin 440 mAh, cung cấp thời gian sử dụng liên tục 5 giờ cho tai nghe và lên đến 28 giờ cho hộp sạc (khi sử dụng ở mức âm lượng 50%). Với nhu cầu nghe nhạc cơ bản của mình thì thời lượng pin của tai nghe có thể đáp ứng được khoảng 3 ngày.", Type = ParseGadgetDescriptionType("NormalText"), Index = 23 },
new GadgetDescription { Id = Guid.Parse("c73f3893-d16a-43a3-97b9-6f928485aea7"), GadgetId = Guid.Parse("d28248e7-74d5-4092-8314-0131a22d0f13"), Value = "https://storage.googleapis.com/fbdemo-f9d5f.appspot.com/Gadgets/3d9f0708-19a6-48d6-9b69-746a92714365.jpg ", Type = ParseGadgetDescriptionType("Image"), Index = 24 },
new GadgetDescription { Id = Guid.Parse("8e47e90a-18d5-4733-a759-400d01fb4ffa"), GadgetId = Guid.Parse("d28248e7-74d5-4092-8314-0131a22d0f13"), Value = "Ngoài ra, tai nghe True Wireless cũng hỗ trợ thêm chuẩn chống nước IPX4, cho phép chúng ta có thể tập luyện thể thao mà không phải quá lo lắng về việc mồ hôi tiếp xúc với sản phẩm.", Type = ParseGadgetDescriptionType("NormalText"), Index = 25 },
new GadgetDescription { Id = Guid.Parse("3ec1e153-bcda-4c10-bd7a-e384e70f0998"), GadgetId = Guid.Parse("8ad66852-ec57-41a6-9f75-7ca4180c7d02"), Value = "Nếu bạn đang tìm kiếm một sản phẩm tai nghe Bluetooth không dây sở hữu thiết kế đẹp mắt, cảm giác đeo thoải mái, chất âm tốt đi cùng thời lượng pin dài, thì tai nghe Bluetooth TWS Xiaomi Redmi Buds 4 Lite sẽ là một trong những lựa chọn sáng giá dành cho bạn.", Type = ParseGadgetDescriptionType("BoldText"), Index = 0 },
new GadgetDescription { Id = Guid.Parse("4eeb77aa-6bda-4a00-959a-7cf4bfd693a7"), GadgetId = Guid.Parse("8ad66852-ec57-41a6-9f75-7ca4180c7d02"), Value = "Thiết kế đẹp mắt, trải nghiệm đeo vô cùng thoải mái", Type = ParseGadgetDescriptionType("BoldText"), Index = 1 },
new GadgetDescription { Id = Guid.Parse("39552c93-323d-4c13-b48e-1f12d9f174c7"), GadgetId = Guid.Parse("8ad66852-ec57-41a6-9f75-7ca4180c7d02"), Value = "Xiaomi Redmi Buds 4 Lite sở hữu hộp sạc có ngoại hình nhỏ gọn với các cạnh góc được bo cong mềm mại, khi cầm cho cảm giác rất nhẹ và ôm tay.", Type = ParseGadgetDescriptionType("NormalText"), Index = 2 },
