new GadgetDescription { Id = Guid.Parse("01ebeaa0-8f59-4f85-bb25-113ca357d26e"), GadgetId = Guid.Parse("8ad66852-ec57-41a6-9f75-7ca4180c7d02"), Value = "Được hoàn thiện chủ yếu từ chất liệu nhựa nhám hạn chế bám dấu vân tay, tuy nhiên khi mình sử dụng thì vẫn khá bám mồ hôi, dù vậy thì chúng ta vẫn có thể làm sạch một cách dễ dàng với khăn hoặc giấy ướt.", Type = ParseGadgetDescriptionType("NormalText"), Index = 4 },
new GadgetDescription { Id = Guid.Parse("1e61dadb-b92d-4a1a-8963-91d6a6313905"), GadgetId = Guid.Parse("8ad66852-ec57-41a6-9f75-7ca4180c7d02"), Value = "Hộp sạc này mang diện mạo theo hướng tàu vũ trụ không gian độc đáo, hiện đại. Phần bản lề mình đánh giá được hoàn thiện khá tốt, thao tác đóng, mở nắp hộp rất nhẹ nhàng.", Type = ParseGadgetDescriptionType("NormalText"), Index = 5 },
new GadgetDescription { Id = Guid.Parse("67201161-c648-4594-a02b-ec8826bb64a3"), GadgetId = Guid.Parse("8ad66852-ec57-41a6-9f75-7ca4180c7d02"), Value = "Một điểm mình thích ở chiếc tai nghe này đó là Xiaomi đã thiết kế phần nam châm trên chuôi tai nghe và trong hộp sạc, giúp cho tai nghe có thể hít mạnh và bám rất chắc chắn vào hộp sạc dù mình bỏ vào theo bất kỳ hướng nào, rất thuận tiện trong những lúc vội.", Type = ParseGadgetDescriptionType("NormalText"), Index = 6 },
new GadgetDescription { Id = Guid.Parse("2e384899-b4bb-4e27-8c39-ecb9537caa75"), GadgetId = Guid.Parse("8ad66852-ec57-41a6-9f75-7ca4180c7d02"), Value = "Khác với thế hệ trước là tai nghe dạng in-ear, Redmi Buds 4 Lite là dạng tai nghe Earbuds truyền thống, được thiết kế vừa vặn với hầu hết các kiểu tai phổ thông nhất.", Type = ParseGadgetDescriptionType("NormalText"), Index = 7 },
