new GadgetDescription { Id = Guid.Parse("ad00a53c-c734-428c-815f-b2cbeac770b1"), GadgetId = Guid.Parse("c308ba1f-759a-45e8-a4b0-c15cf1c28e16"), Value = "Đem đến chất âm trung thực, sống động", Type = ParseGadgetDescriptionType("BoldText"), Index = 7 },
new GadgetDescription { Id = Guid.Parse("fd61df5b-3568-4c23-bc5d-78c7e6b3aa5b"), GadgetId = Guid.Parse("c308ba1f-759a-45e8-a4b0-c15cf1c28e16"), Value = "Đắm chìm trong âm thanh chất lượng cao chuẩn studio với tai nghe True Wireless, cho độ phân giải âm thanh lên đến 24 bit trên các điện thoại Galaxy dòng S24, S23, Z Fold 6, Z Flip 6, Z Fold 5, Z Flip 5 và Tab S9 với One UI 6.1.1 trở lên. Mang đến chất âm trung thực, giúp bạn nghe được các chi tiết âm phức tạp.", Type = ParseGadgetDescriptionType("NormalText"), Index = 8 },
