new GadgetDescription { Id = Guid.Parse("d739695d-a18f-4c3d-a030-fa2ad021b53d"), GadgetId = Guid.Parse("c308ba1f-759a-45e8-a4b0-c15cf1c28e16"), Value = "Điều chỉnh âm thanh lên xuống theo ý thích mà không phải cầm đến điện thoại của bạn với thao tác vuốt ở phần cạnh của thiết bị. Chạm một lần để tạm dừng và phát nhạc tùy thích hoặc nhấn giữ để khởi chạy ứng dụng yêu thích mà bạn cài đặt sẵn, hỗ trợ người dùng thao tác nhanh chóng và tiện lợi.", Type = ParseGadgetDescriptionType("NormalText"), Index = 11 },
new GadgetDescription { Id = Guid.Parse("2160f57f-744e-4433-930b-0581becc4a3a"), GadgetId = Guid.Parse("be17d34b-110c-4bce-8d7b-a58849339d2b"), Value = "Thiết kế hiện đại, thời lượng pin lâu", Type = ParseGadgetDescriptionType("BoldText"), Index = 0 },
new GadgetDescription { Id = Guid.Parse("1ee5e2a7-17fd-490c-8fc6-149908484367"), GadgetId = Guid.Parse("be17d34b-110c-4bce-8d7b-a58849339d2b"), Value = "So với phiên bản tiền nhiệm Galaxy Buds 2 Pro, Galaxy Buds 3 Pro đã có màn lột xác ngoạn mục về mặt thiết kế. Thay vì kiểu dáng nhỏ nhắn cùng phần vỏ mờ quen thuộc, Galaxy Buds 3 Pro lại sở hữu vẻ ngoài thuôn dài hơn với lớp vỏ bóng bẩy mang đến phong cách hiện đại và đẳng cấp cho người dùng.", Type = ParseGadgetDescriptionType("NormalText"), Index = 1 },
new GadgetDescription { Id = Guid.Parse("abcc40e8-b7a3-47d1-90fb-8d866b3e2e7b"), GadgetId = Guid.Parse("be17d34b-110c-4bce-8d7b-a58849339d2b"), Value = "https://storage.googleapis.com/fbdemo-f9d5f.appspot.com/Gadgets/83f96b66-5dd2-49ea-bd97-fe8eb2e1fc3b.jpg", Type = ParseGadgetDescriptionType("Image"), Index = 2 },
new GadgetDescription { Id = Guid.Parse("420a9a4e-5ffe-415e-bbb4-364777d99948"), GadgetId = Guid.Parse("be17d34b-110c-4bce-8d7b-a58849339d2b"), Value = "Tai nghe Buds 3 Pro có thiết kế công thái học, được chế tác tỉ mỉ từ dữ liệu tai 3D. Đệm tai nghe êm ái mang lại sự vừa vặn thoải mái. Đặc biệt, thanh ánh sáng Blade Lights độc đáo, cho phép tùy chỉnh theo sở thích, chỉ có ở phiên bản Pro này, không chỉ tạo nên vẻ ngoài high-tech mà còn làm nổi bật phong cách của bạn, khiến mọi ánh nhìn đều đổ dồn vào.", Type = ParseGadgetDescriptionType("NormalText"), Index = 3 },
new GadgetDescription { Id = Guid.Parse("c27d5233-8eeb-4e26-bc04-6dc4aec00c88"), GadgetId = Guid.Parse("be17d34b-110c-4bce-8d7b-a58849339d2b"), Value = "Không lo về rào cản ngôn ngữ với Galaxy AI", Type = ParseGadgetDescriptionType("BoldText"), Index = 4 },
new GadgetDescription { Id = Guid.Parse("b4019b32-3461-4ec0-b23d-b52d629a17a3"), GadgetId = Guid.Parse("be17d34b-110c-4bce-8d7b-a58849339d2b"), Value = "Ở mẫu tai nghe Samsung này, hãng đã tích hợp thêm Galaxy AI, cho phép người dùng dịch giọng nói ngay trên tai nghe thông qua điện thoại Samsung Galaxy với tính năng Interpreter. Người dùng không cần lo lắng về tốc độ đọc nhanh, vì bản dịch sẽ hiển thị trên điện thoại Samsung Galaxy.", Type = ParseGadgetDescriptionType("NormalText"), Index = 5 },
