new GadgetDescription { Id = Guid.Parse("517dd072-85ed-4f7e-af28-d2ef816b0092"), GadgetId = Guid.Parse("d8b12d15-9cdb-4617-9f95-c4fc5ec1e40b"), Value = "Điểm nhấn ngoại hình của tai nghe Marshall chính là sự giao thoa tinh tế giữa nét cổ điển và hiện đại. Lớp da tổng hợp cao cấp cùng logo Marshall tạo nên vẻ ngoài sang trọng và lịch lãm. Kiểu dáng bịt tai êm ái ôm sát vành tai mang đến sự thoải mái tối đa cho người sử dụng, ngay cả khi nghe nhạc trong thời gian dài.", Type = ParseGadgetDescriptionType("NormalText"), Index = 2 },
