new GadgetDescription { Id = Guid.Parse("772ba457-4b85-4dc5-b19a-4cd85e166fc7"), GadgetId = Guid.Parse("d8b12d15-9cdb-4617-9f95-c4fc5ec1e40b"), Value = "Marshall Major IV là người bạn đồng hành lý tưởng cho những ai yêu thích âm nhạc mọi lúc mọi nơi. Với khoảng 80 giờ chơi nhạc không dây liên tục, bạn có thể đắm chìm trong thế giới âm thanh yêu thích của mình mà không lo bị gián đoạn. Khả năng sạc nhanh chỉ 15 phút cho 15 giờ nghe cũng giúp bạn luôn sẵn sàng cho mọi cuộc phiêu lưu bất tận.", Type = ParseGadgetDescriptionType("NormalText"), Index = 5 },
