new GadgetDescription { Id = Guid.Parse("e4ef4d3d-1096-4d5e-bb82-53e316fa509b"), GadgetId = Guid.Parse("d8b12d15-9cdb-4617-9f95-c4fc5ec1e40b"), Value = "Âm thanh bùng nổ, lay động mọi giác quan", Type = ParseGadgetDescriptionType("BoldText"), Index = 7 },
new GadgetDescription { Id = Guid.Parse("7f28d5a0-b39b-4df3-83b1-7853ddc54b5b"), GadgetId = Guid.Parse("d8b12d15-9cdb-4617-9f95-c4fc5ec1e40b"), Value = "Tai nghe chụp tai nhà Marshall mang đến cho bạn trải nghiệm âm thanh tuyệt đỉnh với Driver 40 mm và công nghệ Marshall Signature Sound trứ danh, giúp tái tạo âm thanh một cách chính xác và chi tiết. Mỗi nốt nhạc, dù là những giai điệu du dương nhẹ nhàng hay những bản rock cuồng nhiệt sôi động, đều được thể hiện một cách chân thực và sống động như chính bạn đang hòa mình vào sân khấu âm nhạc.", Type = ParseGadgetDescriptionType("NormalText"), Index = 8 },
new GadgetDescription { Id = Guid.Parse("f0b2656b-5bb2-4079-b3fc-5e4e30d63b32"), GadgetId = Guid.Parse("d8b12d15-9cdb-4617-9f95-c4fc5ec1e40b"), Value = "https://storage.googleapis.com/fbdemo-f9d5f.appspot.com/Gadgets/acb29556-2ee8-41bf-8671-ec1c5d24905d.jpg", Type = ParseGadgetDescriptionType("Image"), Index = 9 },
new GadgetDescription { Id = Guid.Parse("0eb1e27a-3c1f-4806-a26d-3a44d7e32788"), GadgetId = Guid.Parse("d8b12d15-9cdb-4617-9f95-c4fc5ec1e40b"), Value = "Kết nối mượt mà, ổn định", Type = ParseGadgetDescriptionType("BoldText"), Index = 10 },
new GadgetDescription { Id = Guid.Parse("bcdbe097-4b9b-4cc1-9af1-a7fd170bffa8"), GadgetId = Guid.Parse("d8b12d15-9cdb-4617-9f95-c4fc5ec1e40b"), Value = "Marshall Major IV mang đến cho bạn trải nghiệm âm nhạc mượt mà và ổn định với công nghệ Bluetooth 5.0, cho phép kết nối nhanh chóng và tin cậy với các thiết bị di động của bạn, giúp bạn tận hưởng âm thanh yêu thích mọi lúc mọi nơi mà không lo bị gián đoạn bởi tình trạng rối, đứt đoạn hay nhiễu sóng.", Type = ParseGadgetDescriptionType("NormalText"), Index = 11 },
new GadgetDescription { Id = Guid.Parse("4be72801-b058-443e-8d8c-41857104068f"), GadgetId = Guid.Parse("06a703ba-c1c2-4fbe-bb6a-2e885368492b"), Value = "Marshall Motif II A.N.C. là mẫu tai nghe Bluetooth mới nhất từ thương hiệu Marshall, nổi tiếng với chất lượng âm thanh và thiết kế đẹp mắt. Motif II A.N.C. mang lại trải nghiệm âm nhạc tinh tế, đồng thời với thiết kế nhỏ gọn tai nghe sẽ đem lại cho bạn trải nghiệm âm nhạc chất lượng nhất.", Type = ParseGadgetDescriptionType("BoldText"), Index = 0 },
new GadgetDescription { Id = Guid.Parse("af26e454-a611-4bb4-8484-c42ea3321040"), GadgetId = Guid.Parse("06a703ba-c1c2-4fbe-bb6a-2e885368492b"), Value = "Tai nghe Bluetooth True Wireless Marshall Motif II A.N.C. nổi bật với thiết kế tinh tế, hiện đại đúng như phong cách đặc trưng của thương hiệu Marshall. Tai nghe có vỏ ngoài màu đen có vân nổi, tạo cảm giác mạnh mẽ và sang trọng, phù hợp với phong cách của những người yêu thích sự cá tính và độc đáo.", Type = ParseGadgetDescriptionType("NormalText"), Index = 1 },
new GadgetDescription { Id = Guid.Parse("3b84ea6a-6335-48a5-8cbc-f6693ea88848"), GadgetId = Guid.Parse("06a703ba-c1c2-4fbe-bb6a-2e885368492b"), Value = "Marshall Motif II A.N.C. gây ấn tượng với thời lượng pin dài và khả năng sạc nhanh. Tai nghe Marshall có thể sử dụng liên tục lên đến 9 giờ sau mỗi lần sạc 1.5 giờ, cho phép bạn thoải mái nghe nhạc, gọi điện hay sử dụng các ứng dụng giải trí suốt cả ngày mà không lo gián đoạn.", Type = ParseGadgetDescriptionType("NormalText"), Index = 2 },
new GadgetDescription { Id = Guid.Parse("aeb8f93e-870e-44f0-a769-62f3b6d6f71b"), GadgetId = Guid.Parse("06a703ba-c1c2-4fbe-bb6a-2e885368492b"), Value = "Tai nghe được trang bị công nghệ âm thanh đặc trưng của Marshall, mang đến trải nghiệm âm nhạc tuyệt vời với chất lượng âm thanh vượt trội. Được gọi là \"Marshall Signature Sound\", công nghệ này tái tạo âm thanh với độ chi tiết cao, cho phép bạn cảm nhận được đầy đủ các dải âm thanh từ thấp đến cao.", Type = ParseGadgetDescriptionType("NormalText"), Index = 3 },
new GadgetDescription { Id = Guid.Parse("5e36955b-95c6-4ea9-8120-62dd0bb72ee1"), GadgetId = Guid.Parse("162065d5-d531-47a2-b976-2d4e7c8ff729"), Value = "Tai nghe Bluetooth True Wireless Marshall Minor IV là sản phẩm nổi bật trong dòng tai nghe không dây của thương hiệu Marshall. Sản phẩm được thiết kế tỉ mỉ, vừa vặn với tai, đồng thời với công nghệ âm thanh hiện đại hứa hẹn đem lại trải nghiệm nghe nhạc tuyệt vời cho bạn ở mọi nơi.", Type = ParseGadgetDescriptionType("BoldText"), Index = 0 },
new GadgetDescription { Id = Guid.Parse("465dfcbe-e7f7-4fb2-b312-a1e48390861e"), GadgetId = Guid.Parse("162065d5-d531-47a2-b976-2d4e7c8ff729"), Value = "Tai nghe Bluetooth này gây ấn tượng mạnh mẽ với thiết kế nhỏ gọn và khối lượng nhẹ chỉ 3.695 g, mang lại cảm giác thoải mái và dễ chịu khi đeo. Vỏ tai nghe được làm từ chất liệu cao cấp, với lớp hoàn thiện mịn màng và đường nét tinh tế, tạo nên vẻ ngoài vừa hiện đại vừa đậm chất cổ điển.", Type = ParseGadgetDescriptionType("NormalText"), Index = 1 },
new GadgetDescription { Id = Guid.Parse("ed3417ad-1fbd-42a7-b258-2c822309f723"), GadgetId = Guid.Parse("162065d5-d531-47a2-b976-2d4e7c8ff729"), Value = "Tai nghe này không chỉ nổi bật với thiết kế tinh tế mà còn ấn tượng về thời lượng pin và thời gian sạc. Với khả năng sử dụng liên tục lên đến 7 giờ sau mỗi lần sạc đầy, Minor IV đảm bảo bạn có thể thoải mái thưởng thức âm nhạc cả ngày mà không lo bị gián đoạn.", Type = ParseGadgetDescriptionType("NormalText"), Index = 2 },
new GadgetDescription { Id = Guid.Parse("ecfd814e-f1a6-4ca5-98e7-0d84aa574dfd"), GadgetId = Guid.Parse("162065d5-d531-47a2-b976-2d4e7c8ff729"), Value = "Mẫu tai nghe Marshall được trang bị công nghệ âm thanh đặc trưng của hãng, mang tên Marshall Signature Sound, mang đến trải nghiệm âm nhạc tuyệt vời và chất lượng âm thanh vượt trội. Công nghệ này được tối ưu hóa để tái hiện âm thanh chi tiết, sắc nét, với dải tần số rộng, giúp từng nốt nhạc và âm thanh trở nên sống động hơn bao giờ hết.", Type = ParseGadgetDescriptionType("NormalText"), Index = 3 },
new GadgetDescription { Id = Guid.Parse("f1533c04-088a-44c6-b556-d0a096391e8e"), GadgetId = Guid.Parse("1945cdf9-3703-43dd-9e34-053f8592b814"), Value = "Thuộc dòng sản phẩm âm thanh cao cấp đến từ thương hiệu Marshall lừng danh, tai nghe Bluetooth chụp tai Marshall Monitor II A.N.C. hứa hẹn mang đến cho bạn những giai điệu bùng nổ, sự yên tĩnh tuyệt đối với tính năng chống ồn chủ động cùng phong cách thời thượng, tạo điểm nhấn đầy phong cách cho mọi tín đồ âm nhạc.", Type = ParseGadgetDescriptionType("BoldText"), Index = 0 },
new GadgetDescription { Id = Guid.Parse("ca8a59b8-d0a8-4564-a854-58be53db76fe"), GadgetId = Guid.Parse("1945cdf9-3703-43dd-9e34-053f8592b814"), Value = "Thiết kế đẳng cấp, tôn vinh cá tính", Type = ParseGadgetDescriptionType("BoldText"), Index = 1 },
new GadgetDescription { Id = Guid.Parse("974998da-1169-48e0-bfdb-59cdc2bcb302"), GadgetId = Guid.Parse("1945cdf9-3703-43dd-9e34-053f8592b814"), Value = "Nổi bật giữa thị trường tai nghe hiện đại, Marshall khẳng định phong cách độc đáo với thiết kế cổ điển đặc trưng. Từng đường nét trên tai nghe đều mang đậm dấu ấn thương hiệu, là sự hòa quyện hoàn hảo giữa nét đẹp hiện đại và hoài cổ đầy tinh tế.", Type = ParseGadgetDescriptionType("NormalText"), Index = 2 },
