new GadgetDescription { Id = Guid.Parse("2ab4b478-f95c-467f-a110-cc237badfae2"), GadgetId = Guid.Parse("3a53f00b-6f0a-4791-b9f4-0b8ab6d188ca"), Value = "Tai nghe Soundpeats Hearfit được thiết kế theo phong cách Open-Ear, cho phép bạn thoải mái nghe nhạc mà vẫn giữ được sự kết nối với môi trường xung quanh. Với thiết kế không dây hoàn toàn, tai nghe mang lại cảm giác nhẹ nhàng và thoải mái, phù hợp cho các hoạt động ngoài trời như chạy bộ, đạp xe hay làm việc tại văn phòng.", Type = ParseGadgetDescriptionType("BoldText"), Index = 0 },
