new GadgetDescription { Id = Guid.Parse("87e707af-830d-4bb7-a015-bfbe8bf9f009"), GadgetId = Guid.Parse("b1f9da60-49ba-4164-9b2c-bb0973179910"), Value = "Soundpeats RunFree Lite 2 được thiết kế dành riêng cho các hoạt động thể thao với kiểu dáng linh hoạt và nhẹ nhàng. Tai nghe ôm sát tai, không lo tuột khi vận động mạnh, giúp bạn tập trung vào việc rèn luyện mà không lo lắng về tai nghe. Đây là lựa chọn hoàn hảo cho người dùng yêu thích chạy bộ, tập gym hoặc các hoạt động ngoài trời.", Type = ParseGadgetDescriptionType("BoldText"), Index = 0 },
