new GadgetDescription { Id = Guid.Parse("59c9c6cf-c818-4754-8efa-15291a9ee330"), GadgetId = Guid.Parse("b1f9da60-49ba-4164-9b2c-bb0973179910"), Value = "Soundpeats RunFree Lite 2 được trang bị công nghệ Bluetooth 5.3 hiện đại, cung cấp khả năng kết nối ổn định, mượt mà và phạm vi hoạt động rộng hơn. Với độ trễ thấp và kết nối không bị gián đoạn, bạn có thể dễ dàng nghe nhạc hoặc nhận cuộc gọi mà không gặp bất kỳ trở ngại nào.", Type = ParseGadgetDescriptionType("NormalText"), Index = 2 },
