new GadgetDescription { Id = Guid.Parse("ff35125a-1d38-48c3-8cea-3fec42596bcf"), GadgetId = Guid.Parse("946fbab2-6468-4b14-bc85-2978d000f94b"), Value = "https://storage.googleapis.com/fbdemo-f9d5f.appspot.com/Gadgets/09c5839a-7553-4ea7-a114-d5e554649c63.jpg", Type = ParseGadgetDescriptionType("Image"), Index = 9 },
new GadgetDescription { Id = Guid.Parse("42f538eb-56bd-4c11-a8a4-ebea9bb01dde"), GadgetId = Guid.Parse("123726f5-4b91-4a64-bbf9-02cfb9187893"), Value = "Tai nghe Bluetooth True Wireless Rezo Air 5 Deluxe ANC là sự kết hợp hoàn hảo giữa thiết kế hiện đại và công nghệ âm thanh tiên tiến. Với tính năng chống ồn chủ động (ANC), Rezo Air 5 Deluxe giúp bạn tận hưởng âm nhạc trong môi trường yên tĩnh, loại bỏ tiếng ồn xung quanh một cách hiệu quả. Chất âm sống động, bass mạnh mẽ cùng khả năng kết nối Bluetooth 5.2 nhanh chóng, ổn định, đảm bảo trải nghiệm nghe liền mạch.", Type = ParseGadgetDescriptionType("BoldText"), Index = 0 },
