new GadgetDescription { Id = Guid.Parse("5db83e2e-85c6-456d-9a63-2d531ab30ca3"), GadgetId = Guid.Parse("3f93ac18-701f-4731-85be-f702bdab7ab0"), Value = "Tai nghe Bluetooth True Wireless Rezo RZ09 ANC mang đến trải nghiệm âm thanh vượt trội với công nghệ chống ồn chủ động (ANC), giúp bạn đắm chìm trong không gian âm nhạc thuần khiết, không bị làm phiền bởi tiếng ồn xung quanh. Với chất lượng âm thanh rõ nét, dải âm cân bằng và âm bass sâu, Rezo RZ09 phù hợp cho cả những tín đồ âm nhạc khó tính.", Type = ParseGadgetDescriptionType("BoldText"), Index = 0 },
