new GadgetDescription { Id = Guid.Parse("7ba8afa0-6a14-4406-b676-a5fcfa3b4105"), GadgetId = Guid.Parse("a17b04d3-d52c-4738-b1f4-650617efbf30"), Value = "Tích hợp Bluetooth 5.3 mới nhất, tai nghe đảm bảo kết nối nhanh chóng và ổn định. Thời lượng pin ấn tượng lên đến 24 giờ kèm hộp sạc, hỗ trợ sạc nhanh giúp bạn thoải mái sử dụng cả ngày dài mà không lo hết pin. Thiết kế gọn nhẹ và vừa vặn, Soundcore R50i NC A3959 mang lại sự thoải mái khi đeo, phù hợp cho cả ngày làm việc hoặc khi di chuyển.", Type = ParseGadgetDescriptionType("NormalText"), Index = 1 },
