new GadgetDescription { Id = Guid.Parse("ba36e31b-1256-4d37-9678-2f57a9759bc4"), GadgetId = Guid.Parse("d38ea524-ef07-43e3-b040-1af24b26acb6"), Value = "Tai nghe Bluetooth AirPods 3 Lightning Charge Apple MPNY3 với nhiều tính năng thông minh như: chip Apple H1, Âm thanh không gian, Adaptive EQ, chống nước IPX4,... mang đến trải nghiệm âm thanh tuyệt vời khi sử dụng.", Type = ParseGadgetDescriptionType("NormalText"), Index = 18 },
new GadgetDescription { Id = Guid.Parse("0445a1d6-54cb-4bac-81af-bb8efc128dc8"), GadgetId = Guid.Parse("93050a85-ccaf-4f70-9f91-ae7d667b547a"), Value = "AirPods 2 - được mệnh danh là một chiếc AirPods huyền thoại quốc dân rất được lòng của các fan nhà táo. Sau thành công vang dội của AirPods đời đầu thì phiên bản đời thứ 2 này của nhà Apple có gì cải tiến để có thể vượt qua người tiền nhiệm và cho tới thời điểm hiện tại vẫn còn nhận được nhiều sự ưa chuộng với các bạn trẻ đến vậy?", Type = ParseGadgetDescriptionType("BoldText"), Index = 0 },
new GadgetDescription { Id = Guid.Parse("beabc3e8-3637-4f9a-a593-f8691914d37f"), GadgetId = Guid.Parse("93050a85-ccaf-4f70-9f91-ae7d667b547a"), Value = "Hệ sinh thái kết nối cực nhanh, cực xịn, mic thoại chất lượng cao", Type = ParseGadgetDescriptionType("BoldText"), Index = 1 },
new GadgetDescription { Id = Guid.Parse("9f9eb249-ea2e-4dd0-bf8e-9f558b005f0c"), GadgetId = Guid.Parse("93050a85-ccaf-4f70-9f91-ae7d667b547a"), Value = "Đầu tiên, nhắc đến điểm cải tiến đáng \"ăn tiền\" nhất trên chiếc tai nghe này chính là AirPods 2 được tích hợp chip xử lý H1 thế hệ mới cho thời gian chuyển đổi kết nối giữa các thiết bị nhanh hơn gấp 2 lần so với phiên bản cũ. Có thể dùng được cho các sản phẩm như: iPhone 15, iPhone 15 Plus, iPhone 15 Pro, iPhone 15 Pro Max, Apple Watch Ultra 2, Apple Watch Series 9, iPhone 16, iPhone 16 Plus, iPhone 16 Pro, iPhone 16 Pro Max,...", Type = ParseGadgetDescriptionType("NormalText"), Index = 2 },
new GadgetDescription { Id = Guid.Parse("61a486b0-3868-41c5-a565-85489ba6018b"), GadgetId = Guid.Parse("93050a85-ccaf-4f70-9f91-ae7d667b547a"), Value = "Đồng thời, trang bị công nghệ Bluetooth 5.0 cho kết nối nhanh chóng, ổn định, độ trễ ít, AirPods 2 cho tốc độ kết nối tới cuộc gọi thoại nhanh gấp 1.5 lần so với bình thường, giảm độ trễ âm thanh khi gọi thoại, chơi game,…Với khả năng cho tốc độ kết nối nhanh và đáp ứng độ trễ âm thanh tốt thì AirPods 2 vẫn là một ứng cử viên sáng giá cho những bạn chuộng hệ sinh thái nhà Apple.", Type = ParseGadgetDescriptionType("NormalText"), Index = 3 },
new GadgetDescription { Id = Guid.Parse("60c7bd27-7d6d-4629-a8e3-f4d3068e78fa"), GadgetId = Guid.Parse("93050a85-ccaf-4f70-9f91-ae7d667b547a"), Value = "https://storage.googleapis.com/fbdemo-f9d5f.appspot.com/Gadgets/601e1abd-9e2e-47ec-9f85-071cc094599c.jpg ", Type = ParseGadgetDescriptionType("Image"), Index = 4 },
new GadgetDescription { Id = Guid.Parse("91034c36-d58f-4906-8f79-10ae0a7909e6"), GadgetId = Guid.Parse("93050a85-ccaf-4f70-9f91-ae7d667b547a"), Value = "Tiếp theo, một trong những lý do khiến nhiều người dùng vẫn luôn “săn đón\" chiếc tai nghe này là khả năng đàm thoại với mic khử tiếng ồn tốt, đem đến một chất âm sạch, trong trẻo dễ nghe.", Type = ParseGadgetDescriptionType("NormalText"), Index = 5 },
new GadgetDescription { Id = Guid.Parse("c9169ab4-6a87-438c-9552-3330b07b4f48"), GadgetId = Guid.Parse("93050a85-ccaf-4f70-9f91-ae7d667b547a"), Value = "Mình đã thử sử dụng tai nghe trong phòng máy lạnh (loại máy lạnh công nghiệp). Tất nhiên, như những gì đã đề cập ở trên, AirPods 2 thu âm tiếng cực kỳ trong và rõ, không bị trộn tạp âm hay tiếng ồn phà phà hơi gió của máy lạnh vào micro, rất phù hợp cho những bạn thường xuyên phải sử dụng tai nghe để họp hay làm việc trực tuyến khi ở trong môi trường có nhiều người. Đây là một ưu điểm rất lớn mà cho dù đã ra mắt khá lâu nhưng về chất lượng sản phẩm mà hãng Apple đem lại thì luôn làm hài lòng người dùng.", Type = ParseGadgetDescriptionType("NormalText"), Index = 6 },
new GadgetDescription { Id = Guid.Parse("b64dd7b4-6169-40cd-bccb-e66a38355f37"), GadgetId = Guid.Parse("93050a85-ccaf-4f70-9f91-ae7d667b547a"), Value = "https://storage.googleapis.com/fbdemo-f9d5f.appspot.com/Gadgets/69f83d51-c54d-461c-8760-07bab652a646.jpg ", Type = ParseGadgetDescriptionType("Image"), Index = 7 },
new GadgetDescription { Id = Guid.Parse("d1cf3b6c-30fe-4f62-b320-044aaf05c0a8"), GadgetId = Guid.Parse("93050a85-ccaf-4f70-9f91-ae7d667b547a"), Value = "Đối với những bạn sử dụng điện thoại Android, chiếc tai nghe này vẫn có khả năng kết nối đầy đủ và sử dụng bình thường. Tuy nhiên, tốc độ kết nối sẽ bị chậm và tai nghe cũng sẽ không hoạt động hết tối đa công suất của nó, tất nhiên đây vẫn là sự ưu tiên đến từ mục đích “những sản phẩm trong hệ sinh thái độc quyền của nhà Apple”. ", Type = ParseGadgetDescriptionType("NormalText"), Index = 8 },
new GadgetDescription { Id = Guid.Parse("4bea7470-d428-4849-a95f-73b1a9004f6b"), GadgetId = Guid.Parse("93050a85-ccaf-4f70-9f91-ae7d667b547a"), Value = "https://storage.googleapis.com/fbdemo-f9d5f.appspot.com/Gadgets/bce6e839-370a-427b-8a2d-17cef40be914.jpg ", Type = ParseGadgetDescriptionType("Image"), Index = 9 },
new GadgetDescription { Id = Guid.Parse("e3b4d457-04cf-452b-b03e-773b41e4d27e"), GadgetId = Guid.Parse("93050a85-ccaf-4f70-9f91-ae7d667b547a"), Value = "Giữ nguyên mẫu thiết kế quen thuộc, nhỏ gọn và tiện lợi, đi kèm thời lượng pin ổn định", Type = ParseGadgetDescriptionType("BoldText"), Index = 10 },
new GadgetDescription { Id = Guid.Parse("c0649cee-f59b-4565-9cd2-3c1c7bed6d85"), GadgetId = Guid.Parse("93050a85-ccaf-4f70-9f91-ae7d667b547a"), Value = "AirPods 2 giữ nguyên thiết kế của người tiền nhiệm, vẫn là vỏ hộp đựng bo tròn được làm bằng nhựa trơn bóng, kết hợp là bản lề đóng mở nắp hộp mang lại sự hoàn thiện cao, 2 tay hay 1 tay sử dụng đóng mở nắp hộp vẫn rất tiện lợi, cho cảm giác cầm nắm gọn trong lòng bàn tay và dễ dàng bỏ vào túi quần, túi áo, balo, túi xách để mang theo sử dụng mọi lúc.", Type = ParseGadgetDescriptionType("NormalText"), Index = 11 },
new GadgetDescription { Id = Guid.Parse("8f4a03ab-570e-4252-8604-4f798eeb83e9"), GadgetId = Guid.Parse("93050a85-ccaf-4f70-9f91-ae7d667b547a"), Value = "https://storage.googleapis.com/fbdemo-f9d5f.appspot.com/Gadgets/0fd3d4d7-6b8d-41f6-8cd2-d01fc0730f27.jpg ", Type = ParseGadgetDescriptionType("Image"), Index = 12 },
new GadgetDescription { Id = Guid.Parse("aa67e612-e328-4b58-9ad7-b353d0af4e1d"), GadgetId = Guid.Parse("93050a85-ccaf-4f70-9f91-ae7d667b547a"), Value = "Ở phần đèn LED và nút bấm, giống như thế hệ “đàn anh\" vị trí đèn LED vẫn nằm ở trong hộp, nút bấm vẫn nằm ở vị trí cũ là phía sau hộp, khi bấm nút nhận diện kết nối thì đèn có màu trắng. Ở tình trạng gần hết pin, đèn sẽ chuyển báo hiệu cho bạn thấy màu cam và khi được sạc đầy thì lại hiển thị về màu xanh.", Type = ParseGadgetDescriptionType("NormalText"), Index = 13 },
new GadgetDescription { Id = Guid.Parse("7e52d15d-2604-4c9e-b876-be16fb27447f"), GadgetId = Guid.Parse("93050a85-ccaf-4f70-9f91-ae7d667b547a"), Value = "Về thiết kế tai nghe dù không thay đổi gì nhiều, nhưng tai nghe Apple lại mang đến sự trải nghiệm thoải mái bậc nhất trên thị trường tai nghe không dây. Tai nghe nhỏ nhưng khó rơi, vừa vặn và không gây khó chịu hay bị bí tai khi đeo thời gian dài.", Type = ParseGadgetDescriptionType("NormalText"), Index = 14 },
new GadgetDescription { Id = Guid.Parse("ff5028d0-a5e3-47c0-9924-3ff3b67fb76e"), GadgetId = Guid.Parse("93050a85-ccaf-4f70-9f91-ae7d667b547a"), Value = "Thao tác điều khiển cảm ứng nhanh nhạy, chất âm phù hợp với mọi thể loại nhạc", Type = ParseGadgetDescriptionType("BoldText"), Index = 15 },
new GadgetDescription { Id = Guid.Parse("44cae480-f6ab-47f7-a0c4-79dcaddefb23"), GadgetId = Guid.Parse("93050a85-ccaf-4f70-9f91-ae7d667b547a"), Value = "So với bản 1 thì có thêm tính năng gọi siri. Nhưng cơ bản vào năm 2022 thì gần như mọi tai nghe đều có rồi nên đây vẫn làm mức đáp ứng đủ nhu cầu thông thường chứ không quá đặc biệt. Trải nghiệm thì tất nhiên, đây là những sản phẩm chuẩn mực về mặt thao tác điều khiển, khả năng xử lý rất tốt, chạm vào “siêu xịn”.", Type = ParseGadgetDescriptionType("NormalText"), Index = 16 },
new GadgetDescription { Id = Guid.Parse("bb344526-a9bf-42f1-b494-24e01f3a3398"), GadgetId = Guid.Parse("93050a85-ccaf-4f70-9f91-ae7d667b547a"), Value = "Tất cả các tính năng như: gọi Siri, chuyển bài hát, tạm dừng bài hát, nghe nhận cuộc gọi chỉ cần thực hiện thao tác chạm là rất \"nhạy\" và cho đến thời điểm hiện tại, rất ít các hãng làm tốt được điểm này như tai nghe AirPods, vì cũng còn khá nhiều sản phẩm ở phần cảm ứng chạm vẫn chưa được linh hoạt và nhanh nhạy như vậy.", Type = ParseGadgetDescriptionType("NormalText"), Index = 17 },
new GadgetDescription { Id = Guid.Parse("9c528b60-36b4-48e5-96bf-25eb2cd82622"), GadgetId = Guid.Parse("fef2b8e4-806f-4db5-9a5e-90ee050f3fcd"), Value = "Tai nghe chụp tai Bluetooth AirPods Max Apple sở hữu ngoại hình độc đáo, tích hợp các công nghệ hàng đầu trong thị trường tai nghe ở thời điểm ra mắt (2020). Tai nghe xứng đáng là món đồ công nghệ ''đỉnh của đỉnh'' nâng cấp trải nghiệm âm nhạc của người dùng.", Type = ParseGadgetDescriptionType("BoldText"), Index = 0 },
new GadgetDescription { Id = Guid.Parse("9979fa00-e05a-42fb-9e05-e085561ed9ce"), GadgetId = Guid.Parse("fef2b8e4-806f-4db5-9a5e-90ee050f3fcd"), Value = "Thiết kế độc đáo, sang trọng", Type = ParseGadgetDescriptionType("BoldText"), Index = 1 },
new GadgetDescription { Id = Guid.Parse("065add1b-7381-4304-8048-dc698082bc8b"), GadgetId = Guid.Parse("fef2b8e4-806f-4db5-9a5e-90ee050f3fcd"), Value = "Nhìn từ hình dáng bên ngoài, mình đã liên tưởng tới một thiết kế mang hơi hướng công nghệ tương lai, khác biệt hoàn toàn với những thiết kế tai nghe chụp tai ở thời điểm hiện tại.", Type = ParseGadgetDescriptionType("NormalText"), Index = 2 },
new GadgetDescription { Id = Guid.Parse("5b0ab5af-b265-4625-8ab1-ee4675e5177e"), GadgetId = Guid.Parse("fef2b8e4-806f-4db5-9a5e-90ee050f3fcd"), Value = "https://storage.googleapis.com/fbdemo-f9d5f.appspot.com/Gadgets/be6c10d4-cdb8-4709-a384-20a43a4bfd59.jpg ", Type = ParseGadgetDescriptionType("Image"), Index = 3 },
new GadgetDescription { Id = Guid.Parse("6b8a15fc-51ed-449e-a411-031a402b4ede"), GadgetId = Guid.Parse("fef2b8e4-806f-4db5-9a5e-90ee050f3fcd"), Value = "Ngoài ra, Apple có trang bị thêm một case bọc mềm mịn giúp bảo vệ phần earcup khi cất giữ.", Type = ParseGadgetDescriptionType("NormalText"), Index = 4 },
new GadgetDescription { Id = Guid.Parse("e23efe28-efa0-47fb-8eb6-0ab90f9a74ed"), GadgetId = Guid.Parse("fef2b8e4-806f-4db5-9a5e-90ee050f3fcd"), Value = "Phần earcup được làm hoàn toàn từ nhôm bền chắc, trông cực kỳ sang trọng. Khi cầm trên tay cảm giác sẽ hơi nặng một chút, tuy nhiên khi mình đeo lên lại có một trải nghiệm hoàn toàn khác, đó là tai nghe vô cùng nhẹ đầu và êm tai.", Type = ParseGadgetDescriptionType("NormalText"), Index = 5 },
new GadgetDescription { Id = Guid.Parse("a041c013-787b-4af6-887f-63e018c75a66"), GadgetId = Guid.Parse("fef2b8e4-806f-4db5-9a5e-90ee050f3fcd"), Value = "https://storage.googleapis.com/fbdemo-f9d5f.appspot.com/Gadgets/d8e6e7ba-32d2-4c95-b9ed-e3b97a34cfd9.jpg ", Type = ParseGadgetDescriptionType("Image"), Index = 6 },
new GadgetDescription { Id = Guid.Parse("12792c47-4848-48be-807a-c64c6772b50b"), GadgetId = Guid.Parse("fef2b8e4-806f-4db5-9a5e-90ee050f3fcd"), Value = "Một điểm cộng lớn đó là miếng đệm tai được làm từ vải lưới giúp mình đeo trên tai dễ chịu hơn, không bị cảm giác bí bách khi dùng lâu. Ngoài ra, đệm tai còn được trang bị thêm miếng nam châm tích hợp, do vậy mình có thể dễ dàng gỡ ra để vệ sinh cũng như gắn vào chắc chắn.", Type = ParseGadgetDescriptionType("NormalText"), Index = 7 },
new GadgetDescription { Id = Guid.Parse("186ae97b-e79b-4e16-96e1-46fbab475952"), GadgetId = Guid.Parse("fef2b8e4-806f-4db5-9a5e-90ee050f3fcd"), Value = "Chất âm cực đã với loạt công nghệ hiện đại từ Apple", Type = ParseGadgetDescriptionType("BoldText"), Index = 8 },
new GadgetDescription { Id = Guid.Parse("b381238d-eec4-4189-8cc0-27f87ffb749c"), GadgetId = Guid.Parse("fef2b8e4-806f-4db5-9a5e-90ee050f3fcd"), Value = "Các sản phẩm của Apple vốn nổi tiếng ở sự chất lượng có thể dễ dàng chinh phục được nhiều người dùng khó tính. Và chiếc AirPods Max cũng vậy, các công nghệ được trang bị trên tai nghe đều là những công nghệ hiện đại và đáng tiền như: Spatial Audio, Active Noise Cancellation, Adaptive EQ và Transparency Mode.", Type = ParseGadgetDescriptionType("NormalText"), Index = 9 },
new GadgetDescription { Id = Guid.Parse("a26fae2c-d7b9-4511-b108-1003d341f8e0"), GadgetId = Guid.Parse("fef2b8e4-806f-4db5-9a5e-90ee050f3fcd"), Value = "https://storage.googleapis.com/fbdemo-f9d5f.appspot.com/Gadgets/9acc3fcd-a465-4407-bd15-6b30a569605c.jpg ", Type = ParseGadgetDescriptionType("Image"), Index = 10 },
new GadgetDescription { Id = Guid.Parse("e33d9abd-c9c0-433b-9679-25b6e27cbea6"), GadgetId = Guid.Parse("fef2b8e4-806f-4db5-9a5e-90ee050f3fcd"), Value = "Bộ đôi công nghệ Chống ồn chủ động (Active Noise Cancellation) và Xuyên âm (Transparency Mode) thực sự giúp nâng cao hơn hẳn trải nghiệm giải trí của mình. Ở chế độ chống ồn, mọi âm thanh trong môi trường ngoài được loại bỏ gần như hoàn toàn nhờ cơ chế khử tiếng ồn bằng làn sóng có tần số đối lập với tạp âm. Còn khi cần trò chuyện, lắng nghe thông báo mình có thể dễ dàng chuyển sang chế độ Xuyên âm.", Type = ParseGadgetDescriptionType("NormalText"), Index = 11 },
new GadgetDescription { Id = Guid.Parse("12224549-1e27-4ec3-a049-50497641079b"), GadgetId = Guid.Parse("fef2b8e4-806f-4db5-9a5e-90ee050f3fcd"), Value = "Mic thoại thu âm đầy đủ, kết nối Bluetooth mượt mà", Type = ParseGadgetDescriptionType("BoldText"), Index = 12 },
new GadgetDescription { Id = Guid.Parse("ad8fc569-fd4d-46cc-ba5c-651edcbb1ad5"), GadgetId = Guid.Parse("fef2b8e4-806f-4db5-9a5e-90ee050f3fcd"), Value = "Do được trang bị mic thoại ngay trên tai nghe nên mình có thể sử dụng để gọi điện, họp online,... Khả năng thu âm của chiếc tai nghe này khá tốt, quá đủ để cuộc trò chuyện diễn ra liền mạch, không bị mất tiếng.", Type = ParseGadgetDescriptionType("NormalText"), Index = 13 },
new GadgetDescription { Id = Guid.Parse("81a8f891-f4f0-4de7-abfd-2ca23ab8e1f1"), GadgetId = Guid.Parse("fef2b8e4-806f-4db5-9a5e-90ee050f3fcd"), Value = "Điều khiển tiện lợi với các nút bấm trên tai nghe", Type = ParseGadgetDescriptionType("BoldText"), Index = 14 },
